        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label for="slider-cylinder-radius">cylinder radius</label>
                <input type="range" id="slider-cylinder-radius" min="0.5" max="3.0" step="0.05" value="2.1">
                <span id="label-cylinder-radius">2.10</span>
            </div>

            <div>
                <label for="slider-opacity">opacity</label>
                <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.95">
                <span id="label-opacity">0.95</span>
            </div>

            <div>
                <label for="slider-drill-radius">drill radius</label>
                <input type="range" id="slider-drill-radius" min="0.1" max="2.5" step="0.05" value="1.35">
                <span id="label-drill-radius">1.35</span>
            </div>

            <div>
                <label for="slider-inclination">inclination</label>
                <input type="range" id="slider-inclination" min="0" max="1.57" step="0.01" value="0.76">
                <span id="label-inclination">0.76</span>
            </div>

            <div>
                <label for="slider-rotation">rotation around vertical</label>
                <input type="range" id="slider-rotation" min="-3.14" max="3.14" step="0.01" value="0.9">
                <span id="label-rotation">0.90</span>
            </div>

            <div>
                <label for="slider-offset">offset</label>
                <input type="range" id="slider-offset" min="-2.0" max="2.0" step="0.01" value="-0.92">
                <span id="label-offset">-0.92</span>
            </div>

            <div>
                <label for="checkbox-boundary-only">
                    <input type="checkbox" id="checkbox-boundary-only">
                    boundary curve only
                </label>
            </div>
        </div>

        <div id="visualization-container">
            <canvas id="main-canvas"></canvas>
            <div class="plus-icon">+</div>
        </div>
    </div>

    <script type="importmap">
        {
            "imports": {
                "three": "https://unpkg.com/three@0.158.0/build/three.module.js",
                "three/bvh-csg": "https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js",
                "three/addons/": "https://unpkg.com/three@0.158.0/examples/jsm/"
            }
        }
    </script>

    <script type="module">
        import * as THREE from 'three';
        import { CSG } from 'three/bvh-csg';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xe0e0e0);

        const canvas = document.getElementById('main-canvas');
        const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
        renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        renderer.shadowMap.enabled = true;

        const camera = new THREE.PerspectiveCamera(
            75,
            canvas.clientWidth / canvas.clientHeight,
            0.1,
            1000
        );
        camera.position.set(5, 5, 5);
        camera.lookAt(0, 0, 0);

        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight1.position.set(5, 10, 5);
        directionalLight1.castShadow = true;
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
        directionalLight2.position.set(-5, 5, -5);
        scene.add(directionalLight2);

        // Scene objects
        let csgResult = null;
        let boundaryCurves = [];
        let ghostCylinder = null;

        // Get control elements
        const sliders = {
            cylinderRadius: document.getElementById('slider-cylinder-radius'),
            opacity: document.getElementById('slider-opacity'),
            drillRadius: document.getElementById('slider-drill-radius'),
            inclination: document.getElementById('slider-inclination'),
            rotation: document.getElementById('slider-rotation'),
            offset: document.getElementById('slider-offset')
        };

        const labels = {
            cylinderRadius: document.getElementById('label-cylinder-radius'),
            opacity: document.getElementById('label-opacity'),
            drillRadius: document.getElementById('label-drill-radius'),
            inclination: document.getElementById('label-inclination'),
            rotation: document.getElementById('label-rotation'),
            offset: document.getElementById('label-offset')
        };

        const boundaryOnlyCheckbox = document.getElementById('checkbox-boundary-only');

        // Update label values
        function updateLabels() {
            labels.cylinderRadius.textContent = parseFloat(sliders.cylinderRadius.value).toFixed(2);
            labels.opacity.textContent = parseFloat(sliders.opacity.value).toFixed(2);
            labels.drillRadius.textContent = parseFloat(sliders.drillRadius.value).toFixed(2);
            labels.inclination.textContent = parseFloat(sliders.inclination.value).toFixed(2);
            labels.rotation.textContent = parseFloat(sliders.rotation.value).toFixed(2);
            labels.offset.textContent = parseFloat(sliders.offset.value).toFixed(2);
        }

        // Clear previous geometries
        function clearScene() {
            if (csgResult) {
                scene.remove(csgResult);
                csgResult.geometry.dispose();
                csgResult.material.dispose();
                csgResult = null;
            }

            boundaryCurves.forEach(curve => {
                scene.remove(curve);
                curve.geometry.dispose();
                curve.material.dispose();
            });
            boundaryCurves = [];

            if (ghostCylinder) {
                scene.remove(ghostCylinder);
                ghostCylinder.geometry.dispose();
                ghostCylinder.material.dispose();
                ghostCylinder = null;
            }
        }

        // Calculate boundary curve points
        function calculateBoundaryCurve(cylinderRadius, drillRadius, inclination, rotation, offset, isFront = true) {
            const points = [];
            const segments = 100;

            for (let i = 0; i <= segments; i++) {
                const t = (i / segments) * Math.PI * 2;

                // Point on drill cylinder cap
                const drillPoint = new THREE.Vector3(
                    drillRadius * Math.cos(t),
                    0,
                    drillRadius * Math.sin(t) * (isFront ? 1 : -1)
                );

                // Apply drill cylinder transformations
                const transformMatrix = new THREE.Matrix4();
                transformMatrix.makeRotationZ(Math.PI / 2); // Make horizontal
                transformMatrix.multiply(new THREE.Matrix4().makeRotationX(inclination));
                transformMatrix.multiply(new THREE.Matrix4().makeRotationY(rotation));
                transformMatrix.multiply(new THREE.Matrix4().makeTranslation(0, offset, 0));

                drillPoint.applyMatrix4(transformMatrix);

                // Find intersection with main cylinder surface
                const dir = new THREE.Vector3(drillPoint.x, 0, drillPoint.z).normalize();
                const a = dir.x * dir.x + dir.z * dir.z;
                const b = 2 * (drillPoint.x * dir.x + drillPoint.z * dir.z);
                const c = drillPoint.x * drillPoint.x + drillPoint.z * drillPoint.z - cylinderRadius * cylinderRadius;

                const discriminant = b * b - 4 * a * c;
                if (discriminant >= 0) {
                    const t1 = (-b - Math.sqrt(discriminant)) / (2 * a);
                    const t2 = (-b + Math.sqrt(discriminant)) / (2 * a);

                    // Choose the appropriate intersection
                    const tIntersection = isFront ? Math.max(t1, t2) : Math.min(t1, t2);

                    const intersectionPoint = new THREE.Vector3(
                        drillPoint.x + dir.x * tIntersection,
                        drillPoint.y,
                        drillPoint.z + dir.z * tIntersection
                    );

                    points.push(intersectionPoint);
                }
            }

            return points;
        }

        // Update scene
        function updateScene() {
            clearScene();

            const cylinderRadius = parseFloat(sliders.cylinderRadius.value);
            const opacity = parseFloat(sliders.opacity.value);
            const drillRadius = parseFloat(sliders.drillRadius.value);
            const inclination = parseFloat(sliders.inclination.value);
            const rotation = parseFloat(sliders.rotation.value);
            const offset = parseFloat(sliders.offset.value);
            const boundaryOnly = boundaryOnlyCheckbox.checked;

            // Create main cylinder
            const mainCylinderGeometry = new THREE.CylinderGeometry(cylinderRadius, cylinderRadius, 8, 32);
            const mainCylinderMaterial = new THREE.MeshStandardMaterial({
                color: 0xD2B48C,
                opacity: boundaryOnly ? 0.15 : opacity,
                transparent: true
            });

            if (boundaryOnly) {
                // Create ghost cylinder
                ghostCylinder = new THREE.Mesh(mainCylinderGeometry, mainCylinderMaterial);
                scene.add(ghostCylinder);
            } else {
                // Create drill cylinder
                const drillCylinderGeometry = new THREE.CylinderGeometry(drillRadius, drillRadius, 10, 32);
                const drillCylinderMaterial = new THREE.MeshStandardMaterial({ color: 0xff0000 });
                const drillCylinder = new THREE.Mesh(drillCylinderGeometry, drillCylinderMaterial);

                // Transform drill cylinder
                drillCylinder.rotation.z = Math.PI / 2; // Make horizontal
                drillCylinder.rotation.x = inclination;
                drillCylinder.rotation.y = rotation;
                drillCylinder.position.y = offset;

                // Perform CSG operation
                const csg = new CSG();
                csg.add(mainCylinderGeometry, mainCylinderMaterial);
                csg.subtract(drillCylinderGeometry, drillCylinderMaterial, drillCylinder.matrix);

                const result = csg.toMesh();
                result.material = mainCylinderMaterial.clone();
                csgResult = result;
                scene.add(csgResult);
            }

            // Create boundary curves
            const frontPoints = calculateBoundaryCurve(cylinderRadius, drillRadius, inclination, rotation, offset, true);
            const backPoints = calculateBoundaryCurve(cylinderRadius, drillRadius, inclination, rotation, offset, false);

            // Create tube geometries for boundary curves
            const tubeRadius = 0.05;
            const tubeSegments = 8;

            if (frontPoints.length > 1) {
                const frontCurve = new THREE.CatmullRomCurve3(frontPoints, true);
                const frontTubeGeometry = new THREE.TubeGeometry(frontCurve, frontPoints.length, tubeRadius, tubeSegments, true);
                const frontTubeMaterial = new THREE.MeshStandardMaterial({ color: 0xFFA500 });
                const frontTube = new THREE.Mesh(frontTubeGeometry, frontTubeMaterial);
                boundaryCurves.push(frontTube);
                scene.add(frontTube);
            }

            if (backPoints.length > 1) {
                const backCurve = new THREE.CatmullRomCurve3(backPoints, true);
                const backTubeGeometry = new THREE.TubeGeometry(backCurve, backPoints.length, tubeRadius, tubeSegments, true);
                const backTubeMaterial = new THREE.MeshStandardMaterial({ color: 0x4169E1 });
                const backTube = new THREE.Mesh(backTubeGeometry, backTubeMaterial);
                boundaryCurves.push(backTube);
                scene.add(backTube);
            }
        }

        // Add event listeners
        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', () => {
                updateLabels();
                updateScene();
            });
        });

        boundaryOnlyCheckbox.addEventListener('change', updateScene);

        // Handle window resize
        function handleResize() {
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        window.addEventListener('resize', handleResize);

        // Initial setup
        updateLabels();
        updateScene();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();
    </script>
</body>
</html>
