</head>
<body>
    <main id="app-container">
        <div id="controls-container">
            <button id="btn-reset">+</button>
            <div class="slider-row">
                <label for="slider-hue">hue</label>
                <input type="range" id="slider-hue" min="0" max="1" step="0.001" value="0">
                <span id="value-hue">0</span>
            </div>
            <div class="slider-row">
                <label for="slider-saturation">saturation</label>
                <input type="range" id="slider-saturation" min="0" max="1" step="0.001" value="0.5">
                <span id="value-saturation">0.5</span>
            </div>
            <div class="slider-row">
                <label for="slider-brightness">brightness</label>
                <input type="range" id="slider-brightness" min="0" max="1" step="0.001" value="1">
                <span id="value-brightness">1</span>
            </div>
        </div>

        <div id="visualization-container">
            <div class="bar-wrapper">
                <div id="canvas-hue-parent"></div>
                <div id="arrow-hue" class="arrow">↑</div>
            </div>
            <div class="bar-wrapper">
                <div id="canvas-saturation-parent"></div>
                <div id="arrow-saturation" class="arrow">↑</div>
            </div>
            <div class="bar-wrapper">
                <div id="canvas-brightness-parent"></div>
                <div id="arrow-brightness" class="arrow">↑</div>
            </div>
            <div class="bar-wrapper">
                <div id="canvas-result-parent"></div>
            </div>
        </div>
    </main>

    <script>
        // Global variables to store sketch instances
        let hueSketch, saturationSketch, brightnessSketch, resultSketch;
        let hueCanvas, saturationCanvas, brightnessCanvas, resultCanvas;

        // Get DOM elements
        const sliderHue = document.getElementById('slider-hue');
        const sliderSaturation = document.getElementById('slider-saturation');
        const sliderBrightness = document.getElementById('slider-brightness');
        const valueHue = document.getElementById('value-hue');
        const valueSaturation = document.getElementById('value-saturation');
        const valueBrightness = document.getElementById('value-brightness');
        const btnReset = document.getElementById('btn-reset');
        const arrowHue = document.getElementById('arrow-hue');
        const arrowSaturation = document.getElementById('arrow-saturation');
        const arrowBrightness = document.getElementById('arrow-brightness');

        // Hue bar sketch (static)
        function hueSketchCode(p) {
            p.setup = function() {
                const parent = document.getElementById('canvas-hue-parent');
                hueCanvas = p.createCanvas(parent.offsetWidth, 40);
                hueCanvas.parent('canvas-hue-parent');
                p.colorMode(p.HSB, 1, 1, 1);
                drawHueBar();
            };

            function drawHueBar() {
                p.noStroke();
                for (let x = 0; x < p.width; x++) {
                    const hue = x / p.width;
                    p.stroke(hue, 1, 1);
                    p.line(x, 0, x, p.height);
                }
            }
        }

        // Saturation bar sketch (dynamic)
        function saturationSketchCode(p) {
            p.setup = function() {
                const parent = document.getElementById('canvas-saturation-parent');
                saturationCanvas = p.createCanvas(parent.offsetWidth, 40);
                saturationCanvas.parent('canvas-saturation-parent');
                p.colorMode(p.HSB, 1, 1, 1);
                drawSaturationBar(parseFloat(sliderHue.value), parseFloat(sliderBrightness.value));
            };

            p.drawSaturationBar = function(hue, brightness) {
                p.noStroke();
                for (let x = 0; x < p.width; x++) {
                    const saturation = x / p.width;
                    p.stroke(hue, saturation, brightness);
                    p.line(x, 0, x, p.height);
                }
            };
        }

        // Brightness bar sketch (dynamic)
        function brightnessSketchCode(p) {
            p.setup = function() {
                const parent = document.getElementById('canvas-brightness-parent');
                brightnessCanvas = p.createCanvas(parent.offsetWidth, 40);
                brightnessCanvas.parent('canvas-brightness-parent');
                p.colorMode(p.HSB, 1, 1, 1);
                drawBrightnessBar(parseFloat(sliderHue.value), parseFloat(sliderSaturation.value));
            };

            p.drawBrightnessBar = function(hue, saturation) {
                p.noStroke();
                for (let x = 0; x < p.width; x++) {
                    const brightness = x / p.width;
                    p.stroke(hue, saturation, brightness);
                    p.line(x, 0, x, p.height);
                }
            };
        }

        // Result bar sketch (dynamic)
        function resultSketchCode(p) {
            p.setup = function() {
                const parent = document.getElementById('canvas-result-parent');
                resultCanvas = p.createCanvas(parent.offsetWidth, 40);
                resultCanvas.parent('canvas-result-parent');
                p.colorMode(p.HSB, 1, 1, 1);
                drawResultBar(parseFloat(sliderHue.value), parseFloat(sliderSaturation.value), parseFloat(sliderBrightness.value));
            };

            p.drawResultBar = function(hue, saturation, brightness) {
                p.background(hue, saturation, brightness);
            };
        }

        // Helper functions to draw bars
        function drawSaturationBar(hue, brightness) {
            if (saturationSketch) {
                saturationSketch.drawSaturationBar(hue, brightness);
            }
        }

        function drawBrightnessBar(hue, saturation) {
            if (brightnessSketch) {
                brightnessSketch.drawBrightnessBar(hue, saturation);
            }
        }

        function drawResultBar(hue, saturation, brightness) {
            if (resultSketch) {
                resultSketch.drawResultBar(hue, saturation, brightness);
            }
        }

        // Update arrow positions
        function updateArrowPosition(arrowId, value, canvasWidth) {
            const arrow = document.getElementById(arrowId);
            const arrowWidth = arrow.offsetWidth;
            const leftPosition = (value * canvasWidth) - (arrowWidth / 2);
            arrow.style.left = leftPosition + 'px';
        }

        // Update all visuals
        function updateVisuals() {
            const hue = parseFloat(sliderHue.value);
            const saturation = parseFloat(sliderSaturation.value);
            const brightness = parseFloat(sliderBrightness.value);

            // Update value displays
            valueHue.textContent = hue.toFixed(3);
            valueSaturation.textContent = saturation.toFixed(3);
            valueBrightness.textContent = brightness.toFixed(3);

            // Update bars
            drawSaturationBar(hue, brightness);
            drawBrightnessBar(hue, saturation);
            drawResultBar(hue, saturation, brightness);

            // Update arrows
            updateArrowPosition('arrow-hue', hue, hueCanvas.width);
            updateArrowPosition('arrow-saturation', saturation, saturationCanvas.width);
            updateArrowPosition('arrow-brightness', brightness, brightnessCanvas.width);
        }

        // Initialize p5.js instances
        hueSketch = new p5(hueSketchCode);
        saturationSketch = new p5(saturationSketchCode);
        brightnessSketch = new p5(brightnessSketchCode);
        resultSketch = new p5(resultSketchCode);

        // Wait for all sketches to be initialized before setting up event listeners
        setTimeout(() => {
            // Add event listeners
            sliderHue.addEventListener('input', updateVisuals);
            sliderSaturation.addEventListener('input', updateVisuals);
            sliderBrightness.addEventListener('input', updateVisuals);

            btnReset.addEventListener('click', () => {
                sliderHue.value = 0;
                sliderSaturation.value = 0.5;
                sliderBrightness.value = 1;
                updateVisuals();
            });

            // Initial update
            updateVisuals();
        }, 100);

        // Handle window resize
        window.addEventListener('resize', () => {
            // Recreate canvases with new dimensions
            setTimeout(() => {
                updateVisuals();
            }, 100);
        });
    </script>
</body>
</html>
