            border: 1px solid #ccc;
            border-radius: 5px;
            background-color: #f0f0f0;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-alpha">α</label>
                <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05">
                <span id="value-alpha" class="value-display">0.0500</span>
            </div>
            <div class="control-row">
                <label for="slider-beta">β</label>
                <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5">
                <span id="value-beta" class="value-display">5.00</span>
            </div>
            <div class="control-row">
                <label for="slider-gamma">γ</label>
                <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
                <span id="value-gamma" class="value-display">500</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Get DOM elements
        const sliderAlpha = document.getElementById('slider-alpha');
        const sliderBeta = document.getElementById('slider-beta');
        const sliderGamma = document.getElementById('slider-gamma');
        const valueAlpha = document.getElementById('value-alpha');
        const valueBeta = document.getElementById('value-beta');
        const valueGamma = document.getElementById('value-gamma');
        const plotContainer = document.getElementById('plot-container');

        // ODE System definition
        function odesystem(t, y, alpha, beta, gamma) {
            const dT = y[0];
            const Cx = y[1];

            const expTerm = Math.exp(dT / (1 + dT / alpha));

            const dydt = [
                gamma * Cx * expTerm - beta * dT,
                1 - Cx * expTerm
            ];

            return dydt;
        }

        // RK4 solver
        function solveODE(alpha, beta, gamma) {
            const tStart = 0;
            const tEnd = 20;
            const h = 0.05;
            const steps = Math.floor((tEnd - tStart) / h);

            // Initialize arrays
            const t = [];
            const dT = [];
            const Cx = [];

            // Initial conditions
            let y = [0, 0];

            t[0] = tStart;
            dT[0] = y[0];
            Cx[0] = y[1];

            // RK4 integration
            for (let i = 1; i <= steps; i++) {
                const k1 = odesystem(t[i-1], y, alpha, beta, gamma);

                const ytemp = [y[0] + 0.5 * h * k1[0], y[1] + 0.5 * h * k1[1]];
                const k2 = odesystem(t[i-1] + 0.5 * h, ytemp, alpha, beta, gamma);

                const ytemp2 = [y[0] + 0.5 * h * k2[0], y[1] + 0.5 * h * k2[1]];
                const k3 = odesystem(t[i-1] + 0.5 * h, ytemp2, alpha, beta, gamma);

                const ytemp3 = [y[0] + h * k3[0], y[1] + h * k3[1]];
                const k4 = odesystem(t[i-1] + h, ytemp3, alpha, beta, gamma);

                y[0] = y[0] + (h / 6) * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]);
                y[1] = y[1] + (h / 6) * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1]);

                t[i] = t[i-1] + h;
                dT[i] = y[0];
                Cx[i] = y[1];
            }

            return { t, dT, Cx };
        }

        // Function to update the plot
        function updatePlot() {
            const alpha = parseFloat(sliderAlpha.value);
            const beta = parseFloat(sliderBeta.value);
            const gamma = parseFloat(sliderGamma.value);

            // Update value displays
            valueAlpha.textContent = alpha.toFixed(4);
            valueBeta.textContent = beta.toFixed(2);
            valueGamma.textContent = gamma.toFixed(0);

            // Solve ODE
            const solution = solveODE(alpha, beta, gamma);

            // Scale Cx by 1000
            const Cx_scaled = solution.Cx.map(c => c * 1000);

            // Create traces
            const trace1 = {
                x: solution.t,
                y: solution.dT,
                mode: 'lines',
                line: { color: 'orange', width: 2 },
                name: 'ΔT'
            };

            const trace2 = {
                x: solution.t,
                y: Cx_scaled,
                mode: 'lines',
                line: { color: 'steelblue', width: 2 },
                name: '1000 C<sub>X</sub>'
            };

            // Layout configuration
            const layout = {
                xaxis: {
                    range: [0, 20],
                    zeroline: true,
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    range: [0, 100],
                    zeroline: true,
                    gridcolor: '#e0e0e0'
                },
                showlegend: false,
                margin: { l: 50, r: 20, b: 30, t: 20 },
                plot_bgcolor: '#f0f0f0',
                paper_bgcolor: 'white',
                annotations: [
                    {
                        text: 'ΔT',
                        x: -0.05,
                        y: 100,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple', size: 14 }
                    },
                    {
                        text: '1000 C<sub>X</sub>',
                        x: -0.05,
                        y: 95,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple', size: 14 }
                    }
                ]
            };

            // Plot
            Plotly.newPlot(plotContainer, [trace1, trace2], layout, {responsive: true});
        }

        // Add event listeners
        sliderAlpha.addEventListener('input', updatePlot);
        sliderBeta.addEventListener('input', updatePlot);
        sliderGamma.addEventListener('input', updatePlot);

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
