<body>
    <div id="main-container">
        <div id="controls-container">
            <div class="slider-group">
                <label for="slider-time">time</label>
                <input type="range" id="slider-time" min="0" max="25" step="0.01" value="7.0">
                <span id="value-time" class="value-display">7.00</span>
            </div>
            <div class="slider-group">
                <label for="slider-wavelength">wavelength</label>
                <input type="range" id="slider-wavelength" min="1" max="8" step="0.01" value="4.0">
                <span id="value-wavelength" class="value-display">4.00</span>
            </div>
            <div class="slider-group">
                <label for="slider-longitudinal-amplitude">longitudinal amplitude</label>
                <input type="range" id="slider-longitudinal-amplitude" min="0" max="3" step="0.01" value="2.0">
                <span id="value-longitudinal-amplitude" class="value-display">2.00</span>
            </div>
            <div class="slider-group">
                <label for="slider-latitudinal-amplitude">latitudinal amplitude</label>
                <input type="range" id="slider-latitudinal-amplitude" min="0" max="3" step="0.01" value="1.0">
                <span id="value-latitudinal-amplitude" class="value-display">1.00</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // Global variables
        let canvas;
        let particles = [];
        let cols = 10;
        let rows = 6;
        let time = 7.0;
        let isSliderDragging = false;

        // HTML elements
        const sliderTime = document.getElementById('slider-time');
        const sliderWavelength = document.getElementById('slider-wavelength');
        const sliderLongitudinal = document.getElementById('slider-longitudinal-amplitude');
        const sliderLatitudinal = document.getElementById('slider-latitudinal-amplitude');
        const valueTime = document.getElementById('value-time');
        const valueWavelength = document.getElementById('value-wavelength');
        const valueLongitudinal = document.getElementById('value-longitudinal-amplitude');
        const valueLatitudinal = document.getElementById('value-latitudinal-amplitude');

        function setup() {
            canvas = createCanvas(700, 450);
            canvas.parent('canvas-container');

            // Initialize particle grid
            let xSpacing = width / (cols + 1);
            let ySpacing = height / (rows + 1);

            for (let i = 0; i < cols; i++) {
                for (let j = 0; j < rows; j++) {
                    particles.push({
                        x0: xSpacing * (i + 1),
                        y0: ySpacing * (j + 1),
                        x: 0,
                        y: 0,
                        row: j
                    });
                }
            }

            // Add event listeners for sliders
            sliderTime.addEventListener('input', handleTimeSliderChange);
            sliderTime.addEventListener('mousedown', () => isSliderDragging = true);
            sliderTime.addEventListener('mouseup', () => isSliderDragging = false);
            sliderTime.addEventListener('mouseleave', () => isSliderDragging = false);

            sliderWavelength.addEventListener('input', handleSliderChange);
            sliderLongitudinal.addEventListener('input', handleSliderChange);
            sliderLatitudinal.addEventListener('input', handleSliderChange);

            // Update initial value displays
            updateValueDisplays();
        }

        function draw() {
            // Get current slider values
            let wavelength = parseFloat(sliderWavelength.value);
            let longitudinalAmp = parseFloat(sliderLongitudinal.value);
            let latitudinalAmp = parseFloat(sliderLatitudinal.value);

            // Update time if not dragging time slider
            if (!isSliderDragging) {
                time += 0.03;
                if (time > 25) time = 0;
                sliderTime.value = time;
                valueTime.textContent = time.toFixed(2);
            } else {
                time = parseFloat(sliderTime.value);
            }

            // Clear canvas with light blue background
            background('#E0F0FF');

            // Calculate wave properties
            let k = TWO_PI / wavelength;

            // Draw elliptical paths
            noFill();
            strokeWeight(2);
            stroke('#8F998F');

            for (let particle of particles) {
                let rowIndex = particle.row;
                let ampDecay = Math.exp(- (rowIndex / (rows - 1)) * 1.5);
                let ampX = longitudinalAmp * ampDecay * 20;
                let ampY = latitudinalAmp * ampDecay * 20;

                ellipse(particle.x0, particle.y0, 2 * ampX, 2 * ampY);
            }

            // Calculate and store particle positions
            let particlePositions = [];

            for (let particle of particles) {
                let rowIndex = particle.row;
                let ampDecay = Math.exp(- (rowIndex / (rows - 1)) * 1.5);
                let ampX = longitudinalAmp * ampDecay * 20;
                let ampY = latitudinalAmp * ampDecay * 20;
                let phase = k * particle.x0 - time;

                // Retrograde motion (counter-clockwise for wave moving right)
                let dx = ampX * cos(phase);
                let dy = ampY * sin(phase);

                particlePositions.push({
                    x: particle.x0 + dx,
                    y: particle.y0 + dy,
                    row: particle.row,
                    col: Math.floor(particles.indexOf(particle) / rows)
                });
            }

            // Draw connecting lines with dashed style
            stroke('#0000FF');
            strokeWeight(2);
            drawingContext.setLineDash([5, 5]);

            for (let row = 0; row < rows; row++) {
                let rowParticles = particlePositions.filter(p => p.row === row);
                rowParticles.sort((a, b) => a.col - b.col);

                beginShape();
                noFill();
                for (let i = 0; i < rowParticles.length; i++) {
                    if (i === 0) {
                        vertex(rowParticles[i].x, rowParticles[i].y);
                    } else {
                        vertex(rowParticles[i].x, rowParticles[i].y);
                    }
                }
                endShape();
            }

            // Reset line dash
            drawingContext.setLineDash([]);

            // Draw particles
            noStroke();
            fill('#FF0000');
            for (let pos of particlePositions) {
                circle(pos.x, pos.y, 8);
            }
        }

        function handleTimeSliderChange() {
            valueTime.textContent = parseFloat(sliderTime.value).toFixed(2);
        }

        function handleSliderChange() {
            updateValueDisplays();
        }

        function updateValueDisplays() {
            valueTime.textContent = parseFloat(sliderTime.value).toFixed(2);
            valueWavelength.textContent = parseFloat(sliderWavelength.value).toFixed(2);
            valueLongitudinal.textContent = parseFloat(sliderLongitudinal.value).toFixed(2);
            valueLatitudinal.textContent = parseFloat(sliderLatitudinal.value).toFixed(2);
        }
    </script>
</body>
</html>
