                <div class="plot-title">Linearized Plot</div>
                <div id="plot-bottom"></div>
            </div>
        </div>
    </div>

    <script>
        // Global state
        let currentModel = 'Arrhenius';

        // Get all DOM elements
        const modelButtons = {
            arrhenius: document.getElementById('btn-arrhenius'),
            exponential: document.getElementById('btn-exponential')
        };

        const sliders = {
            n: { element: document.getElementById('slider-n'), display: document.getElementById('value-n') },
            tmin: { element: document.getElementById('slider-tmin'), display: document.getElementById('value-tmin') },
            tmax: { element: document.getElementById('slider-tmax'), display: document.getElementById('value-tmax') },
            tref: { element: document.getElementById('slider-tref'), display: document.getElementById('value-tref') },
            c: { element: document.getElementById('slider-c'), display: document.getElementById('value-c') },
            b: { element: document.getElementById('slider-b'), display: document.getElementById('value-b') },
            a: { element: document.getElementById('slider-a'), display: document.getElementById('value-a') }
        };

        // Linear regression function
        function linearRegression(points) {
            const n = points.length;
            let sumX = 0, sumY = 0, sumXY = 0, sumX2 = 0, sumY2 = 0;

            for (const [x, y] of points) {
                sumX += x;
                sumY += y;
                sumXY += x * y;
                sumX2 += x * x;
                sumY2 += y * y;
            }

            const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
            const intercept = (sumY - slope * sumX) / n;

            const ssTotal = sumY2 - (sumY * sumY) / n;
            const ssResidual = ssTotal - slope * (n * sumXY - sumX * sumY);
            const rSquared = ssTotal > 0 ? (1 - ssResidual / ssTotal) : 1;

            return { slope, intercept, rSquared };
        }

        // Generate data based on current model
        function generateData() {
            const n = parseInt(sliders.n.element.value);
            const tMin = parseFloat(sliders.tmin.element.value);
            const tMax = parseFloat(sliders.tmax.element.value);
            const tRef = parseFloat(sliders.tref.element.value);
            const c = parseFloat(sliders.c.element.value);
            const b = parseFloat(sliders.b.element.value);
            const a = parseFloat(sliders.a.element.value);

            // Generate temperature array
            const temperatures = [];
            for (let i = 0; i < n; i++) {
                temperatures.push(tMin + (tMax - tMin) * i / (n - 1));
            }

            // Generate y values based on model
            const yValues = [];
            if (currentModel === 'Arrhenius') {
                const kRef = Math.exp(-a / (tRef + b));
                for (const T of temperatures) {
                    const kT = Math.exp(-a / (T + b));
                    yValues.push(kT / kRef);
                }
            } else {
                for (const T of temperatures) {
                    yValues.push(Math.exp(c * (T - tRef)));
                }
            }

            return { temperatures, yValues };
        }

        // Perform fit and update plots
        function updatePlots() {
            const n = parseInt(sliders.n.element.value);
            const tRef = parseFloat(sliders.tref.element.value);
            const b = parseFloat(sliders.b.element.value);

            const { temperatures, yValues } = generateData();

            let fitParams, fitPoints, transformedX, transformedY;

            if (currentModel === 'Arrhenius') {
                // Fit with exponential model
                const regressionPoints = temperatures.map((T, i) => [T - tRef, Math.log(yValues[i])]);
                const regression = linearRegression(regressionPoints);
                fitParams = { c: regression.slope, rSquared: regression.rSquared };

                // Generate fit curve
                const fitTemperatures = [];
                for (let i = 0; i <= 100; i++) {
                    fitTemperatures.push(temperatures[0] + (temperatures[temperatures.length - 1] - temperatures[0]) * i / 100);
                }
                fitPoints = fitTemperatures.map(T => Math.exp(regression.slope * (T - tRef)));

                transformedX = regressionPoints.map(p => p[0]);
                transformedY = regressionPoints.map(p => p[1]);

                // Update top plot
                Plotly.newPlot('plot-top', [
                    {
                        x: temperatures,
                        y: yValues,
                        type: 'scatter',
                        mode: 'markers',
                        name: 'Data',
                        marker: { color: '#d62728', size: 8 }
                    },
                    {
                        x: fitTemperatures,
                        y: fitPoints,
                        type: 'scatter',
                        mode: 'lines',
                        name: 'Fit',
                        line: { color: '#d62728', width: 2 }
                    }
                ], {
                    title: `exponential model fit to Arrhenius data<br>c = ${regression.slope.toFixed(4)}, R² = ${regression.rSquared.toFixed(4)}`,
                    xaxis: { title: 'T (°C)' },
                    yaxis: { title: 'k(T)/k(T<sub>ref</sub>)' },
                    showlegend: false,
                    margin: { l: 60, r: 30, t: 60, b: 50 }
                });

                // Update bottom plot
                Plotly.newPlot('plot-bottom', [
                    {
                        x: transformedX,
                        y: transformedY,
                        type: 'scatter',
                        mode: 'markers',
                        name: 'Transformed Data',
                        marker: { color: '#2ca02c', size: 8 }
                    },
                    {
                        x: [Math.min(...transformedX), Math.max(...transformedX)],
                        y: [
                            regression.slope * Math.min(...transformedX) + regression.intercept,
                            regression.slope * Math.max(...transformedX) + regression.intercept
                        ],
                        type: 'scatter',
                        mode: 'lines',
                        name: 'Linear Fit',
                        line: { color: '#2ca02c', width: 2 }
                    }
                ], {
                    title: `linear fit of the exponential model<br>R² = ${regression.rSquared.toFixed(4)}`,
                    xaxis: { title: 'T - T<sub>ref</sub>' },
                    yaxis: { title: 'log(k(T)/k(T<sub>ref</sub>))' },
                    showlegend: false,
                    margin: { l: 60, r: 30, t: 60, b: 50 },
                    shapes: [
                        {
                            type: 'line',
                            x0: 0,
                            y0: Math.min(...transformedY),
                            x1: 0,
                            y1: Math.max(...transformedY),
                            line: { color: 'gray', width: 1, dash: 'dot' }
                        },
                        {
                            type: 'line',
                            x0: Math.min(...transformedX),
                            y0: 0,
                            x1: Math.max(...transformedX),
                            y1: 0,
                            line: { color: 'gray', width: 1, dash: 'dot' }
                        }
                    ]
                });
            } else {
                // Fit with Arrhenius model
                const regressionPoints = temperatures.map((T, i) => [1 / (T + b), Math.log(yValues[i])]);
                const regression = linearRegression(regressionPoints);
                fitParams = { a: -regression.slope, rSquared: regression.rSquared };

                // Generate fit curve
                const fitTemperatures = [];
                for (let i = 0; i <= 100; i++) {
                    fitTemperatures.push(temperatures[0] + (temperatures[temperatures.length - 1] - temperatures[0]) * i / 100);
                }
                const kRef = Math.exp(regression.slope / (tRef + b) + regression.intercept);
                fitPoints = fitTemperatures.map(T => Math.exp(regression.slope / (T + b) + regression.intercept) / kRef);

                transformedX = regressionPoints.map(p => p[0]);
                transformedY = regressionPoints.map(p => p[1]);

                // Update top plot
                Plotly.newPlot('plot-top', [
                    {
                        x: temperatures,
                        y: yValues,
                        type: 'scatter',
                        mode: 'markers',
                        name: 'Data',
                        marker: { color: '#d62728', size: 8 }
                    },
                    {
                        x: fitTemperatures,
                        y: fitPoints,
                        type: 'scatter',
                        mode: 'lines',
                        name: 'Fit',
                        line: { color: '#d62728', width: 2 }
                    }
                ], {
                    title: `Arrhenius model fit to exponential data<br>a = ${(-regression.slope).toFixed(1)}, R² = ${regression.rSquared.toFixed(4)}`,
                    xaxis: { title: 'T (°C)' },
                    yaxis: { title: 'k(T)/k(T<sub>ref</sub>)' },
                    showlegend: false,
                    margin: { l: 60, r: 30, t: 60, b: 50 }
                });

                // Update bottom plot
                const xLineMin = Math.min(...transformedX);
                const xLineMax = Math.max(...transformedX);
                Plotly.newPlot('plot-bottom', [
                    {
                        x: transformedX,
                        y: transformedY,
                        type: 'scatter',
                        mode: 'markers',
                        name: 'Transformed Data',
                        marker: { color: '#1f77b4', size: 8 }
                    },
                    {
                        x: [xLineMin, xLineMax],
                        y: [
                            regression.slope * xLineMin + regression.intercept,
                            regression.slope * xLineMax + regression.intercept
                        ],
                        type: 'scatter',
                        mode: 'lines',
                        name: 'Linear Fit',
                        line: { color: '#1f77b4', width: 2 }
                    }
                ], {
                    title: `linear fit of the Arrhenius model<br>R² = ${regression.rSquared.toFixed(4)}`,
                    xaxis: { title: '1/(T + b)' },
                    yaxis: { title: 'log(k(T)/k(T<sub>ref</sub>))' },
                    showlegend: false,
                    margin: { l: 60, r: 30, t: 60, b: 50 },
                    shapes: [
                        {
                            type: 'line',
                            x0: 0,
                            y0: Math.min(...transformedY),
                            x1: 0,
                            y1: Math.max(...transformedY),
                            line: { color: 'gray', width: 1, dash: 'dot' }
                        },
                        {
                            type: 'line',
                            x0: xLineMin,
                            y0: 0,
                            x1: xLineMax,
                            y1: 0,
                            line: { color: 'gray', width: 1, dash: 'dot' }
                        }
                    ]
                });
            }
        }

        // Event handlers
        modelButtons.arrhenius.addEventListener('click', () => {
            currentModel = 'Arrhenius';
            modelButtons.arrhenius.classList.add('active-button');
            modelButtons.exponential.classList.remove('active-button');
            updatePlots();
        });

        modelButtons.exponential.addEventListener('click', () => {
            currentModel = 'exponential';
            modelButtons.exponential.classList.add('active-button');
            modelButtons.arrhenius.classList.remove('active-button');
            updatePlots();
        });

        // Slider event handlers
        Object.keys(sliders).forEach(key => {
            sliders[key].element.addEventListener('input', (e) => {
                sliders[key].display.textContent = e.target.value;

                // Enforce T_max > T_min constraint
                if (key === 'tmin') {
                    const newMin = parseInt(e.target.value);
                    const tmaxSlider = sliders.tmax.element;
                    tmaxSlider.min = newMin + 1;
                    if (parseInt(tmaxSlider.value) <= newMin) {
                        tmaxSlider.value = newMin + 1;
                        sliders.tmax.display.textContent = newMin + 1;
                    }
                }

                updatePlots();
            });
        });

        // Initial update
        updatePlots();
    </script>
</body>
</html>
