            margin-bottom: 20px;
        }

        #results-container {
            padding: 15px;
            background-color: #f8f8f8;
            border-radius: 5px;
            margin-bottom: 10px;
        }

        #results-container p {
            margin: 5px 0;
        }

        #data-table {
            border-collapse: collapse;
            width: 100%;
        }

        #data-table th, #data-table td {
            border: 1px solid black;
            padding: 8px;
            text-align: center;
        }

        #data-table th {
            background-color: #f2f2f2;
        }
    </style>
</head>
<body>
    <div id="left-column">
        <div id="canvas-container"></div>
        <div id="results-container">
            <p>rejuvenation bias <i>u</i> = <span id="results-u"></span></p>
            <p>site productivity <i>p</i> = <span id="results-p"></span></p>
        </div>
    </div>

    <div id="right-column">
        <table id="data-table">
            <thead>
                <tr>
                    <th>age</th>
                    <th>volume</th>
                </tr>
            </thead>
            <tbody id="data-table-body"></tbody>
        </table>
    </div>

    <script>
        let dataPoints = [
            { "age": 30, "volume": 289 },
            { "age": 35, "volume": 445 },
            { "age": 40, "volume": 565 },
            { "age": 45, "volume": 704 },
            { "age": 50, "volume": 833 },
            { "age": 55, "volume": 924 }
        ];

        let p = 58.2729;
        let u = 22.7319;
        let selectedPoint = null;
        let canvasWidth = 500;
        let canvasHeight = 500;
        let margin = 50;

        // Data bounds
        let minAge = 20;
        let maxAge = 80;
        let minVolume = 0;
        let maxVolume = 1500;

        function setup() {
            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');

            // Fit model to initial data
            fitModel();

            // Update UI
            updateResults();
            updateTable();
        }

        function draw() {
            background('#FFFFE0');

            // Draw grid
            drawGrid();

            // Draw axes
            drawAxes();

            // Draw labels
            drawLabels();

            // Draw fitted curve
            drawFittedCurve();

            // Draw data points
            drawDataPoints();
        }

        function drawGrid() {
            stroke(200);
            strokeWeight(1);
            setLineDash([5, 5]);

            // Vertical grid lines
            for (let age = minAge; age <= maxAge; age += 10) {
                let x = map(age, minAge, maxAge, margin, canvasWidth - margin);
                line(x, margin, x, canvasHeight - margin);
            }

            // Horizontal grid lines
            for (let volume = minVolume; volume <= maxVolume; volume += 200) {
                let y = map(volume, minVolume, maxVolume, canvasHeight - margin, margin);
                line(margin, y, canvasWidth - margin, y);
            }

            setLineDash([]);
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(2);

            // X-axis
            line(margin, canvasHeight - margin, canvasWidth - margin, canvasHeight - margin);

            // Y-axis
            line(margin, margin, margin, canvasHeight - margin);

            // Draw ticks and labels
            textAlign(CENTER, CENTER);
            textSize(12);

            // X-axis ticks
            for (let age = minAge; age <= maxAge; age += 10) {
                let x = map(age, minAge, maxAge, margin, canvasWidth - margin);
                line(x, canvasHeight - margin, x, canvasHeight - margin + 5);
                text(age, x, canvasHeight - margin + 20);
            }

            // Y-axis ticks
            for (let volume = 0; volume <= maxVolume; volume += 200) {
                let y = map(volume, minVolume, maxVolume, canvasHeight - margin, margin);
                line(margin - 5, y, margin, y);
                text(volume, margin - 20, y);
            }
        }

        function drawLabels() {
            fill(0);
            noStroke();
            textAlign(CENTER);
            textSize(16);
            text("Forest Growth Curve", canvasWidth / 2, 20);

            textSize(14);
            text("Stand age, yr", canvasWidth / 2, canvasHeight - 10);

            // Y-axis label (rotated)
            push();
            translate(15, canvasHeight / 2);
            rotate(-PI / 2);
            text("Wood volume, m³ ha⁻¹", 0, 0);
            pop();

            // 4/5 law label (rotated on right)
            push();
            translate(canvasWidth - 20, canvasHeight / 2);
            rotate(-PI / 2);
            text("4/5 law", 0, 0);
            pop();
        }

        function drawDataPoints() {
            for (let point of dataPoints) {
                let x = map(point.age, minAge, maxAge, margin, canvasWidth - margin);
                let y = map(point.volume, minVolume, maxVolume, canvasHeight - margin, margin);

                fill('#0000CD');
                stroke(0);
                strokeWeight(1);
                ellipse(x, y, 10, 10);
            }
        }

        function drawFittedCurve() {
            stroke('#0000CD');
            strokeWeight(2);
            noFill();
            beginShape();

            for (let x = u + 0.1; x <= maxAge; x += 0.5) {
                let y = p * Math.pow(x - u, 4/5);
                if (y <= maxVolume) {
                    let px = map(x, minAge, maxAge, margin, canvasWidth - margin);
                    let py = map(y, minVolume, maxVolume, canvasHeight - margin, margin);
                    vertex(px, py);
                }
            }

            endShape();
        }

        function mousePressed() {
            // Check if clicking on existing point
            for (let i = 0; i < dataPoints.length; i++) {
                let point = dataPoints[i];
                let x = map(point.age, minAge, maxAge, margin, canvasWidth - margin);
                let y = map(point.volume, minVolume, maxVolume, canvasHeight - margin, margin);

                if (dist(mouseX, mouseY, x, y) < 10) {
                    selectedPoint = i;
                    return;
                }
            }

            // If not clicking on point, add new point
            if (mouseX > margin && mouseX < canvasWidth - margin &&
                mouseY > margin && mouseY < canvasHeight - margin) {
                let age = map(mouseX, margin, canvasWidth - margin, minAge, maxAge);
                let volume = map(mouseY, canvasHeight - margin, margin, minVolume, maxVolume);

                dataPoints.push({
                    age: Math.round(age * 10) / 10,
                    volume: Math.round(volume * 10) / 10
                });

                fitModel();
                updateResults();
                updateTable();
            }
        }

        function mouseDragged() {
            if (selectedPoint !== null) {
                let age = map(mouseX, margin, canvasWidth - margin, minAge, maxAge);
                let volume = map(mouseY, canvasHeight - margin, margin, minVolume, maxVolume);

                // Constrain values to plot area
                age = constrain(age, minAge, maxAge);
                volume = constrain(volume, minVolume, maxVolume);

                dataPoints[selectedPoint].age = Math.round(age * 10) / 10;
                dataPoints[selectedPoint].volume = Math.round(volume * 10) / 10;

                fitModel();
                updateResults();
                updateTable();
            }
        }

        function mouseReleased() {
            selectedPoint = null;
        }

        function doubleClicked() {
            // Check if double-clicking on existing point
            for (let i = 0; i < dataPoints.length; i++) {
                let point = dataPoints[i];
                let x = map(point.age, minAge, maxAge, margin, canvasWidth - margin);
                let y = map(point.volume, minVolume, maxVolume, canvasHeight - margin, margin);

                if (dist(mouseX, mouseY, x, y) < 10) {
                    dataPoints.splice(i, 1);
                    fitModel();
                    updateResults();
                    updateTable();
                    return;
                }
            }
        }

        function fitModel() {
            // Gradient descent for non-linear regression
            // volume = p * (age - u)^(4/5)

            let learningRate = 0.001;
            let iterations = 1000;
            let minAgeData = Math.min(...dataPoints.map(p => p.age));

            // Initialize parameters
            p = 50;
            u = minAgeData - 1;

            for (let iter = 0; iter < iterations; iter++) {
                let sumErrorP = 0;
                let sumErrorU = 0;

                for (let point of dataPoints) {
                    if (point.age <= u) continue; // Skip invalid points

                    let predicted = p * Math.pow(point.age - u, 4/5);
                    let error = predicted - point.volume;

                    // Gradient with respect to p
                    sumErrorP += error * Math.pow(point.age - u, 4/5);

                    // Gradient with respect to u
                    sumErrorU += error * p * (4/5) * Math.pow(point.age - u, -1/5);
                }

                // Update parameters
                p -= learningRate * 2 * sumErrorP;
                u += learningRate * 2 * sumErrorU; // Note: + because of negative derivative

                // Constraint: u must be less than minimum age
                u = Math.min(u, minAgeData - 0.01);
            }

            // Round to 4 decimal places
            p = Math.round(p * 10000) / 10000;
            u = Math.round(u * 10000) / 10000;
        }

        function updateResults() {
            document.getElementById('results-u').textContent = u.toFixed(4);
            document.getElementById('results-p').textContent = p.toFixed(4);
        }

        function updateTable() {
            let tbody = document.getElementById('data-table-body');
            tbody.innerHTML = '';

            for (let point of dataPoints) {
                let row = tbody.insertRow();
                let ageCell = row.insertCell(0);
                let volumeCell = row.insertCell(1);

                ageCell.textContent = point.age;
                volumeCell.textContent = point.volume;
            }
        }

        // Helper function to draw dashed lines
        function setLineDash(list) {
            drawingContext.setLineDash(list);
        }
    </script>
</body>
</html>
