<body>
    <div id="main-container">
        <div id="control-panel">
            <h1>Parameter Controls</h1>

            <div class="slider-container">
                <label for="slider-order">expansion order</label>
                <div class="slider-row">
                    <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
                    <span id="value-order" class="value-display">5</span>
                </div>
            </div>

            <div class="slider-container">
                <label for="slider-a">f(0)</label>
                <div class="slider-row">
                    <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
                    <span id="value-a" class="value-display">2.0</span>
                </div>
            </div>

            <div class="slider-container">
                <label for="slider-b">f'(0)</label>
                <div class="slider-row">
                    <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
                    <span id="value-b" class="value-display">0.0</span>
                </div>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Initialize plot
        let plotData = [];
        let layout = {};

        // Get DOM elements
        const sliderOrder = document.getElementById('slider-order');
        const sliderA = document.getElementById('slider-a');
        const sliderB = document.getElementById('slider-b');
        const valueOrder = document.getElementById('value-order');
        const valueA = document.getElementById('value-a');
        const valueB = document.getElementById('value-b');

        // Add event listeners
        sliderOrder.addEventListener('input', updatePlot);
        sliderA.addEventListener('input', updatePlot);
        sliderB.addEventListener('input', updatePlot);

        // Factorial function
        function factorial(n) {
            if (n <= 1) return 1;
            return n * factorial(n - 1);
        }

        // RK4 numerical solver
        function rk4Solve(f0, fp0, x0, xEnd, nPoints) {
            const h = (xEnd - x0) / nPoints;
            const xValues = [x0];
            const yValues = [f0];

            function derivatives(y1, y2) {
                const dy1dx = y2;
                const dy2dx = -(y2 + 1.5 * y1) / y1;
                return [dy1dx, dy2dx];
            }

            let y1 = f0;
            let y2 = fp0;

            for (let i = 0; i < nPoints; i++) {
                const k1y1 = h * derivatives(y1, y2)[0];
                const k1y2 = h * derivatives(y1, y2)[1];

                const k2y1 = h * derivatives(y1 + k1y1/2, y2 + k1y2/2)[0];
                const k2y2 = h * derivatives(y1 + k1y1/2, y2 + k1y2/2)[1];

                const k3y1 = h * derivatives(y1 + k2y1/2, y2 + k2y2/2)[0];
                const k3y2 = h * derivatives(y1 + k2y1/2, y2 + k2y2/2)[1];

                const k4y1 = h * derivatives(y1 + k3y1, y2 + k3y2)[0];
                const k4y2 = h * derivatives(y1 + k3y1, y2 + k3y2)[1];

                y1 += (k1y1 + 2*k2y1 + 2*k3y1 + k4y1) / 6;
                y2 += (k1y2 + 2*k2y2 + 2*k3y2 + k4y2) / 6;

                xValues.push(x0 + (i + 1) * h);
                yValues.push(y1);
            }

            return { x: xValues, y: yValues };
        }

        // Format polynomial string
        function formatPolynomial(coeffs, order) {
            let terms = [];

            for (let k = 0; k <= order; k++) {
                if (k < coeffs.length) {
                    const coeff = coeffs[k];
                    if (Math.abs(coeff) > 1e-10) {
                        coeffStr = coeff.toFixed(5).replace(/\.?0+$/, '');

                        let term;
                        if (k === 0) {
                            term = coeffStr;
                        } else {
                            term = (coeff === 1 ? '' : (coeff === -1 ? '-' : coeffStr + ' ')) + 
                                   (k === 1 ? 'x' : 'x^' + k);
                        }

                        terms.push({ k, term });
                    }
                }
            }

            let result = '';
            for (let i = 0; i < terms.length; i++) {
                const { term, k } = terms[i];
                const coeff = coeffs[k];

                if (i === 0) {
                    result = term;
                } else {
                    if (coeff > 0) {
                        result += ' + ' + term;
                    } else {
                        result += ' - ' + term.replace(/^-/, '');
                    }
                }
            }

            return result || '0';
        }

        // Update plot function
        function updatePlot() {
            // Read input values
            const order = parseInt(sliderOrder.value);
            const a = parseFloat(sliderA.value);
            const b = parseFloat(sliderB.value);

            // Update display values
            valueOrder.textContent = order;
            valueA.textContent = a.toFixed(1);
            valueB.textContent = b.toFixed(1);

            // Calculate derivatives at x=0
            const f_derivs = [a, b];
            f_derivs[2] = -(f_derivs[1] + 1.5 * f_derivs[0]) / f_derivs[0];

            if (order >= 3) {
                f_derivs[3] = -[f_derivs[2] * f_derivs[1] + f_derivs[2] + 1.5 * f_derivs[1]] / f_derivs[0];
            }
            if (order >= 4) {
                f_derivs[4] = -[2 * f_derivs[3] * f_derivs[1] + f_derivs[2] * f_derivs[2] + f_derivs[3] + 1.5 * f_derivs[2]] / f_derivs[0];
            }
            if (order >= 5) {
                f_derivs[5] = -[3 * f_derivs[4] * f_derivs[1] + 4 * f_derivs[3] * f_derivs[2] + f_derivs[4] + 1.5 * f_derivs[3]] / f_derivs[0];
            }

            // Calculate coefficients
            const coeffs = [];
            for (let k = 0; k < 6; k++) {
                if (k < f_derivs.length) {
                    coeffs[k] = f_derivs[k] / factorial(k);
                } else {
                    coeffs[k] = 0;
                }
            }

            // Generate power series data
            const nPoints = 200;
            const xMax = 1.5;
            const xPower = [];
            const yPower = [];

            for (let i = 0; i <= nPoints; i++) {
                const x = (xMax * i) / nPoints;
                let y = 0;
                for (let k = 0; k <= order; k++) {
                    if (k < coeffs.length) {
                        y += coeffs[k] * Math.pow(x, k);
                    }
                }
                xPower.push(x);
                yPower.push(y);
            }

            // Generate numerical solution data
            const numerical = rk4Solve(a, b, 0, xMax, nPoints);

            // Find agreement region
            let xLimit = xMax;
            for (let i = 0; i < xPower.length && i < numerical.x.length; i++) {
                if (Math.abs(yPower[i] - numerical.y[i]) >= 0.01) {
                    xLimit = xPower[i];
                    break;
                }
            }

            // Create traces
            const tracePower = {
                x: xPower,
                y: yPower,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'red', width: 2 },
                name: 'Power Series'
            };

            const traceNumerical = {
                x: numerical.x,
                y: numerical.y,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'green', width: 3 },
                name: 'Numerical Solution'
            };

            // Create layout
            layout = {
                title: {
                    text: `f(x) = ${formatPolynomial(coeffs, order)}`,
                    font: { size: 16 }
                },
                xaxis: {
                    title: 'x',
                    range: [0, xMax],
                    zeroline: false,
                    showgrid: true,
                    gridwidth: 1,
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: 'f(x)',
                    range: [-2, 6],
                    zeroline: false,
                    showgrid: true,
                    gridwidth: 1,
                    gridcolor: '#e0e0e0'
                },
                margin: { l: 50, r: 20, t: 80, b: 40 },
                shapes: [{
                    type: 'rect',
                    xref: 'x',
                    yref: 'paper',
                    x0: 0,
                    y0: 0,
                    x1: xLimit,
                    y1: 1,
                    fillcolor: 'lightblue',
                    opacity: 0.4,
                    layer: 'below',
                    line: { width: 0 }
                }],
                showlegend: true,
                legend: {
                    x: 0.02,
                    y: 0.98,
                    xanchor: 'left',
                    yanchor: 'top'
                }
            };

            // Update plot
            Plotly.react('plot-container', [tracePower, traceNumerical], layout);
        }

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
