</head>
<body>
    <div id="main-container">
        <div id="controls">
            <div id="text-control">
                <label for="select-text">text</label>
                <select id="select-text">
                    <option selected>To Be Or Not To Be</option>
                    <option>The quick brown fox jumps over the lazy dog</option>
                    <option>It was the best of times, it was the worst of times</option>
                </select>
            </div>
            <div id="view-controls">
                <div class="button-group">
                    <label>plot</label>
                    <button id="btn-plot-2d" class="active">distribution 2D</button>
                    <button id="btn-plot-3d">keyboard 3D</button>
                </div>
                <div class="button-group">
                    <label>keyboard</label>
                    <button id="btn-keyboard-qwerty" class="active">QWERTY</button>
                    <button id="btn-keyboard-dvorak">Dvorak</button>
                </div>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="visualization-canvas"></canvas>
        </div>
    </div>

    <script>
        // Global variables
        let scene, camera, renderer;
        let frequencies = {};
        let currentView = '2d';
        let currentLayout = 'qwerty';
        let maxFrequency = 0;

        // Keyboard layouts
        const keyboardLayouts = {
            qwerty: {
                rows: [
                    { letters: 'QWERTYUIOP', color: 0x8B9467, yOffset: 0 },
                    { letters: 'ASDFGHJKL', color: 0x5B9BD5, yOffset: -1 },
                    { letters: 'ZXCVBNM', color: 0x7030A0, yOffset: -2 }
                ]
            },
            dvorak: {
                rows: [
                    { letters: 'PYFGCRL', color: 0x8B9467, yOffset: 0 },
                    { letters: 'AOEUIDHTNS', color: 0x5B9BD5, yOffset: -1 },
                    { letters: 'QJKXBMWVZ', color: 0x7030A0, yOffset: -2 }
                ]
            }
        };

        // Initialize Three.js scene
        function initScene() {
            const canvas = document.getElementById('visualization-canvas');
            const container = document.getElementById('canvas-container');

            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xFFFFFF);

            const aspect = canvas.clientWidth / canvas.clientHeight;

            // Create both cameras and switch based on view
            camera = {
                orthographic: new THREE.OrthographicCamera(
                    -aspect * 15, aspect * 15, 15, -15, 0.1, 1000
                ),
                perspective: new THREE.PerspectiveCamera(45, aspect, 0.1, 1000)
            };

            camera.orthographic.position.set(0, 15, 30);
            camera.orthographic.lookAt(0, 0, 0);

            camera.perspective.position.set(20, 30, 30);
            camera.perspective.lookAt(0, 0, 0);

            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
            renderer.shadowMap.enabled = true;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(10, 20, 10);
            directionalLight.castShadow = true;
            scene.add(directionalLight);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function onWindowResize() {
            const canvas = document.getElementById('visualization-canvas');
            const aspect = canvas.clientWidth / canvas.clientHeight;

            camera.orthographic.left = -aspect * 15;
            camera.orthographic.right = aspect * 15;
            camera.orthographic.updateProjectionMatrix();

            camera.perspective.aspect = aspect;
            camera.perspective.updateProjectionMatrix();

            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        // Calculate letter frequencies
        function calculateFrequencies(text) {
            const freq = {};
            for (let i = 65; i <= 90; i++) {
                const letter = String.fromCharCode(i);
                freq[letter] = 0;
            }

            const upperText = text.toUpperCase();
            for (let char of upperText) {
                if (char >= 'A' && char <= 'Z') {
                    freq[char]++;
                }
            }

            maxFrequency = Math.max(...Object.values(freq));
            return freq;
        }

        // Create text texture
        function createTextTexture(text, color = 0x000000) {
            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            canvas.width = 128;
            canvas.height = 128;

            context.fillStyle = 'white';
            context.fillRect(0, 0, canvas.width, canvas.height);

            context.font = 'bold 72px Arial';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillStyle = `#${color.toString(16).padStart(6, '0')}`;
            context.fillText(text, canvas.width / 2, canvas.height / 2);

            const texture = new THREE.CanvasTexture(canvas);
            return texture;
        }

        // Create axes and grid
        function createAxes() {
            const axesGroup = new THREE.Group();

            // Y-axis
            const yAxisGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(0, maxFrequency + 10, 0)
            ]);
            const yAxisMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
            const yAxis = new THREE.Line(yAxisGeometry, yAxisMaterial);
            axesGroup.add(yAxis);

            // X-axis
            const xAxisGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(-15, 0, 0),
                new THREE.Vector3(15, 0, 0)
            ]);
            const xAxis = new THREE.Line(xAxisGeometry, yAxisMaterial);
            axesGroup.add(xAxis);

            // Y-axis labels
            const steps = Math.ceil((maxFrequency + 10) / 50) + 1;
            for (let i = 0; i <= steps; i++) {
                const y = i * 50;
                if (y <= maxFrequency + 10) {
                    const labelGeometry = new THREE.PlaneGeometry(2, 1);
                    const labelTexture = createTextTexture(y.toString());
                    const labelMaterial = new THREE.MeshBasicMaterial({ 
                        map: labelTexture, 
                        transparent: true 
                    });
                    const label = new THREE.Mesh(labelGeometry, labelMaterial);
                    label.position.set(-3, y, 0);
                    label.lookAt(camera.orthographic.position);
                    axesGroup.add(label);

                    // Grid line
                    if (y > 0) {
                        const gridGeometry = new THREE.BufferGeometry().setFromPoints([
                            new THREE.Vector3(-15, y, 0),
                            new THREE.Vector3(15, y, 0)
                        ]);
                        const gridLine = new THREE.Line(gridGeometry, new THREE.LineBasicMaterial({ color: 0xcccccc }));
                        axesGroup.add(gridLine);
                    }
                }
            }

            return axesGroup;
        }

        // Create color gradient
        function getFrequencyColor(frequency) {
            const normalized = frequency / maxFrequency;
            const colors = [
                { pos: 0, color: [59, 130, 246] },    // Blue
                { pos: 0.33, color: [139, 92, 246] }, // Purple
                { pos: 0.66, color: [251, 146, 60] }, // Orange
                { pos: 1, color: [239, 68, 68] }      // Red
            ];

            for (let i = 0; i < colors.length - 1; i++) {
                if (normalized >= colors[i].pos && normalized <= colors[i + 1].pos) {
                    const t = (normalized - colors[i].pos) / (colors[i + 1].pos - colors[i].pos);
                    const r = Math.round(colors[i].color[0] + t * (colors[i + 1].color[0] - colors[i].color[0]));
                    const g = Math.round(colors[i].color[1] + t * (colors[i + 1].color[1] - colors[i].color[1]));
                    const b = Math.round(colors[i].color[2] + t * (colors[i + 1].color[2] - colors[i].color[2]));
                    return (r << 16) | (g << 8) | b;
                }
            }
            return colors[colors.length - 1].color[0] << 16 | colors[colors.length - 1].color[1] << 8 | colors[colors.length - 1].color[2];
        }

        // Render 2D bar chart
        function render2DChart() {
            clearScene();
            scene.add(createAxes());

            const barWidth = 0.8;
            const barDepth = 0.8;
            const spacing = 1.2;
            const startX = -12.5;

            for (let i = 0; i < 26; i++) {
                const letter = String.fromCharCode(65 + i);
                const frequency = frequencies[letter];
                const height = Math.max(frequency, 1);

                // Bar
                const geometry = new THREE.BoxGeometry(barWidth, height, barDepth);
                const color = getFrequencyColor(frequency);
                const material = new THREE.MeshLambertMaterial({ color: color });
                const bar = new THREE.Mesh(geometry, material);
                bar.position.set(startX + i * spacing, height / 2, 0);
                scene.add(bar);

                // Label
                const labelGeometry = new THREE.PlaneGeometry(1, 0.5);
                const labelTexture = createTextTexture(letter);
                const labelMaterial = new THREE.MeshBasicMaterial({ 
                    map: labelTexture, 
                    transparent: true 
                });
                const label = new THREE.Mesh(labelGeometry, labelMaterial);
                label.position.set(startX + i * spacing, height + 1, 0);
                label.lookAt(camera.orthographic.position);
                scene.add(label);
            }

            renderer.render(scene, camera.orthographic);
        }

        // Render 3D keyboard
        function render3DKeyboard() {
            clearScene();
            scene.add(createAxes());

            const layout = keyboardLayouts[currentLayout];
            const keyRadius = 0.8;
            const rowSpacing = 2.5;
            const keySpacing = 2.2;

            layout.rows.forEach(row => {
                const rowWidth = row.letters.length * keySpacing;
                const startX = -rowWidth / 2 + keySpacing / 2;

                for (let i = 0; i < row.letters.length; i++) {
                    const letter = row.letters[i];
                    const frequency = frequencies[letter];
                    const height = Math.max(frequency / 5, 0.5);

                    // Key cylinder
                    const geometry = new THREE.CylinderGeometry(keyRadius, keyRadius, height, 32);
                    const material = new THREE.MeshStandardMaterial({ 
                        color: row.color,
                        metalness: 0.3,
                        roughness: 0.4
                    });
                    const key = new THREE.Mesh(geometry, material);
                    key.position.set(startX + i * keySpacing, height / 2, row.yOffset * rowSpacing);
                    key.rotation.x = Math.PI / 2;
                    key.castShadow = true;
                    key.receiveShadow = true;
                    scene.add(key);

                    // Key label
                    const labelGeometry = new THREE.CircleGeometry(keyRadius * 0.8, 32);
                    const labelTexture = createTextTexture(letter, 0xFFFFFF);
                    const labelMaterial = new THREE.MeshBasicMaterial({ 
                        map: labelTexture, 
                        transparent: true 
                    });
                    const label = new THREE.Mesh(labelGeometry, labelMaterial);
                    label.position.set(startX + i * keySpacing, height / 2 + 0.01, row.yOffset * rowSpacing);
                    label.rotation.x = -Math.PI / 2;
                    scene.add(label);
                }
            });

            renderer.render(scene, camera.perspective);
        }

        // Clear scene
        function clearScene() {
            while (scene.children.length > 2) { // Keep lights
                scene.remove(scene.children[scene.children.length - 1]);
            }
        }

        // Update visualization
        function updateVisualization() {
            const text = document.getElementById('select-text').value;
            frequencies = calculateFrequencies(text);

            if (currentView === '2d') {
                render2DChart();
            } else {
                render3DKeyboard();
            }
        }

        // Initialize event listeners
        function initEventListeners() {
            // Text selection
            document.getElementById('select-text').addEventListener('change', updateVisualization);

            // Plot view buttons
            document.getElementById('btn-plot-2d').addEventListener('click', function() {
                currentView = '2d';
                this.classList.add('active');
                document.getElementById('btn-plot-3d').classList.remove('active');
                updateVisualization();
            });

            document.getElementById('btn-plot-3d').addEventListener('click', function() {
                currentView = '3d';
                this.classList.add('active');
                document.getElementById('btn-plot-2d').classList.remove('active');
                updateVisualization();
            });

            // Keyboard layout buttons
            document.getElementById('btn-keyboard-qwerty').addEventListener('click', function() {
                currentLayout = 'qwerty';
                this.classList.add('active');
                document.getElementById('btn-keyboard-dvorak').classList.remove('active');
                if (currentView === '3d') {
                    updateVisualization();
                }
            });

            document.getElementById('btn-keyboard-dvorak').addEventListener('click', function() {
                currentLayout = 'dvorak';
                this.classList.add('active');
                document.getElementById('btn-keyboard-qwerty').classList.remove('active');
                if (currentView === '3d') {
                    updateVisualization();
                }
            });
        }

        // Initialize application
        function init() {
            initScene();
            initEventListeners();
            updateVisualization();
        }

        // Start when DOM is loaded
        window.addEventListener('DOMContentLoaded', init);
    </script>
</body>
</html>
