            points: 50,
            p_amp: 0.2,
            p_wl: 3,
            s_amp: 0.1,
            s_wl: 1
        };

        function setup() {
            let canvas = createCanvas(800, 400);
            canvas.parent('canvas-container');

            initializeParticles();

            // Set up event listeners
            setupEventListeners();
