        function setupEventListeners() {
            // Slider event listeners
            const sliders = ['slider-time', 'slider-trail', 'slider-points', 'slider-p-amp', 'slider-p-wl', 'slider-s-amp', 'slider-s-wl'];

            sliders.forEach(sliderId => {
                const slider = document.getElementById(sliderId);
                const labelId = sliderId.replace('slider-', 'label-');
                const label = document.getElementById(labelId);

                slider.addEventListener('input', function() {
                    label.textContent = parseFloat(this.value).toFixed(sliderId.includes('amp') || sliderId.includes('wl') || sliderId === 'slider-time' ? 2 : 0);

                    if (sliderId === 'slider-points') {
                        initializeParticles();
                    }
                });
            });

            // Play/Pause button
            document.getElementById('btn-play-pause').addEventListener('click', function() {
                isPlaying = !isPlaying;
                this.textContent = isPlaying ? 'Pause' : 'Play';
            });

            // Reset button
            document.getElementById('btn-reset').addEventListener('click', function() {
                resetToDefaults();
            });
        }

        function resetToDefaults() {
            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = 'Play';

            // Reset all sliders
            Object.keys(defaults).forEach(key => {
                const sliderId = 'slider-' + key.replace('_', '-');
                const slider = document.getElementById(sliderId);
                const labelId = sliderId.replace('slider-', 'label-');
                const label = document.getElementById(labelId);

                slider.value = defaults[key];
                label.textContent = parseFloat(defaults[key]).toFixed(
                    key.includes('amp') || key.includes('wl') || key === 'time' ? 2 : 0
                );
            });

            initializeParticles();
        }

        function draw() {
            background(255);

            // Get current values
            let time = parseFloat(document.getElementById('slider-time').value);
            let trailLength = parseInt(document.getElementById('slider-trail').value);
            let pAmp = parseFloat(document.getElementById('slider-p-amp').value);
            let pWl = parseFloat(document.getElementById('slider-p-wl').value);
            let sAmp = parseFloat(document.getElementById('slider-s-amp').value);
            let sWl = parseFloat(document.getElementById('slider-s-wl').value);

            // Update time if playing
            if (isPlaying) {
                time += 0.01;
                if (time > 5) time = 0;

                document.getElementById('slider-time').value = time;
                document.getElementById('label-time').textContent = time.toFixed(2);
            }

            // Draw axes
            drawAxes();

            // Map logical coordinates to pixel coordinates
            let xMin = 0, xMax = 5;
            let yMin = -0.4, yMax = 0.4;

            // Draw particles
            particles.forEach(particle => {
                // Calculate wave displacements
                let dx = pAmp * sin(2 * PI * (particle.x0 / pWl - time));
                let dy = sAmp * sin(2 * PI * (particle.x0 / sWl - time));

                let xFinal = particle.x0 + dx;
                let yFinal = dy;

                // Map to canvas coordinates
                let xPixel = map(xFinal, xMin, xMax, 50, width - 50);
                let yPixel = map(yFinal, yMin, yMax, height - 50, 50);

                // Add to history
                particle.history.push({x: xPixel, y: yPixel});
                if (particle.history.length > trailLength) {
                    particle.history.shift();
                }

                // Draw vertical line
                stroke(211, 211, 211); // lightgray
                strokeWeight(1);
                line(xPixel, 50, xPixel, height - 50);

                // Draw trail
                if (trailLength > 0) {
                    noStroke();
                    particle.history.forEach((pos, index) => {
                        let alpha = map(index, 0, particle.history.length - 1, 50, 200);
                        fill(106, 90, 205, alpha); // slateblue with transparency
                        circle(pos.x, pos.y, 3);
                    });
                }

                // Draw particle
                fill(106, 90, 205); // slateblue
                noStroke();
                circle(xPixel, yPixel, 6);
            });
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(2);

            // X-axis
            line(50, height - 50, width - 50, height - 50);

            // Y-axis
            line(50, 50, 50, height - 50);

            // X-axis ticks and labels
            strokeWeight(1);
            textAlign(CENTER, TOP);
            textSize(12);
            fill(0);

            for (let i = 0; i <= 5; i++) {
                let x = map(i, 0, 5, 50, width - 50);
                line(x, height - 50, x, height - 45);
                text(i.toString(), x, height - 40);
            }

            // Y-axis ticks and labels
            textAlign(RIGHT, CENTER);
            for (let i = -4; i <= 4; i++) {
                if (i === 0) continue;
                let y = map(i * 0.1, -0.4, 0.4, height - 50, 50);
                line(50, y, 55, y);
                text((i * 0.1).toFixed(1), 45, y);
            }

            // Axis labels
            textAlign(CENTER, CENTER);
            textSize(14);
            text('Distance', width / 2, height - 15);

            push();
            translate(15, height / 2);
            rotate(-PI / 2);
            text('Displacement', 0, 0);
            pop();
        }

        function map(value, start1, stop1, start2, stop2) {
            return start2 + (stop2 - start2) * ((value - start1) / (stop1 - start1));
        }
    </script>
</body>
</html>
