    <script>
        // Constants
        const POISSON_RATIO = 0.25;
        const DISLOCATION_MAGNITUDE = 1.0; // meters
        const GRID_RESOLUTION = 50;

        // State
        let state = {
            dip: 0,
            depth: 500,
            limit: 1000,
            component: 'Z',
            faultType: 'tensile'
        };

        // Initialize
        document.addEventListener('DOMContentLoaded', function() {
            initializeControls();
            updatePlot();
        });

        function initializeControls() {
            // Initialize sliders
            const sliders = ['dip', 'depth', 'limit'];
            sliders.forEach(sliderId => {
                const slider = document.getElementById(`slider-${sliderId}`);
                const valueDisplay = document.getElementById(`value-${sliderId}`);

                slider.addEventListener('input', function() {
                    const value = parseFloat(this.value);
                    state[sliderId] = value;
                    valueDisplay.textContent = value;
                    updatePlot();
                });
            });

            // Initialize component buttons
            const componentButtons = document.querySelectorAll('#btn-group-component button');
            componentButtons.forEach(button => {
                button.addEventListener('click', function() {
                    componentButtons.forEach(btn => btn.classList.remove('active'));
                    this.classList.add('active');
                    state.component = this.textContent;
                    updatePlot();
                });
            });

            // Initialize fault type buttons
            const faultTypeButtons = document.querySelectorAll('#btn-group-fault-type button');
            faultTypeButtons.forEach(button => {
                button.addEventListener('click', function() {
                    faultTypeButtons.forEach(btn => btn.classList.remove('active'));
                    this.classList.add('active');
                    state.faultType = this.id.replace('btn-fault-', '').replace('-', ' ');
                    updatePlot();
                });
            });
        }

        function calculateDisplacements(x, y, dip, depth, faultType, nu) {
            const dipRad = dip * Math.PI / 180;
            const r = Math.sqrt(x * x + y * y + depth * depth);
            const theta = Math.atan2(y, x);
            const phi = Math.atan2(Math.sqrt(x * x + y * y), depth);

            let ux = 0, uy = 0, uz = 0;

            // Simplified displacement calculations for different fault types
            // These are approximations of the full Okada (1985) solutions
            const mu = 1; // Shear modulus (normalized)
            const factor = DISLOCATION_MAGNITUDE / (2 * Math.PI * mu);

            if (faultType === 'tensile') {
                // Tensile fault (opening mode)
                const cosPhi = Math.cos(phi);
                const sinPhi = Math.sin(phi);
                const cosDip = Math.cos(dipRad);
                const sinDip = Math.sin(dipRad);

                const r3 = r * r * r;
                const r5 = r3 * r * r;

                ux = factor * x * depth / r3 * (3 - 2 * nu) * sinDip;
                uy = factor * y * depth / r3 * (3 - 2 * nu) * sinDip;
                uz = factor * (depth * depth / r3 - (1 - 2 * nu) / r) * sinDip;

            } else if (faultType === 'strike-slip') {
                // Strike-slip fault
                const sin2Theta = Math.sin(2 * theta);
                const cos2Theta = Math.cos(2 * theta);

                const r3 = r * r * r;

                ux = factor * y / r3 * (1 - 2 * nu) * Math.cos(dipRad);
                uy = -factor * x / r3 * (1 - 2 * nu) * Math.cos(dipRad);
                uz = 0;

            } else if (faultType === 'normal') {
                // Normal fault
                const cosPhi = Math.cos(phi);
                const sinPhi = Math.sin(phi);
                const cosDip = Math.cos(dipRad);
                const sinDip = Math.sin(dipRad);

                const r3 = r * r * r;

                ux = factor * x * depth / r3 * (3 - 2 * nu) * cosDip;
                uy = factor * y * depth / r3 * (3 - 2 * nu) * cosDip;
                uz = factor * (depth * depth / r3 - (1 - 2 * nu) / r) * cosDip;
            }

            return { ux, uy, uz };
        }

        function updatePlot() {
            const { dip, depth, limit, component, faultType } = state;
            const dipRad = dip * Math.PI / 180;

            // Create grid
            const xCoords = [];
            const yCoords = [];
            const step = (2 * limit) / (GRID_RESOLUTION - 1);

            for (let i = 0; i < GRID_RESOLUTION; i++) {
                xCoords.push(-limit + i * step);
                yCoords.push(-limit + i * step);
            }

            // Calculate displacement values
            const zValues = [];
            for (let i = 0; i < GRID_RESOLUTION; i++) {
                const row = [];
                for (let j = 0; j < GRID_RESOLUTION; j++) {
                    const x = xCoords[j];
                    const y = yCoords[i];
                    const displacements = calculateDisplacements(x, y, dipRad, depth, faultType, POISSON_RATIO);

                    let value;
                    switch (component) {
                        case 'X':
                            value = displacements.ux;
                            break;
                        case 'Y':
                            value = displacements.uy;
                            break;
                        case 'Z':
                            value = displacements.uz;
                            break;
                    }

                    // Convert to millimeters
                    row.push(value * 1000);
                }
                zValues.push(row);
            }

            // Create plot data
            const plotData = [{
                type: 'surface',
                x: xCoords,
                y: yCoords,
                z: zValues,
                colorscale: [['0', '#FFA500'], ['1', '#FFD700']],
                showscale: false,
                contours: {
                    x: { show: true, color: 'black', width: 0.5 },
                    y: { show: true, color: 'black', width: 0.5 },
                    z: { show: false }
                },
                lighting: {
                    ambient: 0.8,
                    diffuse: 0.8,
                    specular: 0.2
                }
            }];

            // Create layout
            const layout = {
                title: '',
                scene: {
                    xaxis: { title: 'x (m)', range: [-limit, limit] },
                    yaxis: { title: 'y (m)', range: [-limit, limit] },
                    zaxis: { title: 'z (mm)', autorange: true },
                    camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } }
                },
                margin: { l: 20, r: 20, b: 20, t: 20 }
            };

            // Plot or update
            Plotly.react('plot', plotData, layout, { responsive: true });
        }
    </script>
