    <script>
        // State variables
        let layeringOrderIndex = 0;
        const layeringPermutations = [
            [1, 2, 3, 4],
            [4, 3, 2, 1],
            [1, 3, 2, 4],
            [2, 4, 1, 3],
            [3, 1, 4, 2]
        ];

        // Circle data
        const circleData = {
            1: { centerX: 0, centerY: 0, diameter: 0, label: '1' },
            2: { centerX: 0, centerY: 0, diameter: 0, label: '2' },
            3: { centerX: 0, centerY: 0, diameter: 0, label: '3' },
            4: { centerX: 0, centerY: 0, diameter: 0, label: '4' }
        };

        // p5.js sketch instance
