                circleData[1].diameter = canvasWidth * 0.4;

                circleData[2].centerX = canvasWidth * 0.65;
                circleData[2].centerY = canvasHeight * 0.4;
                circleData[2].diameter = canvasWidth * 0.4;

                circleData[3].centerX = canvasWidth * 0.5;
                circleData[3].centerY = canvasHeight * 0.65;
                circleData[3].diameter = canvasWidth * 0.4;

                circleData[4].centerX = canvasWidth * 0.5;
                circleData[4].centerY = canvasHeight * 0.45;
                circleData[4].diameter = canvasWidth * 0.3;

                p.noLoop();
                p.draw();
            };

            p.draw = () => {
                // Clear canvas with light gray background
                p.background('#f0f0f0');

                // Get current layering order
                const currentOrder = layeringPermutations[layeringOrderIndex];

                // Draw circles in layering order
                for (let i = 0; i < currentOrder.length; i++) {
                    const circleNumber = currentOrder[i];
                    const circle = circleData[circleNumber];

                    // Get color and opacity
                    const colorPicker = document.getElementById(`color-picker${circleNumber}`);
                    const opacitySlider = document.getElementById(`slider-opacity${circleNumber}`);

                    const rgb = hexToRgb(colorPicker.value);
                    const opacity = parseFloat(opacitySlider.value) * 255;

                    // Set fill color with opacity
                    p.fill(rgb.r, rgb.g, rgb.b, opacity);
                    p.noStroke();

                    // Draw circle
                    p.ellipse(circle.centerX, circle.centerY, circle.diameter, circle.diameter);
                }

                // Draw labels on top
                p.fill(0);
                p.textSize(32);
                p.textAlign(p.CENTER, p.CENTER);

                for (let circleNumber = 1; circleNumber <= 4; circleNumber++) {
                    const circle = circleData[circleNumber];
                    p.text(circle.label, circle.centerX, circle.centerY);
                }
            };

            p.windowResized = () => {
                const container = document.getElementById('canvas-container');
                const containerRect = container.getBoundingClientRect();
                const canvasWidth = containerRect.width - 20;
                const canvasHeight = containerRect.height - 20;

                p.resizeCanvas(canvasWidth, canvasHeight);

                // Update circle positions
                circleData[1].centerX = canvasWidth * 0.35;
                circleData[1].centerY = canvasHeight * 0.4;
                circleData[1].diameter = canvasWidth * 0.4;

                circleData[2].centerX = canvasWidth * 0.65;
                circleData[2].centerY = canvasHeight * 0.4;
                circleData[2].diameter = canvasWidth * 0.4;

                circleData[3].centerX = canvasWidth * 0.5;
                circleData[3].centerY = canvasHeight * 0.65;
                circleData[3].diameter = canvasWidth * 0.4;

                circleData[4].centerX = canvasWidth * 0.5;
                circleData[4].centerY = canvasHeight * 0.45;
                circleData[4].diameter = canvasWidth * 0.3;

                p.draw();
            };
        });

        // Event listeners for opacity sliders
        for (let i = 1; i <= 4; i++) {
            const slider = document.getElementById(`slider-opacity${i}`);
            slider.addEventListener('input', updateCanvas);
        }

        // Event listeners for color pickers
        for (let i = 1; i <= 4; i++) {
            const colorPicker = document.getElementById(`color-picker${i}`);
            colorPicker.addEventListener('input', updateCanvas);
        }

        // Event listeners for layering buttons
        document.getElementById('btn-layer-next').addEventListener('click', () => {
            layeringOrderIndex = (layeringOrderIndex + 1) % layeringPermutations.length;
            const display = document.getElementById('layering-display');
            display.textContent = layeringPermutations[layeringOrderIndex].join(', ');
            updateCanvas();
        });

        document.getElementById('btn-layer-prev').addEventListener('click', () => {
            layeringOrderIndex = (layeringOrderIndex - 1 + layeringPermutations.length) % layeringPermutations.length;
            const display = document.getElementById('layering-display');
            display.textContent = layeringPermutations[layeringOrderIndex].join(', ');
            updateCanvas();
        });

        // Event listener for reset button
        document.getElementById('btn-reset').addEventListener('click', () => {
            // Reset opacity sliders
            for (let i = 1; i <= 4; i++) {
                document.getElementById(`slider-opacity${i}`).value = '0.7';
            }

            // Reset color pickers
            document.getElementById('color-picker1').value = '#00ff00';
            document.getElementById('color-picker2').value = '#0000ff';
            document.getElementById('color-picker3').value = '#ff0000';
            document.getElementById('color-picker4').value = '#000000';

            // Reset layering order
            layeringOrderIndex = 0;
            document.getElementById('layering-display').textContent = '1, 2, 3, 4';

            // Update canvas
            updateCanvas();
        });
    </script>
</body>
</html>
