            background-color: #fafafa;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label>Equation of State:</label>
                <div class="button-group">
                    <button id="btn-srk" class="selected">SRK</button>
                    <button id="btn-pr">PR</button>
                </div>
            </div>

            <div class="control-group checkbox-container">
                <input type="checkbox" id="checkbox-aspen" checked>
                <label for="checkbox-aspen">comparison with Aspen Plus</label>
            </div>

            <div class="control-group slider-container">
                <label for="slider-pressure">pressure P</label>
                <input type="range" id="slider-pressure" min="1" max="20" step="1" value="10">
                <span id="display-pressure">10</span>
            </div>
        </div>

        <div id="plot-container"></div>
    </div>

    <script>
        // Constants for Propylene
        const Tc = 365.57; // K
        const Pc = 46.65; // bar
        const omega = 0.144;
        const R = 0.0831446; // L·bar/(mol·K)

        // Aspen Plus comparison data
        const aspenData = {
            x: [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400],
            y: [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946]
        };

        // State variables
        let currentEoS = 'SRK';
        let currentPressure = 10;
        let showAspen = true;

        // Cubic equation solver
        function solveCubic(a, b, c, d) {
            // Coefficients for x³ + ax² + bx + c = 0
            const A = a;
            const B = b;
            const C = c;
            const D = d;

            // Convert to depressed cubic: t³ + pt + q = 0
            const p = (3 * B - A * A) / 3;
            const q = (2 * A * A * A - 9 * A * B + 27 * C) / 27;

            const discriminant = (q * q) / 4 + (p * p * p) / 27;
            const roots = [];

            if (discriminant > 0) {
                // One real root
                const sqrt_discriminant = Math.sqrt(discriminant);
                const u = Math.cbrt(-q / 2 + sqrt_discriminant);
                const v = Math.cbrt(-q / 2 - sqrt_discriminant);
                const t = u + v;
                roots.push(t - A / 3);
            } else if (discriminant === 0) {
                // Multiple real roots
                const t1 = 3 * q / p;
                const t2 = -3 * q / (2 * p);
                roots.push(t1 - A / 3);
                roots.push(t2 - A / 3);
                roots.push(t2 - A / 3);
            } else {
                // Three real roots
                const sqrt_p = Math.sqrt(-p / 3);
                const theta = Math.acos((3 * q) / (2 * p) * sqrt(-3 / p)) / 3;
                roots.push(2 * sqrt_p * Math.cos(theta) - A / 3);
                roots.push(2 * sqrt_p * Math.cos(theta + 2 * Math.PI / 3) - A / 3);
                roots.push(2 * sqrt_p * Math.cos(theta + 4 * Math.PI / 3) - A / 3);
            }

            // Filter out non-real roots and return the largest real root
            const realRoots = roots.filter(root => Math.abs(root.imag || 0) < 1e-10).map(root => root.real || root);
            if (realRoots.length > 0) {
                return Math.max(...realRoots);
            }

            // Fallback
            return 1;
        }

        // Calculate fugacity coefficient using SRK EoS
        function calculateFugacitySRK(T, P) {
            const Tr = T / Tc;

            const a = 0.42748 * R * R * Tc * Tc / Pc;
            const b = 0.08664 * R * Tc / Pc;
            const m_srk = 0.480 + 1.574 * omega - 0.176 * omega * omega;
            const alpha_srk = Math.pow(1 + m_srk * (1 - Math.sqrt(Tr)), 2);
            const a_eff = a * alpha_srk;

            const A = a_eff * P / (R * T) ** 2;
            const B = b * P / (R * T);

            // Solve cubic: Z³ - Z² + (A - B - B²)Z - AB = 0
            const coeffs = [-1, A - B - B * B, -A * B, 1];
            const Z = solveCubic(...coeffs);

            const ln_phi = Z - 1 - Math.log(Z - B) - (A / B) * Math.log(1 + B / Z);

            return Math.exp(ln_phi);
        }

        // Calculate fugacity coefficient using PR EoS
        function calculateFugacityPR(T, P) {
            const Tr = T / Tc;

            const a = 0.45724 * R * R * Tc * Tc / Pc;
            const b = 0.07780 * R * Tc / Pc;
            const m_pr = 0.37464 + 1.54226 * omega - 0.26992 * omega * omega;
            const alpha_pr = Math.pow(1 + m_pr * (1 - Math.sqrt(Tr)), 2);
            const a_eff = a * alpha_pr;

            const A = a_eff * P / (R * T) ** 2;
            const B = b * P / (R * T);

            // Solve cubic: Z³ - (1-B)Z² + (A - 3B² - 2B)Z - (AB - B² - B³) = 0
            const coeffs = [B - 1, A - 3 * B * B - 2 * B, B * B * B + B * B - A * B, 1];
            const Z = solveCubic(...coeffs);

            const sqrt2 = Math.sqrt(2);
            const ln_phi = Z - 1 - Math.log(Z - B) - (A / (2 * sqrt2 * B)) * 
                          Math.log((Z + (1 + sqrt2) * B) / (Z + (1 - sqrt2) * B));

            return Math.exp(ln_phi);
        }

        // Generate data for the selected EoS
        function generateEoSData() {
            const temperatures = [];
            const fugacities = [];

            for (let T = 300; T <= 500; T += 10) {
                temperatures.push(T);
                const phi = currentEoS === 'SRK' ? 
                    calculateFugacitySRK(T, currentPressure) : 
                    calculateFugacityPR(T, currentPressure);
                fugacities.push(phi);
            }

            return { x: temperatures, y: fugacities };
        }

        // Update the plot
        function updatePlot() {
            const eosData = generateEoSData();
            const traces = [];

            // Add EoS trace
            traces.push({
                x: eosData.x,
                y: eosData.y,
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: currentEoS === 'SRK' ? 'red' : 'blue',
                    width: 2
                },
                name: currentEoS
            });

            // Add Aspen trace if conditions are met
            if (currentPressure === 10 && showAspen) {
                traces.push({
                    x: aspenData.x,
                    y: aspenData.y,
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'green',
                        symbol: 'square',
                        size: 8
                    },
                    name: 'Aspen Plus'
                });
            }

            const layout = {
                title: 'Gas-Phase Fugacity Coefficient vs Temperature',
                xaxis: {
                    title: 'temperature (K)',
                    range: [295, 505],
                    gridcolor: 'white',
                    showgrid: true,
                    showminorgrid: true,
                    zerolinecolor: '#666'
                },
                yaxis: {
                    title: 'gas-phase fugacity coefficient',
                    range: [0.68, 1.02],
                    gridcolor: 'white',
                    showgrid: true,
                    showminorgrid: true,
                    zerolinecolor: '#666'
                },
                plot_bgcolor: '#f0f0f0',
                paper_bgcolor: 'white',
                font: {
                    family: 'Arial, sans-serif',
                    size: 12
                },
                showlegend: true,
                legend: {
                    x: 0.7,
                    y: 1
                }
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        // Event handlers
        document.getElementById('btn-srk').addEventListener('click', function() {
            currentEoS = 'SRK';
            document.getElementById('btn-srk').classList.add('selected');
            document.getElementById('btn-pr').classList.remove('selected');
            updatePlot();
        });

        document.getElementById('btn-pr').addEventListener('click', function() {
            currentEoS = 'PR';
            document.getElementById('btn-pr').classList.add('selected');
            document.getElementById('btn-srk').classList.remove('selected');
            updatePlot();
        });

        document.getElementById('checkbox-aspen').addEventListener('change', function() {
            showAspen = this.checked;
            updatePlot();
        });

        document.getElementById('slider-pressure').addEventListener('input', function() {
            currentPressure = parseInt(this.value);
            document.getElementById('display-pressure').textContent = currentPressure;
            updatePlot();
        });

        // Initialize plot on page load
        updatePlot();
    </script>
</body>
</html>
