<body>
    <div id="demo-container">
        <div id="mode-selector">
            <div id="btn-mode-da" class="mode-button active">Diels–Alder reaction</div>
            <div id="btn-mode-regio" class="mode-button">regioselectivity</div>
            <div id="btn-mode-stereo-phile" class="mode-button">stereospecificity (dienophile)</div>
            <div id="btn-mode-stereo-diene" class="mode-button">stereospecificity (diene)</div>
            <div id="btn-mode-homo" class="mode-button">HOMO/LUMO interactions</div>
        </div>

        <div id="control-panel">
            <div id="inverse-container" class="control-group hidden">
                <input type="checkbox" id="check-inverse">
                <label for="check-inverse">inverse</label>
            </div>

            <div id="focus-container" class="control-group">
                <input type="checkbox" id="check-focus">
                <label for="check-focus">focus</label>
            </div>

            <div id="reaction-slider-container" class="control-group">
                <label for="slider-reaction">reaction evolution</label>
                <button id="btn-reaction-minus">-</button>
                <input type="range" id="slider-reaction" min="0" max="100" value="0" step="1">
                <button id="btn-reaction-plus">+</button>
            </div>

            <div id="erg-ewg-slider-container" class="control-group hidden">
                <label for="slider-erg-ewg">ERG/EWG effect intensity</label>
                <button id="btn-erg-ewg-minus">-</button>
                <input type="range" id="slider-erg-ewg" min="0" max="100" value="0" step="1">
                <button id="btn-erg-ewg-plus">+</button>
            </div>
        </div>

        <div id="canvas-container">
            <canvas id="viz-canvas"></canvas>
        </div>
    </div>

    <script>
        // Global state management
        const state = {
            activeMode: 'da',
            checkInverse: false,
            checkFocus: false,
            sliderReaction: 0,
            sliderErgEwg: 0
        };

        // Mode to control visibility mapping
        const modeControls = {
            da: ['focus-container', 'reaction-slider-container'],
            regio: ['inverse-container', 'focus-container', 'reaction-slider-container', 'erg-ewg-slider-container'],
            'stereo-phile': ['focus-container'],
            'stereo-diene': ['focus-container'],
            homo: ['inverse-container', 'erg-ewg-slider-container']
        };

        // Initialize event listeners
        document.addEventListener('DOMContentLoaded', function() {
            // Mode selector buttons
            document.querySelectorAll('.mode-button').forEach(button => {
                button.addEventListener('click', function() {
                    const mode = this.id.replace('btn-mode-', '');
                    setActiveMode(mode);
                });
            });

            // Checkboxes
            document.getElementById('check-inverse').addEventListener('change', function() {
                state.checkInverse = this.checked;
            });

            document.getElementById('check-focus').addEventListener('change', function() {
                state.checkFocus = this.checked;
            });

            // Sliders
            document.getElementById('slider-reaction').addEventListener('input', function() {
                state.sliderReaction = parseInt(this.value);
            });

            document.getElementById('slider-erg-ewg').addEventListener('input', function() {
                state.sliderErgEwg = parseInt(this.value);
            });

            // Slider buttons
            document.getElementById('btn-reaction-minus').addEventListener('click', function() {
                const slider = document.getElementById('slider-reaction');
                slider.value = Math.max(0, parseInt(slider.value) - 1);
                state.sliderReaction = parseInt(slider.value);
            });

            document.getElementById('btn-reaction-plus').addEventListener('click', function() {
                const slider = document.getElementById('slider-reaction');
                slider.value = Math.min(100, parseInt(slider.value) + 1);
                state.sliderReaction = parseInt(slider.value);
            });

            document.getElementById('btn-erg-ewg-minus').addEventListener('click', function() {
                const slider = document.getElementById('slider-erg-ewg');
                slider.value = Math.max(0, parseInt(slider.value) - 1);
                state.sliderErgEwg = parseInt(slider.value);
            });

            document.getElementById('btn-erg-ewg-plus').addEventListener('click', function() {
                const slider = document.getElementById('slider-erg-ewg');
                slider.value = Math.min(100, parseInt(slider.value) + 1);
                state.sliderErgEwg = parseInt(slider.value);
            });

            // Initialize with default mode
            setActiveMode('da');
        });

        function setActiveMode(mode) {
            state.activeMode = mode;

            // Update active button
            document.querySelectorAll('.mode-button').forEach(btn => {
                btn.classList.remove('active');
            });
            document.getElementById(`btn-mode-${mode}`).classList.add('active');

            // Update control visibility
            document.querySelectorAll('.control-group').forEach(group => {
                group.classList.add('hidden');
            });

            modeControls[mode].forEach(controlId => {
                document.getElementById(controlId).classList.remove('hidden');
            });
        }

        // p5.js sketch
        let canvas;

        function setup() {
            canvas = createCanvas(windowWidth, windowHeight);
            canvas.parent('viz-canvas');
        }

        function draw() {
            background(255);

            switch(state.activeMode) {
                case 'da':
                    drawDielsAlderReaction();
                    break;
                case 'regio':
                    drawRegioselectivity();
                    break;
                case 'stereo-phile':
                    drawStereospecificityPhile();
                    break;
                case 'stereo-diene':
                    drawStereospecificityDiene();
                    break;
                case 'homo':
                    drawHomoLumoInteractions();
                    break;
            }
        }

        function windowResized() {
            resizeCanvas(windowWidth, windowHeight);
        }

        function drawDielsAlderReaction() {
            const progress = state.sliderReaction / 100;
            const centerX = width / 2;
            const centerY = height / 2;

            if (progress < 0.5) {
                // Reactants approaching
                const approachProgress = progress * 2;
                const dieneX = centerX - 150 + (150 * approachProgress);
                const dieneY = centerY;
                const ethyleneX = centerX + 150 - (150 * approachProgress);
                const ethyleneY = centerY;

                drawButadiene(dieneX, dieneY, state.checkFocus);
                drawEthylene(ethyleneX, ethyleneY, state.checkFocus);
            } else {
                // Reaction occurring
                const reactionProgress = (progress - 0.5) * 2;
                const productX = centerX;
                const productY = centerY;

                drawCyclohexene(productX, productY, reactionProgress, state.checkFocus);
            }
        }

        function drawButadiene(x, y, focus) {
            push();
            translate(x, y);

            // Draw bonds
            strokeWeight(2);
            stroke(0);

            // Single bonds
            line(-40, 0, -20, 0);
            line(0, 0, 20, 0);
            line(40, 0, 60, 0);

            // Double bonds
            if (focus) {
                stroke(255, 0, 0, 150);
                strokeWeight(6);
                line(-40, 0, -20, 0);
                line(40, 0, 60, 0);
                stroke(0);
                strokeWeight(2);
            }

            line(-40, -3, -20, -3);
            line(-40, 3, -20, 3);
            line(40, -3, 60, -3);
            line(40, 3, 60, 3);

            pop();
        }

        function drawEthylene(x, y, focus) {
            push();
            translate(x, y);

            strokeWeight(2);
            stroke(0);

            if (focus) {
                stroke(255, 0, 0, 150);
                strokeWeight(6);
                line(-20, 0, 20, 0);
                stroke(0);
                strokeWeight(2);
            }

            // Double bond
            line(-20, -3, 20, -3);
            line(-20, 3, 20, 3);

            pop();
        }

        function drawCyclohexene(x, y, progress, focus) {
            push();
            translate(x, y);

            strokeWeight(2);
            stroke(0);

            // Draw hexagon with one double bond
            const radius = 60;
            const angles = [];
            for (let i = 0; i < 6; i++) {
                angles.push(TWO_PI / 6 * i - PI / 2);
            }

            const points = [];
            for (let i = 0; i < 6; i++) {
                points.push({
                    x: cos(angles[i]) * radius,
                    y: sin(angles[i]) * radius
                });
            }

            // Draw bonds
            for (let i = 0; i < 6; i++) {
                const next = (i + 1) % 6;

                if (focus && (i === 0 || i === 1 || i === 5)) {
                    stroke(255, 0, 0, 150);
                    strokeWeight(6);
                    line(points[i].x, points[i].y, points[next].x, points[next].y);
                    stroke(0);
                    strokeWeight(2);
                }

                line(points[i].x, points[i].y, points[next].x, points[next].y);

                // Double bond between points 0 and 1
                if (i === 0) {
                    const offset = 5;
                    const dx = points[next].x - points[i].x;
                    const dy = points[next].y - points[i].y;
                    const len = sqrt(dx * dx + dy * dy);
                    const nx = -dy / len * offset;
                    const ny = dx / len * offset;

                    if (focus) {
                        stroke(255, 0, 0, 150);
                        strokeWeight(6);
                        line(points[i].x + nx, points[i].y + ny, points[next].x + nx, points[next].y + ny);
                        stroke(0);
                        strokeWeight(2);
                    }

                    line(points[i].x + nx, points[i].y + ny, points[next].x + nx, points[next].y + ny);
                }
            }

            pop();
        }

        function drawRegioselectivity() {
            const progress = state.sliderReaction / 100;
            const ergEwgEffect = state.sliderErgEwg / 100;

            // Draw two reaction paths
            const path1Y = height / 3;
            const path2Y = 2 * height / 3;

            // Path 1 - Ortho product
            drawRegioReactionPath(width / 2, path1Y, progress, ergEwgEffect * (state.checkInverse ? 0.3 : 1), 'ortho');

            // Path 2 - Meta product
            drawRegioReactionPath(width / 2, path2Y, progress, ergEwgEffect * (state.checkInverse ? 1 : 0.3), 'meta');
        }

        function drawRegioReactionPath(x, y, progress, intensity, productType) {
            push();
            translate(x, y);

            const alpha = 100 + intensity * 155;
            stroke(0, alpha);
            strokeWeight(2 * (0.5 + intensity * 0.5));

            // Draw simplified reaction
            if (progress < 0.5) {
                // Reactants
                text('1-substituted diene', -100, -20);
                text('substituted dienophile', -100, 20);
            } else {
                // Products
                text(`${productType} product`, -50, 0);
            }

            pop();
        }

        function drawStereospecificityPhile() {
            // Top half - cis dienophile
            drawStereoPath(width / 2, height / 3, 'cis', 'meso', state.checkFocus);

            // Bottom half - trans dienophile
            drawStereoPath(width / 2, 2 * height / 3, 'trans', 'racemic', state.checkFocus);
        }

        function drawStereospecificityDiene() {
            // Top half - trans,trans diene
            drawStereoPath(width / 2, height / 3, 'trans,trans', 'meso', state.checkFocus);

            // Bottom half - trans,cis diene
            drawStereoPath(width / 2, 2 * height / 3, 'trans,cis', 'racemic', state.checkFocus);
        }

        function drawStereoPath(x, y, reactantType, productType, focus) {
            push();
            translate(x, y);

            textAlign(CENTER);
            textSize(14);
            fill(0);

            text(`${reactantType} reactants → ${productType} product`, 0, -40);

            // Draw simplified representation
            strokeWeight(2);
            stroke(0);

            if (focus) {
                stroke(255, 0, 0, 150);
                strokeWeight(6);
                line(-30, 0, 30, 0);
                stroke(0);
                strokeWeight(2);
            }

            line(-30, 0, 30, 0);

            if (productType === 'meso') {
                // Draw symmetry line
                stroke(255, 0, 0, 100);
                strokeWeight(1);
                setLineDash([5, 5]);
                line(0, -20, 0, 20);
                setLineDash([]);
            }

            pop();
        }

        function drawHomoLumoInteractions() {
            const effect = state.sliderErgEwg / 100;
            const inverse = state.checkInverse;

            const centerX = width / 2;
            const centerY = height / 2;

            // Draw energy levels
            const dieneHomoY = centerY - 100 + (inverse ? effect * 50 : -effect * 50);
            const dieneLumoY = centerY + (inverse ? effect * 50 : -effect * 50);
            const phileHomoY = centerY + 100 + (inverse ? -effect * 50 : effect * 50);
            const phileLumoY = centerY + 200 + (inverse ? -effect * 50 : effect * 50);

            // Draw energy level lines
            strokeWeight(2);
            stroke(0);

            // Diene orbitals
            line(centerX - 150, dieneHomoY, centerX - 50, dieneHomoY);
            line(centerX - 150, dieneLumoY, centerX - 50, dieneLumoY);

            // Dienophile orbitals
            line(centerX + 50, phileHomoY, centerX + 150, phileHomoY);
            line(centerX + 50, phileLumoY, centerX + 150, phileLumoY);

            // Draw orbital representations
            drawOrbital(centerX - 100, dieneHomoY, true);
            drawOrbital(centerX - 100, dieneLumoY, false);
            drawOrbital(centerX + 100, phileHomoY, true);
            drawOrbital(centerX + 100, phileLumoY, false);

            // Draw interaction arrows
            const gap1 = abs(dieneHomoY - phileLumoY);
            const gap2 = abs(phileHomoY - dieneLumoY);

            if (gap1 < gap2) {
                // Diene HOMO -> Dienophile LUMO is dominant
                drawInteractionArrow(centerX - 50, dieneHomoY, centerX + 50, phileLumoY, true);
                drawInteractionArrow(centerX + 50, phileHomoY, centerX - 50, dieneLumoY, false);
            } else {
                // Dienophile HOMO -> Diene LUMO is dominant
                drawInteractionArrow(centerX + 50, phileHomoY, centerX - 50, dieneLumoY, true);
                drawInteractionArrow(centerX - 50, dieneHomoY, centerX + 50, phileLumoY, false);
            }

            // Labels
            textAlign(CENTER);
            textSize(12);
            fill(0);

            text(inverse ? 'diene EWG' : 'diene ERG', centerX - 100, dieneHomoY - 20);
            text('HOMO', centerX - 100, dieneHomoY + 20);
            text('LUMO', centerX - 100, dieneLumoY + 20);

            text(inverse ? 'dienophile ERG' : 'dienophile EWG', centerX + 100, phileHomoY - 20);
            text('HOMO', centerX + 100, phileHomoY + 20);
            text('LUMO', centerX + 100, phileLumoY + 20);
        }

        function drawOrbital(x, y, occupied) {
            push();
            translate(x, y);

            strokeWeight(1);
            stroke(0);

            // Draw orbital lines
            for (let i = -2; i <= 2; i++) {
                if (i === 0) continue;
                line(-10, i * 3, 10, i * 3);
            }

            // Draw electrons
            if (occupied) {
                fill(0);
                noStroke();
                for (let i = -1; i <= 1; i += 2) {
                    ellipse(-5, i * 3, 4, 4);
                    ellipse(5, i * 3, 4, 4);
                }
            }

            pop();
        }

        function drawInteractionArrow(x1, y1, x2, y2, dominant) {
            push();

            if (dominant) {
                stroke(255, 0, 0);
                strokeWeight(3);
            } else {
                stroke(0, 100);
                strokeWeight(1);
            }

            setLineDash([5, 5]);

            const dx = x2 - x1;
            const dy = y2 - y1;
            const len = sqrt(dx * dx + dy * dy);
            const nx = dx / len;
            const ny = dy / len;

            // Adjust length based on dominance
            const adjustedLen = dominant ? len * 0.7 : len * 1.3;
            const endX = x1 + nx * adjustedLen;
            const endY = y1 + ny * adjustedLen;

            line(x1, y1, endX, endY);

            // Arrowhead
            const arrowSize = dominant ? 10 : 6;
            const angle = atan2(dy, dx);

            push();
            translate(endX, endY);
            rotate(angle);
            line(0, 0, -arrowSize, -arrowSize/2);
            line(0, 0, -arrowSize, arrowSize/2);
            pop();

            setLineDash([]);
            pop();
        }

        function setLineDash(list) {
            drawingContext.setLineDash(list);
        }
    </script>
</body>
</html>
