<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>A Visual Proof of Viviani's Theorem</title>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <style>
        body {
            margin: 0;
            padding: 20px;
            font-family: Arial, sans-serif;
            background-color: #f5f5f5;
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
        }

        #demo-container {
            background-color: white;
            border-radius: 8px;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
            padding: 20px;
            max-width: 640px;
        }

        #control-panel {
            margin-bottom: 20px;
            padding: 15px;
            background-color: #f8f9fa;
            border-radius: 6px;
            display: flex;
            align-items: center;
            gap: 15px;
        }

        #control-panel label {
            font-weight: 500;
            color: #333;
        }

        #control-panel input[type="radio"] {
            margin-right: 5px;
        }

        #canvas-container {
            display: flex;
            justify-content: center;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <label>step</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>

    <script>
        let canvas;
        let currentStep = '1';
        let px, py;
        let isDragging = false;
        let triA, triB, triC, triMid;
        let triHeight;

        // Triangle vertices
        let A, B, C;

        function setup() {
            canvas = createCanvas(600, 520);
            canvas.parent('canvas-container');

            // Calculate triangle vertices for equilateral triangle
            const sideLength = 400;
            const centerX = width / 2;
            const baseY = height - 80;

            A = { x: centerX - sideLength / 2, y: baseY };
            B = { x: centerX + sideLength / 2, y: baseY };
            C = { x: centerX, y: baseY - sideLength * sqrt(3) / 2 };

            triHeight = sideLength * sqrt(3) / 2;
            triMid = { x: centerX, y: baseY - triHeight / 2 };

            // Initialize P inside the triangle
            px = width * 0.4;
            py = height * 0.6;

            // Set up radio button listeners
            const radioButtons = document.querySelectorAll('input[name="step-selector"]');
            radioButtons.forEach(radio => {
                radio.addEventListener('change', (event) => {
                    currentStep = event.target.value;
                });
            });
        }

        function draw() {
            background(255);

            // Draw main elements
            drawMainTriangle();
            drawMainAltitude();

            // Step-specific drawing
            switch(currentStep) {
                case '1':
                    drawStep1();
                    break;
                case '2':
                    drawStep2();
                    break;
                case '3':
                    drawStep3();
                    break;
            }

            // Draw point P (unless in step 3 where it's shown as ghost)
            if (currentStep !== '3') {
                drawPointP(px, py, color(128), color(0));
            } else {
                drawPointP(px, py, color(200, 200, 200, 100), color(150, 150, 150, 100));
            }
        }

        function drawMainTriangle() {
            stroke(0);
            strokeWeight(2);
            noFill();
            triangle(A.x, A.y, B.x, B.y, C.x, C.y);

            // Label vertices
            textAlign(CENTER, CENTER);
            textSize(16);
            fill(0);
            noStroke();
            text('A', A.x - 20, A.y + 20);
            text('B', B.x + 20, B.y + 20);
            text('C', C.x, C.y - 20);
        }

        function drawMainAltitude() {
            stroke(100, 149, 237);
            strokeWeight(1.5);
            drawingContext.setLineDash([5, 5]);
            line(C.x, C.y, triMid.x, triMid.y);
            drawingContext.setLineDash([]);
        }

        function drawPointP(x, y, fillColor, strokeColor) {
            push();
            fill(fillColor);
            stroke(strokeColor);
            strokeWeight(2);
            circle(x, y, 20);

            //Draw cross
            line(x - 8, y, x + 8, y);
            line(x, y - 8, x, y + 8);

            //Label P
            fill(0);
            noStroke();
            textAlign(LEFT, CENTER);
            textSize(16);
            text('P', x + 12, y);
            pop();
        }

        function drawStep1() {
            // Calculate perpendicular distances and feet
            const distAB = getDistanceAndFoot(A, B, px, py);
            const distBC = getDistanceAndFoot(B, C, px, py);
            const distCA = getDistanceAndFoot(C, A, px, py);

            stroke(0);
            strokeWeight(2);

            // Draw perpendiculars
            line(px, py, distAB.foot.x, distAB.foot.y);
            line(px, py, distBC.foot.x, distBC.foot.y);
            line(px, py, distCA.foot.x, distCA.foot.y);

            // Draw feet
            fill(0, 0, 255);
            noStroke();
            circle(distAB.foot.x, distAB.foot.y, 8);
            circle(distBC.foot.x, distBC.foot.y, 8);
            circle(distCA.foot.x, distCA.foot.y, 8);
        }

        function drawStep2() {
            const distAB = getDistanceAndFoot(A, B, px, py).distance;
            const distBC = getDistanceAndFoot(B, C, px, py).distance;
            const distCA = getDistanceAndFoot(C, A, px, py).distance;

            // Calculate side lengths for small triangles
            const sideAB = distAB * 2 / sqrt(3);
            const sideBC = distBC * 2 / sqrt(3);
            const sideCA = distCA * 2 / sqrt(3);

            // Draw red triangle (altitude = distAB, horizontal base)
            push();
            fill(255, 0, 0, 100);
            stroke(255, 0, 0);
            drawSmallEquilateral(px, py - distAB/2, sideAB, 0);
            pop();

            // Draw green triangle (altitude = distCA, parallel to AC)
            push();
            fill(0, 255, 0, 100);
            stroke(0, 255, 0);
            rotateAroundPoint(px, py, -PI/3);
            drawSmallEquilateral(px, py - distCA/2, sideCA, 0);
            // Label 'x'
            fill(0, 255, 0);
            noStroke();
            textAlign(CENTER, CENTER);
            text('x', px, py + distCA/2 + 15);
            pop();

            // Draw blue triangle (altitude = distBC, parallel to BC)
            push();
            fill(0, 0, 255, 100);
            stroke(0, 0, 255);
            rotateAroundPoint(px, py, PI/3);
            drawSmallEquilateral(px, py - distBC/2, sideBC, 0);
            // Label 'y'
            fill(0, 0, 255);
            noStroke();
            textAlign(CENTER, CENTER);
            text('y', px, py + distBC/2 + 15);
            pop();

            // Draw perpendiculars inside the triangles
            stroke(0, 150);
            strokeWeight(1);
            line(px, py - distAB/2 + distAB/4, px, py - distAB/2 - distAB/4);
        }

        function drawStep3() {
            const distAB = getDistanceAndFoot(A, B, px, py).distance;
            const distBC = getDistanceAndFoot(B, C, px, py).distance;
            const distCA = getDistanceAndFoot(C, A, px, py).distance;

            const sideAB = distAB * 2 / sqrt(3);
            const sideBC = distBC * 2 / sqrt(3);
            const sideCA = distCA * 2 / sqrt(3);

            const centerX = width / 2;
            let currentY = A.y;

            // Draw red triangle at bottom
            push();
            fill(255, 0, 0, 100);
            stroke(255, 0, 0);
            drawSmallEquilateral(centerX, currentY - distAB/2, sideAB, 0);
            pop();

            // Draw blue triangle on top of red
            currentY = currentY - distAB;
            push();
            fill(0, 0, 255, 100);
            stroke(0, 0, 255);
            drawSmallEquilateral(centerX, currentY - distBC/2, sideBC, 0);
            // Label 'y'
            fill(0, 0, 255);
            noStroke();
            textAlign(CENTER, CENTER);
            text('y', centerX, currentY);
            pop();

            // Draw green triangle on top of blue
            currentY = currentY - distBC;
            push();
            fill(0, 255, 0, 100);
            stroke(0, 255, 0);
            drawSmallEquilateral(centerX, currentY - distCA/2, sideCA, 0);
            pop();

            // The top should align with C
        }

        function drawSmallEquilateral(cx, cy, side, rotation) {
            push();
            translate(cx, cy);
            rotate(rotation);
            const h = side * sqrt(3) / 2;
            triangle(-side/2, h/2, side/2, h/2, 0, -h/2);
            pop();
        }

        function rotateAroundPoint(x, y, angle) {
            push();
            translate(x, y);
            rotate(angle);
            translate(-x, -y);
        }

        function getDistanceAndFoot(p1, p2, px, py) {
            // Vector from p1 to p2
            let dx = p2.x - p1.x;
            let dy = p2.y - p1.y;

            // Vector from p1 to P
            let dxp = px - p1.x;
            let dyp = py - p1.y;

            // Project P onto line p1p2
            let t = (dxp * dx + dyp * dy) / (dx * dx + dy * dy);
            t = constrain(t, 0, 1);

            let footX = p1.x + t * dx;
            let footY = p1.y + t * dy;

            let distance = dist(px, py, footX, footY);

            return { distance: distance, foot: { x: footX, y: footY } };
        }

        function isPointInTriangle(px, py) {
            // Barycentric coordinates method
            let v0x = C.x - A.x;
            let v0y = C.y - A.y;
            let v1x = B.x - A.x;
            let v1y = B.y - A.y;
            let v2x = px - A.x;
            let v2y = py - A.y;

            let dot00 = v0x * v0x + v0y * v0y;
            let dot01 = v0x * v1x + v0y * v1y;
            let dot02 = v0x * v2x + v0y * v2y;
            let dot11 = v1x * v1x + v1y * v1y;
            let dot12 = v1x * v2x + v1y * v2y;

            let invDenom = 1 / (dot00 * dot11 - dot01 * dot01);
            let u = (dot11 * dot02 - dot01 * dot12) * invDenom;
            let v = (dot00 * dot12 - dot01 * dot02) * invDenom;

            return (u >= 0) && (v >= 0) && (u + v <= 1);
        }

        function mousePressed() {
            let d = dist(mouseX, mouseY, px, py);
            if (d < 15) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging && isPointInTriangle(mouseX, mouseY)) {
                px = mouseX;
                py = mouseY;
            }
        }

        function mouseReleased() {
            isDragging = false;
        }
    </script>
</body>
</html>
