    <script>
        // Orbital elements data
        const orbitalElements = {
            Mercury: { a: 0.3871, e: 0.2056, i: 7.005, omega: 48.331, w: 29.124, M: 174.796 },
            Venus: { a: 0.7233, e: 0.0068, i: 3.395, omega: 76.680, w: 54.884, M: 50.416 },
            Earth: { a: 1.0000, e: 0.0167, i: 0.000, omega: -11.260, w: 114.208, M: 358.617 },
            Mars: { a: 1.5237, e: 0.0934, i: 1.850, omega: 49.562, w: 286.502, M: 19.412 },
            Ceres: { a: 2.7691, e: 0.0760, i: 10.593, omega: 80.393, w: 73.598, M: 77.372 },
            Pluto: { a: 39.482, e: 0.2488, i: 17.160, omega: 110.299, w: 113.834, M: 14.882 },
            Haumea: { a: 43.132, e: 0.1912, i: 28.220, omega: 122.155, w: 239.544, M: 204.03 },
            Makemake: { a: 45.791, e: 0.1559, i: 29.006, omega: 79.623, w: 294.821, M: 95.895 },
            Eris: { a: 67.668, e: 0.4418, i: 44.187, omega: 35.951, w: 151.291, M: 206.51 }
        };

        // Scene variables
        let scene, camera, renderer, controls;
        let sun, planets = {}, dwarfPlanet = null;
        let planetOrbits = {}, dwarfPlanetOrbit = null;
        let selectedDwarfPlanet = 'Pluto';
        let isAnimating = false;
        let animationId = null;

        // Initialize Three.js scene
        function initScene() {
            const canvas = document.getElementById('3d-canvas');
            const container = document.getElementById('visualization-container');

            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf8f8f8);

            camera = new THREE.PerspectiveCamera(
                60,
                container.offsetWidth / container.offsetHeight,
                0.1,
                1000
            );
            camera.position.set(0, 15, 30);

            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(container.offsetWidth, container.offsetHeight);
            renderer.shadowMap.enabled = true;
            renderer.shadowMap.type = THREE.PCFSoftShadowMap;

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const sunLight = new THREE.PointLight(0xffffff, 1.5);
            sunLight.position.set(0, 0, 0);
            sunLight.castShadow = true;
            scene.add(sunLight);

            // Create Sun
            const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            sun = new THREE.Mesh(sunGeometry, sunMaterial);
            scene.add(sun);

            // Add AXES helper for reference
            const axesHelper = new THREE.AxesHelper(5);
            scene.add(axesHelper);

            // Create inner planets and their orbits
            const planetColors = {
                Mercury: 0x808080,
                Venus: 0xdaa520,
                Earth: 0x0000ff,
                Mars: 0xff0000
            };

            ['Mercury', 'Venus', 'Earth', 'Mars'].forEach(planetName => {
                // Create planet
                const planetGeometry = new THREE.SphereGeometry(0.1, 16, 16);
                const planetMaterial = new THREE.MeshLambertMaterial({ color: planetColors[planetName] });
                const planet = new THREE.Mesh(planetGeometry, planetMaterial);
                planet.castShadow = true;
                planet.receiveShadow = true;
                scene.add(planet);
                planets[planetName] = planet;

                // Create orbit
                const orbit = createOrbit(orbitalElements[planetName], 0x0080ff);
                planetOrbits[planetName] = orbit;
                scene.add(orbit);
            });

            // Create initial dwarf planet
            createDwarfPlanet(selectedDwarfPlanet);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        // Create orbit line
        function createOrbit(elements, color) {
            const points = [];
            for (let i = 0; i <= 360; i += 2) {
                const angle = i * Math.PI / 180;
                const pos = calculatePositionFromElements(angle, elements);
                points.push(pos);
            }

            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: color, linewidth: 2 });
            return new THREE.Line(geometry, material);
        }

        // Calculate position from orbital elements for a given angle
        function calculatePositionFromElements(angle, elements) {
            const { a, e, i, omega, w } = elements;

            // Position in orbital plane
            const r = a * (1 - e * e) / (1 + e * Math.cos(angle));
            const x_orb = r * Math.cos(angle);
            const y_orb = r * Math.sin(angle);

            // Convert to ecliptic coordinates
            const i_rad = i * Math.PI / 180;
            const omega_rad = omega * Math.PI / 180;
            const w_rad = w * Math.PI / 180;

            const x = (Math.cos(omega_rad) * Math.cos(w_rad) - Math.sin(omega_rad) * Math.sin(w_rad) * Math.cos(i_rad)) * x_orb +
                     (-Math.cos(omega_rad) * Math.sin(w_rad) - Math.sin(omega_rad) * Math.cos(w_rad) * Math.cos(i_rad)) * y_orb;
            const y = (Math.sin(omega_rad) * Math.cos(w_rad) + Math.cos(omega_rad) * Math.sin(w_rad) * Math.cos(i_rad)) * x_orb +
                     (-Math.sin(omega_rad) * Math.sin(w_rad) + Math.cos(omega_rad) * Math.cos(w_rad) * Math.cos(i_rad)) * y_orb;
            const z = (Math.sin(w_rad) * Math.sin(i_rad)) * x_orb + (Math.cos(w_rad) * Math.sin(i_rad)) * y_orb;

            return new THREE.Vector3(x, z, -y);
        }

        // Solve Kepler's equation
        function solveKepler(E0, M, e, tolerance = 1e-6) {
            let E = E0;
            let delta = 1;
            while (Math.abs(delta) > tolerance) {
                delta = (E - e * Math.sin(E) - M) / (1 - e * Math.cos(E));
                E -= delta;
            }
            return E;
        }

        // Calculate position for a given date
        function calculatePosition(date, elements) {
            const J2000 = new Date('2000-01-01T12:00:00Z');
            const d = (date - J2000) / (1000 * 60 * 60 * 24); // Days since J2000

            const a = elements.a;
            const e = elements.e;
            const M0 = elements.M * Math.PI / 180;
            const P = 365.25 * Math.pow(a, 1.5); // Orbital period in days

            const M = (M0 + 2 * Math.PI * d / P) % (2 * Math.PI);
            const E = solveKepler(M, M, e);

            const nu = 2 * Math.atan2(Math.sqrt(1 + e) * Math.sin(E / 2), Math.sqrt(1 - e) * Math.cos(E / 2));

            return calculatePositionFromElements(nu, elements);
        }

        // Create dwarf planet and its orbit
        function createDwarfPlanet(planetName) {
            // Remove existing dwarf planet
            if (dwarfPlanet) {
                scene.remove(dwarfPlanet);
            }
            if (dwarfPlanetOrbit) {
                scene.remove(dwarfPlanetOrbit);
            }

            // Create new dwarf planet
            const planetGeometry = new THREE.SphereGeometry(0.2, 16, 16);
            const planetMaterial = new THREE.MeshLambertMaterial({ color: 0x800080 });
            dwarfPlanet = new THREE.Mesh(planetGeometry, planetMaterial);
            dwarfPlanet.castShadow = true;
            dwarfPlanet.receiveShadow = true;
            scene.add(dwarfPlanet);

            // Create orbit
            dwarfPlanetOrbit = createOrbit(orbitalElements[planetName], 0xff0000);
            scene.add(dwarfPlanetOrbit);

            // Update position
            updatePositions();
        }

        // Update all celestial body positions
        function updatePositions() {
            const sliderValue = parseInt(document.getElementById('time-slider').value);
            const date = new Date('2000-01-01T00:00:00Z');
            date.setDate(date.getDate() + sliderValue);

            // Update planet positions
            ['Mercury', 'Venus', 'Earth', 'Mars'].forEach(planetName => {
                const pos = calculatePosition(date, orbitalElements[planetName]);
                planets[planetName].position.copy(pos);
            });

            // Update dwarf planet position
            if (dwarfPlanet) {
                const pos = calculatePosition(date, orbitalElements[selectedDwarfPlanet]);
                dwarfPlanet.position.copy(pos);
            }

            // Update date display
            updateDateDisplay(date);

            renderer.render(scene, camera);
        }

        // Update date display
        function updateDateDisplay(date) {
            const options = { weekday: 'short', day: 'numeric', month: 'short', year: 'numeric' };
            const dateStr = date.toLocaleDateString('en-GB', options) + ' 00:00:00';
            document.getElementById('date-display').textContent = dateStr;
        }

        // Handle window resize
        function onWindowResize() {
            const container = document.getElementById('visualization-container');
            camera.aspect = container.offsetWidth / container.offsetHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.offsetWidth, container.offsetHeight);
        }

        // Animation loop
        function animate() {
            animationId = requestAnimationFrame(animate);
            controls.update();

            if (isAnimating) {
                const slider = document.getElementById('time-slider');
                let value = parseInt(slider.value) + 2;
                if (value > parseInt(slider.max)) {
                    value = parseInt(slider.min);
                }
                slider.value = value;
                updatePositions();
            }

            renderer.render(scene, camera);
        }

        // Initialize event listeners
        function initEventListeners() {
            // Dwarf planet selector
            const buttons = document.querySelectorAll('#dwarf-planet-selector button');
            buttons.forEach(button => {
                button.addEventListener('click', () => {
                    buttons.forEach(b => b.classList.remove('active'));
                    button.classList.add('active');
                    selectedDwarfPlanet = button.dataset.planet;
                    createDwarfPlanet(selectedDwarfPlanet);
                });
            });

            // Time slider
            document.getElementById('time-slider').addEventListener('input', updatePositions);

            // Play/Pause button
            document.getElementById('play-pause-button').addEventListener('click', () => {
                isAnimating = !isAnimating;
                const button = document.getElementById('play-pause-button');
                button.textContent = isAnimating ? '⏸' : '▶';
            });
        }

        // Initialize everything
        initScene();
        initEventListeners();
        updatePositions();
        animate();
    </script>
</body>
</html>
