    <script>
        // CFC Data
        const cfcData = {
            'CFC-12': { color: 'purple', data: [[1940, 5], [1950, 20], [1960, 50], [1970, 150], [1980, 320], [1990, 480], [2000, 540], [2010, 520], [2020, 500]] },
            'CFC-11': { color: 'orange', data: [[1940, 2], [1950, 10], [1960, 30], [1970, 90], [1980, 180], [1990, 260], [2000, 250], [2010, 230], [2020, 220]] },
            'CFC-113': { color: 'black', data: [[1940, 0], [1950, 1], [1960, 5], [1970, 15], [1980, 40], [1990, 80], [2000, 85], [2010, 75], [2020, 70]] },
            'CCl4': { color: 'green', data: [[1940, 50], [1950, 60], [1960, 75], [1970, 90], [1980, 100], [1990, 105], [2000, 95], [2010, 85], [2020, 80]] }
        };

        // Initialize variables
        let scene, camera, renderer, earth, ozoneLayer, atmosphere, sun;
        let currentCFC = 'CFC-12';
        let showAll = false;
        let currentYear = 1950;
        let currentTime = 1;
        const ozoneInitial = 10; // Initial ozone concentration

        // Function to interpolate CFC concentration at a given year
        function getCFCConcentration(cfcType, year) {
            const data = cfcData[cfcType].data;
            for (let i = 0; i < data.length - 1; i++) {
                if (year >= data[i][0] && year <= data[i + 1][0]) {
                    const ratio = (year - data[i][0]) / (data[i + 1][0] - data[i][0]);
                    return data[i][1] + ratio * (data[i + 1][1] - data[i][1]);
                }
            }
            return data[data.length - 1][1];
        }

        // Function to update all visualizations
        function update() {
            // Update displays
            document.getElementById('label-year').textContent = currentYear;
            document.getElementById('label-time').textContent = currentTime;

            // Update CFC plot
            updateCFCPlot();

            // Update ozone plot
            updateOzonePlot();

            // Update 3D scene
            update3DScene();
        }

        // Function to update CFC concentration plot
        function updateCFCPlot() {
            const traces = [];

            if (showAll) {
                // Show all CFCs
                Object.keys(cfcData).forEach(cfc => {
                    const cfcInfo = cfcData[cfc];
                    const x = cfcInfo.data.map(d => d[0]);
                    const y = cfcInfo.data.map(d => d[1]);

                    traces.push({
                        x: x,
                        y: y,
                        type: 'scatter',
                        mode: 'lines',
                        name: cfc,
                        line: {
                            color: cfcInfo.color,
                            width: cfc === currentCFC ? 3 : 2
                        },
                        showlegend: true
                    });
                });
            } else {
                // Show only selected CFC
                const cfcInfo = cfcData[currentCFC];
                const x = cfcInfo.data.map(d => d[0]);
                const y = cfcInfo.data.map(d => d[1]);

                traces.push({
                    x: x,
                    y: y,
                    type: 'scatter',
                    mode: 'lines',
                    name: currentCFC,
                    line: {
                        color: cfcInfo.color,
                        width: 3
                    },
                    showlegend: true
                });
            }

            // Add marker for current year
            const concentration = getCFCConcentration(currentCFC, currentYear);
            traces.push({
                x: [currentYear],
                y: [concentration],
                type: 'scatter',
                mode: 'markers',
                name: 'Current Year',
                marker: {
                    color: 'red',
                    size: 10
                },
                showlegend: false
            });

            const layout = {
                title: 'CFC concentration',
                xaxis: {
                    title: 'year',
                    range: [1920, 2020]
                },
                yaxis: {
                    range: [0, 600]
                },
                margin: { l: 40, r: 20, t: 40, b: 40 }
            };

            Plotly.newPlot('plot-cfc', traces, layout, {responsive: true});
        }

        // Function to update ozone concentration plot
        function updateOzonePlot() {
            const concentration = getCFCConcentration(currentCFC, currentYear);
            const k = 0.001; // Rate constant (arbitrary units)
            const slope = k * concentration;

            const x = [];
            const y = [];

            for (let t = 0; t <= currentTime; t += 0.5) {
                x.push(t);
                y.push(1/ozoneInitial + slope * t);
            }

            const trace = {
                x: x,
                y: y,
                type: 'scatter',
                mode: 'lines',
                name: '1/[O₃]',
                line: {
                    color: 'blue',
                    width: 3
                }
            };

            const layout = {
                title: 'concentration of ozone',
                xaxis: {
                    title: 'time (s)',
                    range: [0, 30]
                },
                yaxis: {
                    title: '1/[O₃]',
                    range: [0, 110]
                },
                margin: { l: 60, r: 20, t: 40, b: 40 }
            };

            Plotly.newPlot('plot-ozone', [trace], layout, {responsive: true});
        }

        // Function to initialize 3D scene
        function init3DScene() {
            const container = document.getElementById('scene-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000011);

            // Camera
            camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
            camera.position.set(3, 2, 4);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(width, height);
            container.appendChild(renderer.domElement);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(1, 32, 32);
            const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
            earth = new THREE.Mesh(earthGeometry, earthMaterial);
            scene.add(earth);

            // Ozone layer
            const ozoneGeometry = new THREE.SphereGeometry(1.1, 32, 32);
            const ozoneMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x00ff00,
                transparent: true,
                opacity: 0.5
            });
            ozoneLayer = new THREE.Mesh(ozoneGeometry, ozoneMaterial);
            scene.add(ozoneLayer);

            // Atmosphere
            const atmosphereGeometry = new THREE.SphereGeometry(1.2, 32, 32);
            const atmosphereMaterial = new THREE.MeshBasicMaterial({ 
                color: 0xffaa88,
                transparent: true,
                opacity: 0.1
            });
            atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);
            scene.add(atmosphere);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(0.3, 16, 16);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            sun = new THREE.Mesh(sunGeometry, sunMaterial);
            sun.position.set(-4, 1, -1);
            scene.add(sun);

            // Radiation arrows
            for (let i = 0; i < 5; i++) {
                const origin = sun.position.clone();
                const target = new THREE.Vector3(
                    (Math.random() - 0.5) * 2,
                    (Math.random() - 0.5) * 2,
                    (Math.random() - 0.5) * 2
                );
                target.normalize().multiplyScalar(1.2);
                const direction = target.clone().sub(origin).normalize();

                const arrow = new THREE.ArrowHelper(direction, origin, 3, 0xff8800, 0.5, 0.2);
                scene.add(arrow);
            }

            // Add ambient light
            const ambientLight = new THREE.AmbientLight(0x404040, 0.5);
            scene.add(ambientLight);

            animate();
        }

        // Function to update 3D scene
        function update3DScene() {
            const concentration = getCFCConcentration(currentCFC, currentYear);
            const maxConcentration = 600;
            const minOpacity = 0.1;
            const maxOpacity = 0.8;
            const opacity = maxOpacity - (concentration / maxConcentration) * (maxOpacity - minOpacity);
            ozoneLayer.material.opacity = Math.max(minOpacity, Math.min(maxOpacity, opacity));
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);

            // Rotate Earth and ozone layer slowly
            earth.rotation.y += 0.001;
            ozoneLayer.rotation.y += 0.001;

            renderer.render(scene, camera);
        }

        // Event listeners
        document.getElementById('select-cfc').addEventListener('change', (e) => {
            currentCFC = e.target.value;
            update();
        });

        document.getElementById('checkbox-show-all').addEventListener('change', (e) => {
            showAll = e.target.checked;
            update();
        });

        document.getElementById('slider-year').addEventListener('input', (e) => {
            currentYear = parseInt(e.target.value);
            update();
        });

        document.getElementById('slider-time').addEventListener('input', (e) => {
            currentTime = parseInt(e.target.value);
            update();
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            const container = document.getElementById('scene-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);

            // Redraw plots
            updateCFCPlot();
            updateOzonePlot();
        });

        // Initialize everything when page loads
        window.addEventListener('load', () => {
            init3DScene();
            update();
        });
    </script>
</body>
</html>
