<body>
    <h1>Eratosthenes's Measure of the Earth's Circumference</h1>
    <p>This demo illustrates Eratosthenes's method for measuring the Earth's circumference using the angle of shadow cast by a post in Alexandria. Adjust the post angle to see how different angles would affect the calculation, and use the zoom checkbox to focus on the geometric relationships.</p>

    <div id="control-panel">
        <div class="control-group">
            <label for="slider-post-angle">post angle</label>
            <input type="range" id="slider-post-angle" min="1" max="60" step="0.1" value="7">
            <span id="display-post-angle">7.0</span>
        </div>
        <div class="control-group">
            <label for="checkbox-zoom">zoom</label>
            <input type="checkbox" id="checkbox-zoom">
        </div>
    </div>

    <div id="visualization-container">
        <canvas id="demo-canvas"></canvas>
    </div>

    <script>
        // Global variables
        let angle = 7;
        let isZoomed = false;

        // DOM elements
        const slider = document.getElementById('slider-post-angle');
        const display = document.getElementById('display-post-angle');
        const checkbox = document.getElementById('checkbox-zoom');

        // Event listeners
        slider.addEventListener('input', (e) => {
            angle = parseFloat(e.target.value);
            display.textContent = angle.toFixed(1);
        });

        checkbox.addEventListener('change', (e) => {
            isZoomed = e.target.checked;
        });

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(600, 450);
            canvas.parent('demo-canvas');
            angleMode(DEGREES);
            textAlign(CENTER, CENTER);
        }

        function draw() {
            background('#F0F0F0');

            // Draw sun rays
            drawSunRays();

            if (isZoomed) {
                drawZoomedView();
            } else {
                drawFullView();
            }
        }

        function drawSunRays() {
            stroke('#DAA520');
            strokeWeight(2);
            for (let y = 50; y < height - 50; y += 30) {
                // Draw ray line
                line(width - 50, y, width - 150, y);
                // Draw arrowhead
                push();
                noStroke();
                fill('#DAA520');
                triangle(width - 150, y, width - 140, y - 5, width - 140, y + 5);
                pop();
            }
        }

        function drawFullView() {
            const centerX = width / 3;
            const centerY = height / 2;
            const earthRadius = 150;

            // Draw Earth
            fill('#468296');
            noStroke();
            circle(centerX, centerY, earthRadius * 2);
            fill(255);
            textSize(20);
            text('Earth', centerX, centerY);

            // Draw Syene
            stroke(0);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);
            line(centerX, centerY, centerX + earthRadius, centerY);

            // Calculate Alexandria position
            const alexX = centerX + earthRadius * cos(angle);
            const alexY = centerY - earthRadius * sin(angle);

            // Draw Alexandria radius
            line(centerX, centerY, alexX, alexY);

            // Draw horizontal line through Alexandria
            line(alexX - 50, alexY, alexX + 50, alexY);

            // Draw angle arc
            drawingContext.setLineDash([]);
            noFill();
            stroke(0);
            strokeWeight(2);
            arc(alexX, alexY, 60, 60, 0, angle);

            // Draw angle text
            fill(0);
            textSize(16);
            text(angle.toFixed(1) + '°', alexX + 70, alexY - 20);

            // Draw labels
            textSize(14);
            text('Syene', centerX + earthRadius + 20, centerY);
            text('Alexandria', alexX + 50, alexY - 10);

            // Draw arrowhead at Alexandria point
            push();
            translate(alexX, alexY);
            rotate(-angle);
            fill(0);
            triangle(0, 0, 10, -5, 10, 5);
            pop();
        }

        function drawZoomedView() {
            const centerX = 100;
            const centerY = height - 100;
            const radius = 250;

            // Draw Earth sector
            fill('#468296');
            noStroke();
            beginShape();
            vertex(centerX, centerY);
            arc(centerX, centerY, radius * 2, radius * 2, -angle, 0);
            endShape(CLOSE);

            // Draw radii
            stroke(0);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);

            // Syene radius (horizontal)
            line(centerX, centerY, centerX + radius, centerY);

            // Alexandria radius
            const alexX = centerX + radius * cos(angle);
            const alexY = centerY - radius * sin(angle);
            line(centerX, centerY, alexX, alexY);

            // Draw arc between radii
            drawingContext.setLineDash([]);
            noFill();
            strokeWeight(3);
            arc(centerX, centerY, 100, 100, -angle, 0);

            // Draw angle text
            fill(0);
            textSize(24);
            push();
            translate(centerX + 50, centerY - 20);
            rotate(-angle / 2);
            text(angle.toFixed(1) + '°', 0, 0);
            pop();

            // Draw labels
            textSize(16);
            text('Syene', centerX + radius + 10, centerY);
            text('Alexandria', alexX + 10, alexY - 10);

            // Draw post and shadow triangle
            push();
            translate(alexX, alexY);
            rotate(-angle);
            fill('#D2B48C');
            noStroke();
            triangle(0, 0, 30, 0, 0, 30);
            pop();
        }
    </script>
</body>
</html>
