<body>
    <div id="app-container">
        <div id="control-panel">
            <button id="btn-reset">+</button>

            <div class="slider-container">
                <label for="slider-moon-pos">position of moonlabel>
                <input type="range" id="slider-moon-pos" min="0" max="360" step="1" value="144">
            </div>

            <div class="slider-container">
                <label for="slider-moon-pull">pull from moonlabel>
                <input type="range" id="slider-moon-pull" min="0" max="100" step="1" value="40">
            </div>

            <div class="slider-container">
                <label for="slider-sun-pos">position of sunlabel>
                <input type="range" id="slider-sun-pos" min="0" max="360" step="1" value="144">
            </div>

            <div class="slider-container">
                <label for="slider-sun-pull">pull from sunlabel>
                <input type="range" id="slider-sun-pull" min="0" max="100" step="1" value="20">
            </div>
        </div>

        <div id="viz-container"></div>
    </div>

    <script>
        // Get slider elements
        const moonPosSlider = document.getElementById('slider-moon-pos');
        const moonPullSlider = document.getElementById('slider-moon-pull');
        const sunPosSlider = document.getElementById('slider-sun-pos');
        const sunPullSlider = document.getElementById('slider-sun-pull');
        const resetButton = document.getElementById('btn-reset');

        // p5.js sketch
        const sketch = (p) => {
            let canvas;

            p.setup = function() {
                canvas = p.createCanvas(500, 500);
                canvas.parent('viz-container');
                p.noLoop();
                p.draw();
            };

            p.draw = function() {
                p.clear();
                p.background(255);

                // Get current slider values
                const moonAngle = parseFloat(moonPosSlider.value);
                const moonPull = parseFloat(moonPullSlider.value);
                const sunAngle = parseFloat(sunPosSlider.value);
                const sunPull = parseFloat(sunPullSlider.value);

                // Convert to radians
                const moonAngleRad = p.radians(moonAngle);
                const sunAngleRad = p.radians(sunAngle);

                // Constants
                const earthRadius = 180;
                const baseWaterRadius = earthRadius + 10;
                const scalingFactor = 0.5;

                // Draw water layer
                p.push();
                p.translate(p.width / 2, p.height / 2);
                p.fill('#aed6f1');
                p.stroke(0);
                p.strokeWeight(1);

                p.beginShape();
                for (let theta = 0; theta <= 360; theta += 1) {
                    const thetaRad = p.radians(theta);

                    // Calculate tidal effects
                    const moonEffect = moonPull * Math.pow(Math.cos(thetaRad - moonAngleRad), 2);
                    const sunEffect = sunPull * Math.pow(Math.cos(thetaRad - sunAngleRad), 2);

                    // Calculate total radius
                    const totalRadius = baseWaterRadius + scalingFactor * (moonEffect + sunEffect);

                    // Convert to Cartesian coordinates
                    const x = totalRadius * Math.cos(thetaRad);
                    const y = totalRadius * Math.sin(thetaRad);

                    p.vertex(x, y);
                }
                p.endShape(p.CLOSE);
                p.pop();

                // Draw Earth
                p.push();
                p.translate(p.width / 2, p.height / 2);
                p.fill('#966F33');
                p.noStroke();
                p.ellipse(0, 0, earthRadius * 2, earthRadius * 2);
                p.pop();
            };
        };

        // Create p5 instance
        const p5Instance = new p5(sketch);

        // Add event listeners for sliders
        function setupSliderListeners() {
            const sliders = [moonPosSlider, moonPullSlider, sunPosSlider, sunPullSlider];

            sliders.forEach(slider => {
                slider.addEventListener('input', () => {
                    p5Instance.redraw();
                });
            });
        }

        // Reset button functionality
        resetButton.addEventListener('click', () => {
            moonPosSlider.value = 144;
            moonPullSlider.value = 40;
            sunPosSlider.value = 144;
            sunPullSlider.value = 20;
            p5Instance.redraw();
        });

        // Initialize
        setupSliderListeners();
    </script>
