head>
<body>
    <div id="app-container">
        <div id="controls-panel">
            <div class="control-group">
                <label for="slider-earth-position">Earth positionlabel>
                <input type="range" id="slider-earth-position" min="0" max="360" step="1" value="180">
            </div>
            <div class="control-group">
                <label for="slider-comet-position">comet positionlabel>
                <input type="range" id="slider-comet-position" min="0" max="360" step="1" value="180">
            </div>
            <div class="control-group">
                <label for="slider-coma">comalabel>
                <input type="range" id="slider-coma" min="0" max="100" step="1" value="20">
            </div>
            <div class="control-group">
                <label for="slider-tail">taillabel>
                <input type="range" id="slider-tail" min="0" max="100" step="1" value="50">
            </div>
            <div class="control-group">
                <label for="slider-tail-spread">tail spreadlabel>
                <input type="range" id="slider-tail-spread" min="0" max="100" step="1" value="20">
            </div>
        </div>

        <div id="visualization-panel">
            <div id="telescopic-view-wrapper">
                <canvas id="telescopic-canvas"></canvas>
                <p>telescopic viewp>
            </div>
            <div id="orbit-view-wrapper">
                <canvas id="orbit-canvas"></canvas>
                <p>orbit diagramp>
            </div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        // Global variables
        let sceneOrbit, sceneTelescopic;
        let cameraOrbit, cameraTelescopic;
        let rendererOrbit, rendererTelescopic;
        let earth, cometGroup, cometNucleus, cometComa, cometTail;
        let earthPosition = 180, cometPosition = 180;
        let comaSize = 20, tailLength = 50, tailSpread = 20;

        // Orbital parameters
        const orbits = {
            earth: { a: 4, e: 0.0167, color: 0x0066ff },
            mars: { a: 6.1, e: 0.0934, color: 0xff0000 },
            jupiter: { a: 20.8, e: 0.0489, color: 0xff6600 },
            comet: { a: 14.4, e: 0.432, color: 0xffff00 }
        };

        function init() {
            // Initialize both scenes
            setupOrbitScene();
            setupTelescopicScene();

            // Set up event listeners
            setupEventListeners();

            // Start animation loop
            animate();
        }

        function setupOrbitScene() {
            const canvas = document.getElementById('orbit-canvas');
            sceneOrbit = new THREE.Scene();
            sceneOrbit.background = new THREE.Color(0xffffff);

            // Camera setup
            cameraOrbit = new THREE.PerspectiveCamera(75, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
            cameraOrbit.position.set(0, 20, 20);
            cameraOrbit.lookAt(0, 0, 0);

            // Renderer setup
            rendererOrbit = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            rendererOrbit.setSize(canvas.clientWidth, canvas.clientHeight);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            sceneOrbit.add(ambientLight);

            const sunLight = new THREE.PointLight(0xffffff, 1, 100);
            sunLight.position.set(0, 0, 0);
            sceneOrbit.add(sunLight);

            // Create Sun
            const sunGeometry = new THREE.SphereGeometry(0.5, 16, 16);
            const sunMaterial = new THREE.MeshBasicMaterial({ 
                color: 0xffff00,
                emissive: 0xffff00,
                emissiveIntensity: 1
            });
            const sun = new THREE.Mesh(sunGeometry, sunMaterial);
            sceneOrbit.add(sun);

            // Create orbital paths
            createOrbits();

            // Create Earth
            const earthGeometry = new THREE.SphereGeometry(0.3, 16, 16);
            const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0066ff });
            earth = new THREE.Mesh(earthGeometry, earthMaterial);
            updateEarthPosition();
            sceneOrbit.add(earth);

            // Create comet model
            createCometModel();
            sceneOrbit.add(cometGroup);
            updateCometPosition();
        }

        function setupTelescopicScene() {
            const canvas = document.getElementById('telescopic-canvas');
            sceneTelescopic = new THREE.Scene();
            sceneTelescopic.background = new THREE.Color(0x000000);

            // Camera setup (narrow field of view for telescope effect)
            cameraTelescopic = new THREE.PerspectiveCamera(15, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
            updateTelescopicCamera();

            // Renderer setup
            rendererTelescopic = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            rendererTelescopic.setSize(canvas.clientWidth, canvas.clientHeight);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040, 2);
            sceneTelescopic.add(ambientLight);

            // Add the same comet model to telescopic scene
            sceneTelescopic.add(cometGroup);
        }

        function createOrbits() {
            Object.entries(orbits).forEach(([name, params]) => {
                const curve = new THREE.EllipseCurve(
                    0, 0,
                    params.a, params.a * Math.sqrt(1 - params.e * params.e),
                    0, 2 * Math.PI,
                    false,
                    0
                );

                const points = curve.getPoints(100);
                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const material = new THREE.LineBasicMaterial({ color: params.color });
                const orbit = new THREE.Line(geometry, material);
                orbit.rotation.x = Math.PI / 2;
                sceneOrbit.add(orbit);
            });
        }

        function createCometModel() {
            cometGroup = new THREE.Group();

            // Nucleus
            const nucleusGeometry = new THREE.SphereGeometry(0.2, 16, 16);
            const nucleusMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
            cometNucleus = new THREE.Mesh(nucleusGeometry, nucleusMaterial);
            cometGroup.add(cometNucleus);

            // Coma
            const comaGeometry = new THREE.SphereGeometry(0.5, 16, 16);
            const comaMaterial = new THREE.MeshBasicMaterial({ 
                color: 0xAAFFAA,
                transparent: true,
                opacity: 0.5
            });
            cometComa = new THREE.Mesh(comaGeometry, comaMaterial);
            cometGroup.add(cometComa);

            // Tail
            const tailGeometry = new THREE.ConeGeometry(0.3, 1, 8, 1, true);
            const tailMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x88FF88,
                transparent: true,
                opacity: 0.4,
                side: THREE.DoubleSide
            });
            cometTail = new THREE.Mesh(tailGeometry, tailMaterial);
            cometTail.position.z = -0.5;
            cometTail.rotation.x = Math.PI / 2;
            cometGroup.add(cometTail);

            updateCometAppearance();
        }

        function updateEarthPosition() {
            const angle = (earthPosition * Math.PI) / 180;
            const r = orbits.earth.a * (1 - orbits.earth.e * orbits.earth.e) / (1 + orbits.earth.e * Math.cos(angle));
            earth.position.x = r * Math.cos(angle);
            earth.position.z = r * Math.sin(angle);
        }

        function updateCometPosition() {
            const angle = (cometPosition * Math.PI) / 180;
            const r = orbits.comet.a * (1 - orbits.comet.e * orbits.comet.e) / (1 + orbits.comet.e * Math.cos(angle));
            cometGroup.position.x = r * Math.cos(angle);
            cometGroup.position.z = r * Math.sin(angle);

            // Update tail orientation to point away from Sun
            const cometVector = new THREE.Vector3(cometGroup.position.x, 0, cometGroup.position.z);
            cometVector.normalize();
            const angleToSun = Math.atan2(cometVector.z, cometVector.x);
            cometTail.rotation.y = angleToSun + Math.PI;
        }

        function updateCometAppearance() {
            // Update coma size
            const comaScale = 0.5 + (comaSize / 100) * 3;
            cometComa.scale.set(comaScale, comaScale, comaScale);

            // Update tail length
            const tailScaleY = 0.5 + (tailLength / 100) * 3;
            cometTail.scale.y = tailScaleY;

            // Update tail spread
            const tailScaleXZ = 0.5 + (tailSpread / 100) * 2;
            cometTail.scale.x = tailScaleXZ;
            cometTail.scale.z = tailScaleXZ;
        }

        function updateTelescopicCamera() {
            cameraTelescopic.position.copy(earth.position);
            cameraTelescopic.position.y += 0.5;
            cameraTelescopic.lookAt(cometGroup.position);
        }

        function update() {
            updateEarthPosition();
            updateCometPosition();
            updateCometAppearance();
            updateTelescopicCamera();
        }

        function setupEventListeners() {
