            </div>
        </div>

        <div id="canvas-container">
            <!-- Canvas will be created by p5.js -->
        </div>
    </div>

    <script>
        // Get DOM elements
        const sliderM = document.getElementById('slider-m');
        const sliderX = document.getElementById('slider-x');
        const sliderY = document.getElementById('slider-y');
        const labelM = document.getElementById('label-m');
        const labelX = document.getElementById('label-x');
        const labelY = document.getElementById('label-y');

        // Add event listeners for sliders
        sliderM.addEventListener('input', (e) => {
            labelM.textContent = e.target.value;
        });

        sliderX.addEventListener('input', (e) => {
            labelX.textContent = e.target.value;
        });

        sliderY.addEventListener('input', (e) => {
            labelY.textContent = e.target.value;
        });

        // p5.js sketch
        const sketch = (p) => {
            // Canvas dimensions
            const canvasWidth = 800;
            const canvasHeight = 500;

            // Fixed parameters
            const L = 10; // Distance between posts
            const g = 9.81; // Acceleration due to gravity

            // Visual parameters
            const leftAnchorX = 100;
            const rightAnchorX = 700;
            const anchorY = 150;
            const scale = 50; // pixels per logical unit for y-direction
            const pixelRangeX = rightAnchorX - leftAnchorX;

            p.setup = () => {
                const canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');
            };

            p.draw = () => {
                // Get current slider values
                const m = parseFloat(sliderM.value);
                const x = parseFloat(sliderX.value);
                const y = parseFloat(sliderY.value);

                // Calculate angles
                const theta1 = Math.atan(y / x);
                const theta2 = Math.atan(y / (L - x));

                // Calculate gravitational force
                const Fg = m * g;

                // Calculate tensions
                const T1 = (Fg * Math.cos(theta2)) / Math.sin(theta1 + theta2);
                const T2 = (Fg * Math.cos(theta1)) / Math.sin(theta1 + theta2);

                // Clear canvas
                p.background(255);

                // Calculate mass position in pixels
                const massX = leftAnchorX + (x / L) * pixelRangeX;
                const massY = anchorY + y * scale;

                // Draw tension values at the top
                p.textAlign(p.CENTER);
                p.textSize(20);
                p.fill(0);
                p.text(`T₁ = ${T1.toFixed(4)} N | T₂ = ${T2.toFixed(4)} N`, p.width / 2, 40);

                // Draw posts
                p.strokeWeight(8);
                p.stroke(0);
                p.line(leftAnchorX, anchorY - 50, leftAnchorX, anchorY + 200);
                p.line(rightAnchorX, anchorY - 50, rightAnchorX, anchorY + 200);

                // Draw horizontal reference line (dashed)
                p.strokeWeight(2);
                p.stroke(150);
                drawDashedLine(p, leftAnchorX, anchorY, rightAnchorX, anchorY, 10);

                // Draw rope
                p.strokeWeight(3);
                p.stroke(0);
                p.line(leftAnchorX, anchorY, massX, massY);
                p.line(rightAnchorX, anchorY, massX, massY);

                // Draw mass
                p.fill(0);
                p.noStroke();
                p.ellipse(massX, massY, 40, 40);

                // Draw angles
                p.noFill();
                p.stroke(0, 150, 0);
                p.strokeWeight(2);

                // Theta 1 (left angle)
                p.arc(leftAnchorX, anchorY, 80, 80, 0, theta1);

                // Theta 2 (right angle)
                p.arc(rightAnchorX, anchorY, 80, 80, Math.PI - theta2, Math.PI);

                // Draw labels
                p.fill(0);
                p.noStroke();
                p.textAlign(p.CENTER);
                p.textSize(16);

                // Tension labels
                const t1LabelX = (leftAnchorX + massX) / 2 - 30;
                const t1LabelY = (anchorY + massY) / 2 - 10;
                p.text('T₁', t1LabelX, t1LabelY);

                const t2LabelX = (rightAnchorX + massX) / 2 + 30;
                const t2LabelY = (anchorY + massY) / 2 - 10;
                p.text('T₂', t2LabelX, t2LabelY);

                // Angle labels
                p.textSize(14);
                const theta1LabelX = leftAnchorX + 50 * Math.cos(theta1 / 2);
                const theta1LabelY = anchorY + 50 * Math.sin(theta1 / 2);
                p.text('θ₁', theta1LabelX, theta1LabelY);

                const theta2LabelX = rightAnchorX - 50 * Math.cos(theta2 / 2);
                const theta2LabelY = anchorY + 50 * Math.sin(theta2 / 2);
                p.text('θ₂', theta2LabelX, theta2LabelY);
            };

            // Helper function to draw dashed line
            function drawDashedLine(p, x1, y1, x2, y2, dashLength) {
                const dx = x2 - x1;
                const dy = y2 - y1;
                const distance = Math.sqrt(dx * dx + dy * dy);
                const dashes = Math.floor(distance / dashLength);

                for (let i = 0; i < dashes; i += 2) {
                    const start = i / dashes;
                    const end = Math.min((i + 1) / dashes, 1);

                    p.line(
                        x1 + dx * start,
                        y1 + dy * start,
                        x1 + dx * end,
                        y1 + dy * end
                    );
                }
            }
        };

        // Create p5.js instance
        new p5(sketch);
    </script>
</body>
</html>
