    <script>
        // Global variables
        let time = 0;
        let animationFrameId = null;
        let isRunning = false;
        let threeScene, threeCamera, threeRenderer;
        let massMesh, springMesh, damperPiston, damperHousing;
        let timeData = [];
        let displacementData = [];
        let velocityData = [];

        // Get slider elements
        const sliders = {
            displacement: document.getElementById('slider-displacement'),
