            massMesh = new THREE.Mesh(massGeometry, massMaterial);
            massMesh.position.y = 0.4;
            massMesh.castShadow = true;
            threeScene.add(massMesh);

            // Spring
            const springPoints = [];
            const springLength = 2;
            const coils = 10;
            for (let i = 0; i <= 100; i++) {
                const t = i / 100;
                const x = -springLength / 2 + t * springLength;
                const y = 0.4 + 0.2 * Math.sin(t * coils * Math.PI * 2);
                springPoints.push(new THREE.Vector3(x, y, 0));
            }
            const springCurve = new THREE.CatmullRomCurve3(springPoints);
            const springTubeGeometry = new THREE.TubeGeometry(springCurve, 100, 0.05, 8, false);
            const springMaterial = new THREE.MeshStandardMaterial({ color: 0xFF4444 });
            springMesh = new THREE.Mesh(springTubeGeometry, springMaterial);
            springMesh.rotation.z = Math.PI / 2;
            threeScene.add(springMesh);

            // Damper
            const damperHousingGeometry = new THREE.CylinderGeometry(0.15, 0.15, 1.5);
            const damperMaterial = new THREE.MeshStandardMaterial({ color: 0x2196F3 });
            damperHousing = new THREE.Mesh(damperHousingGeometry, damperMaterial);
            damperHousing.rotation.z = Math.PI / 2;
            damperHousing.position.y = 0.4;
            damperHousing.position.z = 0.5;
            damperHousing.castShadow = true;
            threeScene.add(damperHousing);

            const damperPistonGeometry = new THREE.CylinderGeometry(0.1, 0.1, 0.8);
            damperPiston = new THREE.Mesh(damperPistonGeometry, damperMaterial);
            damperPiston.rotation.z = Math.PI / 2;
            damperPiston.position.y = 0.4;
            damperPiston.position.z = 0.5;
            damperPiston.castShadow = true;
            threeScene.add(damperPiston);

            // Enclosure
            const enclosureGeometry = new THREE.BoxGeometry(6, 3, 3);
            const enclosureMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x999999, 
