                transparent: true
            });
            const enclosure = new THREE.Mesh(enclosureGeometry, enclosureMaterial);
            enclosure.position.y = 1.5;
            threeScene.add(enclosure);

            // Handle window resize
            window.addEventListener('resize', () => {
                const newWidth = container.clientWidth;
                const newHeight = container.clientHeight;
                threeCamera.aspect = newWidth / newHeight;
                threeCamera.updateProjectionMatrix();
                threeRenderer.setSize(newWidth, newHeight);
            });
        }

        // Analytical solution for damped harmonic oscillator
        function calculateSolution(x0, v0, m, c_eff, k_eff) {
            const omega0 = Math.sqrt(k_eff / m);
            const zeta = c_eff / (2 * Math.sqrt(m * k_eff));

            timeData = [];
            displacementData = [];
            velocityData = [];

            const dt = 0.01;
            for (let t = 0; t <= 10; t += dt) {
                timeData.push(t);

                let x, v;

                if (zeta < 1) { // Underdamped
                    const omegaD = omega0 * Math.sqrt(1 - zeta * zeta);
                    const A = x0;
                    const B = (v0 + zeta * omega0 * x0) / omegaD;

                    const expTerm = Math.exp(-zeta * omega0 * t);
                    x = expTerm * (A * Math.cos(omegaD * t) + B * Math.sin(omegaD * t));
                    v = expTerm * ((-zeta * omega0 * A + B * omegaD) * Math.cos(omegaD * t) + 
                                   (-A * omegaD - zeta * omega0 * B) * Math.sin(omegaD * t));
                } else if (zeta === 1) { // Critically damped
                    const A = x0;
                    const B = v0 + omega0 * x0;

                    const expTerm = Math.exp(-omega0 * t);
                    x = expTerm * (A + B * t);
                    v = expTerm * (B - omega0 * (A + B * t));
                } else { // Overdamped
                    const r1 = -omega0 * (zeta + Math.sqrt(zeta * zeta - 1));
                    const r2 = -omega0 * (zeta - Math.sqrt(zeta * zeta - 1));

                    const A = (v0 - r2 * x0) / (r1 - r2);
                    const B = (r1 * x0 - v0) / (r1 - r2);

                    x = A * Math.exp(r1 * t) + B * Math.exp(r2 * t);
                    v = A * r1 * Math.exp(r1 * t) + B * r2 * Math.exp(r2 * t);
                }

                displacementData.push(x);
                velocityData.push(v);
            }
        }

        // Initialize Plotly charts
