            };

            // Displacement plot
            const displacementLayout = {
                ...plotLayout,
                yaxis: { ...plotLayout.yaxis, title: 'Displacement (m)' }
            };

            // Velocity plot
            const velocityLayout = {
                ...plotLayout,
                yaxis: { ...plotLayout.yaxis, title: 'Velocity (m/s)' }
            };

            Plotly.newPlot('plot-displacement', [{
                x: [],
                y: [],
                type: 'scatter',
                mode: 'lines',
                line: { color: '#2196F3', width: 2 },
                name: 'Displacement'
            }, {
                x: [0],
                y: [0],
                type: 'scatter',
                mode: 'markers',
                marker: { color: '#1976D2', size: 10 },
                name: 'Current'
            }], displacementLayout);

            Plotly.newPlot('plot-velocity', [{
                x: [],
                y: [],
                type: 'scatter',
                mode: 'lines',
                line: { color: '#4CAF50', width: 2 },
                name: 'Velocity'
            }, {
                x: [0],
                y: [0],
                type: 'scatter',
                mode: 'markers',
                marker: { color: '#388E3C', size: 10 },
                name: 'Current'
            }], velocityLayout);
        }

        // Update system based on slider values
        function updateSystem() {
            // Get values
            const x0 = parseFloat(sliders.displacement.value);
            const v0 = parseFloat(sliders.velocity.value);
            const m = parseFloat(sliders.mass.value);
            const k = parseFloat(slickers.stiffness.value);
            const c = parseFloat(sliders.damping.value);
            const Kp = parseFloat(sliders.proportionalGain.value);
            const Kd = parseFloat(sliders.derivativeGain.value);

            // Calculate effective parameters
            const k_eff = k + Kp;
            const c_eff = c + Kd;

            // Calculate new solution
            calculateSolution(x0, v0, m, c_eff, k_eff);

            // Update plots
            Plotly.react('plot-displacement', [{
                x: timeData,
                y: displacementData,
                type: 'scatter',
                mode: 'lines',
                line: { color: '#2196F3', width: 2 }
            }, {
                x: [0],
                y: [x0],
                type: 'scatter',
                mode: 'markers',
                marker: { color: '#1976D2', size: 10 }
            }]);

            Plotly.react('plot-velocity', [{
                x: timeData,
                y: velocityData,
                type: 'scatter',
                mode: 'lines',
                line: { color: '#4CAF50', width: 2 }
            }, {
                x: [0],
                y: [v0],
                type: 'scatter',
                mode: 'markers',
                marker: { color: '#388E3C', size: 10 }
            }]);

            // Update 3D scene
            if (massMesh) {
                massMesh.position.x = x0;
                updateSpringScale(x0);
 damperPiston.position.x = x0 / 2; // Damper piston moves half the distance
            }
        }

        // Update spring scaling based on displacement
        function updateSpringScale(displacement) {
            if (springMesh) {
                const baseLength = 2;
                const newLength = baseLength + displacement;
                springMesh.scale.x = newLength / baseLength;
            }
        }

        // Animation loop
        function animate() {
            if (!isRunning) return;

            time += 0.01;

            if (time > 10) {
                pauseAnimation();
                return;
            }

            // Get current values
            const x0 = parseFloat(sliders.displacement.value);
            const v0 = parseFloat(sliders.velocity.value);
            const m = parseFloat(sliders.mass.value);
            const k = parseFloat(sliders.stiffness.value);
            const c = parseFloat(sliders.damping.value);
            const Kp = parseFloat(sliders.proportionalGain.value);
            const Kd = parseFloat(sliders.derivativeGain.value);

            const k_eff = k + Kp;
            const c_eff = c + Kd;

            // Calculate position at current time
            const omega0 = Math.sqrt(k_eff / m);
            const zeta = c_eff / (2 * Math.sqrt(m * k_eff));

            let x, v;

            if (zeta < 1) { // Underdamped
                const omegaD = omega0 * Math.sqrt(1 - zeta * zeta);
                const A = x0;
                const B = (v0 + zeta * omega0 * x0) / omegaD;

                const expTerm = Math.exp(-zeta * omega0 * time);
                x = expTerm * (A * Math.cos(omegaD * time) + B * Math.sin(omegaD * time));
                v = expTerm * ((-zeta * omega0 * A + B * omegaD) * Math.cos(omegaD * time) + 
                               (-A * omegaD - zeta * omega0 * B) * Math.sin(omegaD * time));
            } else if (zeta === 1) { // Critically damped
                const A = x0;
                const B = v0 + omega0 * x0;

                const expTerm = Math.exp(-omega0 * time);
                x = expTerm * (A + B * time);
                v = expTerm * (B - omega0 * (A + B * time));
            } else { // Overdamped
                const r1 = -omega0 * (zeta + Math.sqrt(zeta * zeta - 1));
                const r2 = -omega0 * (zeta - Math.sqrt(zeta * zeta - 1));

                const A = (v0 - r2 * x0) / (r1 - r2);
                const B = (r1 * x0 - v0) / (r1 - r2);

                x = A * Math.exp(r1 * time) + B * Math.exp(r2 * time);
                v = A * r1 * Math.exp(r1 * time) + B * r2 * Math.exp(r2 * time);
            }

            // Update 3D scene
            if (massMesh) {
                massMesh.position.x = x;
                updateSpringScale(x);
                damperPiston.position.x = x / 2;
            }

            // Update plot markers
            Plotly.restyle('plot-displacement', {
                x: [[time]],
                y: [[x]]
            }, [1]);

            Plotly.restyle('plot-velocity', {
                x: [[time]],
                y: [[v]]
            }, [1]);

            // Render scene
            if (threeRenderer && threeScene && threeCamera) {
                threeRenderer.render(threeScene, threeCamera);
            }

            animationFrameId = requestAnimationFrame(animate);
        }

        // Control functions
        function playAnimation() {
            if (!isRunning) {
                isRunning = true;
                animate();
            }
        }

        function pauseAnimation() {
            isRunning = false;
            if (animationFrameId) {
                cancelAnimationFrame(animationFrameId);
                animationFrameId = null;
            }
        }

        function resetAnimation() {
            pauseAnimation();
            time = 0;
            updateSystem();
            if (threeRenderer && threeScene && threeCamera) {
                threeRenderer.render(threeScene, threeCamera);
            }
        }

        // Initialize everything
        window.addEventListener('load', () => {
            initThreeJS();
            initPlots();

            // Update value displays
            Object.keys(sliders).forEach(key => {
                const slider = sliders[key];
                const display = valueDisplays[key];

                display.textContent = parseFloat(slider.value).toFixed(key === 'stiffness' || key === 'mass' ? 2 : 1);

                slider.addEventListener('input', (e) => {
                    display.textContent = parseFloat(e.target.value).toFixed(key === 'stiffness' || key === 'mass' ? 2 : 1);
                    resetAnimation();
                });
            });

            // Button event listeners
            btnPlay.addEventListener('click', playAnimation);
            btnPause.addEventListener('click', pauseAnimation);
            btnReset.addEventListener('click', resetAnimation);

            // Initial update
            updateSystem();

            // Initial render
            if (threeRenderer && threeScene && threeCamera) {
                threeRenderer.render(threeScene, threeCamera);
            }
        });
    </script>
