            <div id="velocity-plot-container" class="plot-container"></div>
        </div>
    </div>

    <script>
        // Global variables
        let isAnimating = false;
        let t = 0;
        let dt = 0.01;
        let animationId;

        // Physics variables
        let T, m1, m2, m3, x1, x2, x3;
        let M, K, eigenvalues, eigenvectors, c;
        let omega, A;

        // Plot data
        let timeData = [];
        let displacementData = [[], [], []];
        let velocityData = [[], [], []];

        // p5.js sketch
        let sketch = function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(600, 360);
                canvas.parent('main-animation-container');
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);

                // Draw grid
                p.stroke(230);
                p.strokeWeight(1);
                for (let i = 0; i <= 4; i++) {
                    let x = p.map(i, 0, 4, 50, 550);
                    p.line(x, 50, x, 310);
                }
                for (let j = -1; j <= 1; j += 0.5) {
                    let y = p.map(j, -1, 1, 310, 50);
                    p.line(50, y, 550, y);
                }

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(50, 310, 550, 310); // x-axis
                p.line(50, 50, 50, 310); // y-axis

                // Draw axis labels
                p.fill(0);
                p.noStroke();
                p.textAlign(CENTER);
                for (let i = 0; i <= 4; i++) {
                    let x = p.map(i, 0, 4, 50, 550);
                    p.text(i, x, 330);
                }
                p.textAlign(RIGHT);
                for (let j = -1; j <= 1; j += 0.5) {
                    let y = p.map(j, -1, 1, 310, 50);
                    p.text(j.toFixed(1), 40, y + 5);
                }

                // Calculate current positions
                let y = calculateDisplacement(t);

                // Draw string
                p.stroke(0);
                p.strokeWeight(2);
                p.beginShape();
                p.noFill();
                p.vertex(50, 310);
                for (let i = 0; i < 3; i++) {
                    let x = p.map(i + 1, 0, 4, 50, 550);
                    let yPos = p.map(y[i], -1, 1, 310, 50);
                    p.vertex(x, yPos);
                }
                p.vertex(550, 310);
                p.endShape();

                // Draw masses
                p.fill(255, 0, 0);
                p.noStroke();
                for (let i = 0; i < 3; i++) {
                    let x = p.map(i + 1, 0, 4, 50, 550);
                    let yPos = p.map(y[i], -1, 1, 310, 50);
                    let mass = [m1, m2, m3][i];
                    let radius = 10 * Math.sqrt(mass);
                    p.circle(x, yPos, radius * 2);
                }
            };
        };

        // Initialize p5.js
        let p5Instance = new p5(sketch);

        // Initialize sliders
        const sliders = {
            tension: document.getElementById('slider-tension'),
            x1: document.getElementById('slider-x1'),
            x2: document.getElementById('slider-x2'),
            x3: document.getElementById('slider-x3'),
            m1: document.getElementById('slider-m1'),
            m2: document.getElementById('slider-m2'),
            m3: document.getElementById('slider-m3')
        };

        const btnPlayPause = document.getElementById('btn-play-pause');
        const btnReset = document.getElementById('btn-reset');

        // Add event listeners
        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', resetSimulation);
        });

        btnPlayPause.addEventListener('click', toggleAnimation);
        btnReset.addEventListener('click', resetSimulation);

        function resetSimulation() {
            isAnimating = false;
            t = 0;
            btnPlayPause.textContent = '▶';
            p5Instance.noLoop();

            // Get current values
            T = parseFloat(sliders.tension.value);
            m1 = parseFloat(sliders.m1.value);
            m2 = parseFloat(sliders.m2.value);
            m3 = parseFloat(sliders.m3.value);
            x1 = parseFloat(sliders.x1.value);
            x2 = parseFloat(sliders.x2.value);
            x3 = parseFloat(sliders.x3.value);

            // Calculate physics
            calculatePhysics();

            // Update plots
            updatePlots();

            // Redraw animation
            p5Instance.redraw();
        }

        function calculatePhysics() {
            // Mass matrix
            M = [[m1, 0, 0], [0, m2, 0], [0, 0, m3]];

            // Stiffness matrix
            K = T * [[2, -1, 0], [-1, 2, -1], [0, -1, 2]];

            // Solve generalized eigenvalue problem
            // Transform to standard eigenvalue problem: C*z = λ*z
            // where C = M^(-1/2) * K * M^(-1/2)
            let M_sqrt = numeric.sqrtm(numeric.inv(M));
            let C = numeric.dot(M_sqrt, numeric.dot(K, M_sqrt));

            // Solve standard eigenvalue problem
            let eig = numeric.eig(C);
            eigenvalues = eig.lambda.x;
            eigenvectors = eig.E.x;

            // Transform back to original coordinates
            omega = numeric.sqrt(eigenvalues);
            A = numeric.dot(numeric.inv(M_sqrt), eigenvectors);

            // Calculate modal participation factors
            let y0 = [x1, x2, x3];
            c = numeric.dot(numeric.inv(A), y0);

            // Pre-calculate data for plots
            timeData = [];
            displacementData = [[], [], []];
            velocityData = [[], [], []];

            for (let t_plot = 0; t_plot <= 5; t_plot += 0.05) {
                timeData.push(t_plot);
                let y = calculateDisplacement(t_plot);
                let v = calculateVelocity(t_plot);

                for (let i = 0; i < 3; i++) {
                    displacementData[i].push(y[i]);
                    velocityData[i].push(v[i]);
                }
            }
        }

        function calculateDisplacement(t_calc) {
            let y = [0, 0, 0];
            for (let i = 0; i < 3; i++) {
                for (let j = 0; j < 3; j++) {
                    y[i] += c[j] * A[i][j] * Math.cos(omega[j] * t_calc);
                }
            }
            return y;
        }

        function calculateVelocity(t_calc) {
            let v = [0, 0, 0];
            for (let i = 0; i < 3; i++) {
                for (let j = 0; j < 3; j++) {
                    v[i] -= c[j] * A[i][j] * omega[j] * Math.sin(omega[j] * t_calc);
                }
            }
            return v;
        }

        function updatePlots() {
            // Displacement plot
            let displacementTrace = [
                {
                    x: timeData,
                    y: displacementData[0],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'y₁(t)',
                    line: {color: 'blue'}
                },
                {
                    x: timeData,
                    y: displacementData[1],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'y₂(t)',
                    line: {color: 'orange'}
                },
                {
                    x: timeData,
                    y: displacementData[2],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'y₃(t)',
                    line: {color: 'green'}
                }
            ];

            let displacementLayout = {
                title: 'displacement',
                xaxis: {title: 'time t', range: [0, 5]},
                yaxis: {title: 'displacement'},
                shapes: [{
                    type: 'line',
                    x0: 0,
                    x1: 0,
                    y0: -1,
                    y1: 1,
                    line: {color: 'red', width: 2, dash: 'dot'}
                }]
            };

            Plotly.newPlot('displacement-plot-container', displacementTrace, displacementLayout, {responsive: true});

            // Velocity plot
            let velocityTrace = [
                {
                    x: timeData,
                    y: velocityData[0],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'v₁(t)',
                    line: {color: 'blue'}
                },
                {
                    x: timeData,
                    y: velocityData[1],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'v₂(t)',
                    line: {color: 'orange'}
                },
                {
                    x: timeData,
                    y: velocityData[2],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'v₃(t)',
                    line: {color: 'green'}
                }
            ];

            let velocityLayout = {
                title: 'velocity',
                xaxis: {title: 'time t', range: [0, 5]},
                yaxis: {title: 'velocity'},
                shapes: [{
                    type: 'line',
                    x0: 0,
                    x1: 0,
                    y0: -5,
                    y1: 5,
                    line: {color: 'red', width: 2, dash: 'dot'}
                }]
            };

            Plotly.newPlot('velocity-plot-container', velocityTrace, velocityLayout, {responsive: true});
        }

        function toggleAnimation() {
            isAnimating = !isAnimating;

            if (isAnimating) {
                btnPlayPause.textContent = '❚❚';
                p5Instance.loop();
                animate();
            } else {
                btnPlayPause.textContent = '▶';
                p5Instance.noLoop();
                cancelAnimationFrame(animationId);
            }
        }

        function animate() {
            if (!isAnimating) return;

            t += dt;
            if (t > 5) t = 0;

            // Update time marker in plots
            let displacementUpdate = {
                shapes: [{
                    type: 'line',
                    x0: t,
                    x1: t,
                    y0: -1,
                    y1: 1,
                    line: {color: 'red', width: 2, dash: 'dot'}
                }]
            };

            let velocityUpdate = {
                shapes: [{
                    type: 'line',
                    x0: t,
                    x1: t,
                    y0: -5,
                    y1: 5,
                    line: {color: 'red', width: 2, dash: 'dot'}
                }]
            };

            Plotly.relayout('displacement-plot-container', displacementUpdate);
            Plotly.relayout('velocity-plot-container', velocityUpdate);

            animationId = requestAnimationFrame(animate);
        }

        // Initialize on load
        window.addEventListener('load', resetSimulation);
    </script>
