            <p id="explanation-text"></p>
        </div>
    </div>

    <script>
        let canvas;
        let mode = 'move';
        let points = [
            {x: 150, y: 350}, 
            {x: 250, y: 150}, 
            {x: 350, y: 250}, 
            {x: 550, y: 180}, 
            {x: 480, y: 320}, 
            {x: 420, y: 400}
        ];
        let draggedPoint = null;

        let algorithmState = {
            step: 0,
            hullPointsIndices: [],
            fullHull: [],
            leftmostPointIndex: null,
            currentPointIndex: null,
            candidateLines: [],
            explanationSteps: []
        };

        function setup() {
            canvas = createCanvas(700, 500);
            canvas.parent('canvas-container');

            setupEventListeners();
        }

        function draw() {
            background(255);
            stroke(220);
            noFill();
            rect(0, 0, width, height);

            // Draw candidate lines
            stroke(60, 150, 140);
            strokeWeight(2);
            for (let line of algorithmState.candidateLines) {
                let p1 = points[line[0]];
                let p2 = points[line[1]];
                line(p1.x, p1.y, p2.x, p2.y);
            }

            // Draw hull edges
            if (algorithmState.hullPointsIndices.length > 1) {
                stroke(45, 60, 110);
                strokeWeight(3);
                for (let i = 0; i < algorithmState.hullPointsIndices.length - 1; i++) {
                    let p1 = points[algorithmState.hullPointsIndices[i]];
                    let p2 = points[algorithmState.hullPointsIndices[i + 1]];
                    line(p1.x, p1.y, p2.x, p2.y);
                }

                // Close the hull if we're back to the start
                if (algorithmState.step === 3 && 
                    algorithmState.currentPointIndex === algorithmState.leftmostPointIndex) {
                    let p1 = points[algorithmState.hullPointsIndices[algorithmState.hullPointsIndices.length - 1]];
                    let p2 = points[algorithmState.hullPointsIndices[0]];
                    line(p1.x, p1.y, p2.x, p2.y);
                }
            }

            // Draw points
            for (let i = 0; i < points.length; i++) {
                let isHullPoint = algorithmState.hullPointsIndices.includes(i);

                if (isHullPoint) {
                    fill(45, 60, 110);
                } else {
                    fill(200, 220, 100);
                }

                stroke(0);
                strokeWeight(1);
                ellipse(points[i].x, points[i].y, 12, 12);

                // Draw crosshair
                stroke(0);
                line(points[i].x - 4, points[i].y, points[i].x + 4, points[i].y);
                line(points[i].x, points[i].y - 4, points[i].x, points[i].y + 4);
            }
        }

        function setupEventListeners() {
            document.getElementById('btn-move').addEventListener('click', handleMoveClick);
            document.getElementById('btn-start-algo').addEventListener('click', handleStartAlgoClick);
            document.getElementById('btn-step1-explain').addEventListener('click', handleStep1Explain);
            document.getElementById('btn-step1-do').addEventListener('click', handleStep1Do);
            document.getElementById('btn-step2-explain').addEventListener('click', handleStep2Explain);
            document.getElementById('btn-step2-do').addEventListener('click', handleStep2Do);
            document.getElementById('btn-step3-explain').addEventListener('click', handleStep3Explain);
        }

        function mousePressed() {
            if (mode === 'move') {
                for (let i = 0; i < points.length; i++) {
                    let d = dist(mouseX, mouseY, points[i].x, points[i].y);
                    if (d < 10) {
                        draggedPoint = i;
                        break;
                    }
                }
            }
        }

        function mouseDragged() {
            if (draggedPoint !== null && mode === 'move') {
                points[draggedPoint].x = mouseX;
                points[draggedPoint].y = mouseY;
            }
        }

        function mouseReleased() {
            draggedPoint = null;
        }

        function handleMoveClick() {
            mode = 'move';
            resetAlgorithmState();
            document.getElementById('btn-move').classList.add('active');
            document.getElementById('btn-start-algo').classList.remove('active');
            document.getElementById('step-controls').style.display = 'none';
            document.getElementById('explanation-text').textContent = '';
        }

        function handleStartAlgoClick() {
            mode = 'algorithm';
            resetAlgorithmState();
            document.getElementById('btn-start-algo').classList.add('active');
            document.getElementById('btn-move').classList.remove('active');
            document.getElementById('step-controls').style.display = 'block';

            // Pre-calculate full hull
            algorithmState.fullHull = calculateJarvisMarch(points);

            // Generate step 3 buttons
            generateStep3Buttons();

            // Enable step 1 buttons
            enableStepButtons(1);
        }

        function handleStep1Explain() {
            algorithmState.explanationSteps.push("Step 1. Find the leftmost point.");
            updateExplanation();
        }

        function handleStep1Do() {
            // Find leftmost point
            let leftmostIndex = 0;
            for (let i = 1; i < points.length; i++) {
                if (points[i].x < points[leftmostIndex].x) {
                    leftmostIndex = i;
                }
            }

            algorithmState.leftmostPointIndex = leftmostIndex;
            algorithmState.currentPointIndex = leftmostIndex;
            algorithmState.hullPointsIndices.push(leftmostIndex);

            disableStepButtons(1);
            enableStepButtons(2);
        }

        function handleStep2Explain() {
            algorithmState.explanationSteps.push("Step 2. Find the point clockwise furthest to the right relative to the leftmost point.");
            updateExplanation();
        }

        function handleStep2Do() {
            // Create candidate lines from current point to all others
            let currentIndex = algorithmState.currentPointIndex;
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (i !== currentIndex && !algorithmState.hullPointsIndices.includes(i)) {
                    algorithmState.candidateLines.push([currentIndex, i]);
                }
            }

            // Schedule drawing of the selected line
            setTimeout(() => {
                let nextIndex = algorithmState.fullHull[1];
                algorithmState.candidateLines = [];
                algorithmState.hullPointsIndices.push(nextIndex);
                algorithmState.currentPointIndex = nextIndex;

                disableStepButtons(2);
                enableStepButtons(3);
            }, 1000);
        }

        function handleStep3Explain() {
            algorithmState.explanationSteps.push("Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.");
            updateExplanation();
        }

        function handleStep3Do(repeatIndex = 2) {
            let currentIndex = algorithmState.currentPointIndex;
            let nextIndex = algorithmState.fullHull[repeatIndex];

            // Show candidate lines
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (i !== currentIndex && !algorithmState.hullPointsIndices.includes(i)) {
                    algorithmState.candidateLines.push([currentIndex, i]);
                }
            }

            // After delay, show the selected line
            setTimeout(() => {
                algorithmState.candidateLines = [];
                algorithmState.hullPointsIndices.push(nextIndex);
                algorithmState.currentPointIndex = nextIndex;

                // Disable the clicked button
                let btnId = repeatIndex === 2 ? 'btn-step3-do' : `btn-step3-repeat-${repeatIndex - 2}`;
                document.getElementById(btnId).disabled = true;

                // Enable next button if exists
                if (repeatIndex < algorithmState.fullHull.length) {
                    let nextBtnId = `btn-step3-repeat-${repeatIndex - 1}`;
                    let nextBtn = document.getElementById(nextBtnId);
                    if (nextBtn) nextBtn.disabled = false;
                }

                // If this is the last point, close the hull
                if (nextIndex === algorithmState.leftmostPointIndex) {
                    setTimeout(() => {
                        // Draw final edge
                        redraw();
                    }, 500);
                }
            }, 1000);
        }

        function generateStep3Buttons() {
            let container = document.getElementById('step3-actions');
            container.innerHTML = '';

            // Add "do it" button
            let doButton = document.createElement('button');
            doButton.id = 'btn-step3-do';
            doButton.textContent = 'do it';
            doButton.addEventListener('click', () => handleStep3Do(2));
            container.appendChild(doButton);

            // Add repeat buttons
            for (let i = 2; i < algorithmState.fullHull.length - 1; i++) {
                let repeatButton = document.createElement('button');
                repeatButton.id = `btn-step3-repeat-${i - 1}`;
                repeatButton.textContent = 'repeat';
                repeatButton.disabled = true;
                repeatButton.addEventListener('click', () => handleStep3Do(i + 1));
                container.appendChild(repeatButton);
            }
        }

        function resetAlgorithmState() {
            algorithmState = {
                step: 0,
                hullPointsIndices: [],
                fullHull: [],
                leftmostPointIndex: null,
                currentPointIndex: null,
                candidateLines: [],
                explanationSteps: []
            };
        }

        function enableStepButtons(step) {
            if (step === 1) {
                document.getElementById('btn-step1-explain').disabled = false;
                document.getElementById('btn-step1-do').disabled = false;
            } else if (step === 2) {
                document.getElementById('btn-step2-explain').disabled = false;
                document.getElementById('btn-step2-do').disabled = false;
            } else if (step === 3) {
                document.getElementById('btn-step3-explain').disabled = false;
                document.getElementById('btn-step3-do').disabled = false;
            }
        }

        function disableStepButtons(step) {
            if (step === 1) {
                document.getElementById('btn-step1-explain').disabled = true;
                document.getElementById('btn-step1-do').disabled = true;
            } else if (step === 2) {
                document.getElementById('btn-step2-explain').disabled = true;
                document.getElementById('btn-step2-do').disabled = true;
            }
        }

        function updateExplanation() {
            let text = algorithmState.explanationSteps.join('\n\n');
            document.getElementById('explanation-text').textContent = text;
        }

        function calculateJarvisMarch(points) {
            if (points.length < 3) return [];

            let hull = [];
            let leftmost = 0;
            for (let i = 1; i < points.length; i++) {
                if (points[i].x < points[leftmost].x) {
                    leftmost = i;
                }
            }

            let p = leftmost;
            do {
                hull.push(p);
                let q = (p + 1) % points.length;

                for (let i = 0; i < points.length; i++) {
                    if (orientation(points[p], points[i], points[q]) === 2) {
                        q = i;
                    }
                }

                p = q;
            } while (p !== leftmost);

            return hull;
        }

        function orientation(p, q, r) {
            let val = (q.y - p.y) * (r.x - q.x) - (q.x - p.x) * (r.y - q.y);
            if (val === 0) return 0;
            return (val > 0) ? 1 : 2;
        }
    </script>
</body>
