    </div>

    <script>
        // Initial conditions
        const initialConditions = {
            X: 0.1,    // Biomass [mol/m³]
            Glu: 60,   // Glucose [mol/m³]
            Eta: 0,    // Ethanol [mol/m³]
            E0: 1.0,   // Enzyme E0 [mol/m³]
            E1: 0.0,   // Enzyme E1 [mol/m³]
            E2: 0.0,   // Enzyme E2 [mol/m³]
            V: 1.0     // Volume [m³]
        };

        // Simulation parameters
        const modelParams = {
            mu_max: 0.5,       // Maximum growth rate [1/h]
            Ks_glu: 0.1,       // Glucose saturation constant [mol/m³]
            Ks_eta: 0.1,       // Ethanol saturation constant [mol/m³]
            Yxs_glu: 0.5,      // Yield coefficient X/Glu [mol/mol]
            Yxs_eta: 0.3,      // Yield coefficient X/Eta [mol/mol]
            m_glu: 0.01,       // Maintenance coefficient on glucose [mol/mol/h]
            m_eta: 0.01,       // Maintenance coefficient on ethanol [mol/mol/h]
            k_syn: 0.5,        // Enzyme synthesis rate [1/h]
            k_deg: 0.05,       // Enzyme degradation rate [1/h]
            n: 4               // Hill coefficient
        };

        // Current simulation data
        let currentData = null;
        let currentTimeData = null;
        let currentAlphaData = null;

        // Get slider elements
        const sliders = {
            alphaMax: document.getElementById('slider-alpha-max'),
            tMin: document.getElementById('slider-t-min'),
            tMax: document.getElementById('slider-t-max'),
            enzymeControl: document.getElementById('slider-enzyme-control'),
            feedRate: document.getElementById('slider-feed-rate'),
            substrateFeedConc: document.getElementById('slider-substrate-feed-conc'),
            feedTime: document.getElementById('slider-feed-time')
        };

        const valueDisplays = {
            alphaMax: document.getElementById('value-alpha-max'),
            tMin: document.getElementById('value-t-min'),
            tMax: document.getElementById('value-t-max'),
            enzymeControl: document.getElementById('value-enzyme-control'),
            feedRate: document.getElementById('value-feed-rate'),
            substrateFeedConc: document.getElementById('value-substrate-feed-conc'),
            feedTime: document.getElementById('value-feed-time')
        };

        const plotSelect = document.getElementById('select-plot');

        // Update value displays
        function updateValueDisplays() {
            valueDisplays.alphaMax.textContent = parseFloat(sliders.alphaMax.value).toFixed(2);
            valueDisplays.tMin.textContent = sliders.tMin.value;
            valueDisplays.tMax.textContent = sliders.tMax.value;
            valueDisplays.enzymeControl.textContent = parseFloat(sliders.enzymeControl.value).toFixed(2);
            valueDisplays.feedRate.textContent = parseFloat(sliders.feedRate.value).toFixed(2);
            valueDisplays.substrateFeedConc.textContent = sliders.substrateFeedConc.value;
            valueDisplays.feedTime.textContent = sliders.feedTime.value;
        }

        // Hill equation for alpha (fraction of respiring biomass)
        function calculateAlpha(glu, alphaMax, KI) {
            return alphaMax * Math.pow(KI, modelParams.n) / 
                   (Math.pow(KI, modelParams.n) + Math.pow(glu, modelParams.n));
        }

        // ODE system for diauxic growth
        function odeSystem(t, y, params) {
            const [X, Glu, Eta, E0, E1, E2, V] = y;

            // Calculate alpha
            const alpha = calculateAlpha(Glu, params.alphaMax, params.KI);

            // Feed flow and concentration (only during feed time)
            const F = t <= params.tFeed ? params.feedRate : 0;
            const G_in = t <= params.tFeed ? params.substrateFeedConc : 0;

            // Specific growth rates
            const mu_glu = modelParams.mu_max * Glu / (modelParams.Ks_glu + Glu) * E1;
            const mu_eta = modelParams.mu_max * Eta / (modelParams.Ks_eta + Eta) * E2;

            // Total specific growth rate
            const mu = (1 - alpha) * mu_glu + alpha * mu_eta;

            // Enzyme dynamics
            const dE0_dt = modelParams.k_syn * (1 - alpha) - modelParams.k_deg * E0;
            const dE1_dt = modelParams.k_syn * (1 - alpha) * E0 - modelParams.k_deg * E1;
            const dE2_dt = modelParams.k_syn * alpha * E0 - modelParams.k_deg * E2;

            // Mass balances for fed-batch reactor
            const dX_dt = mu * X - F * X / V;
            const dGlu_dt = -mu * X / modelParams.Yxs_glu * (1 - alpha) - modelParams.m_glu * X + F * G_in / V;
            const dEta_dt = mu * X / modelParams.Yxs_eta * alpha - modelParams.m_eta * X * Eta / (modelParams.Ks_eta + Eta);
            const dV_dt = F;

            return [dX_dt, dGlu_dt, dEta_dt, dE0_dt, dE1_dt, dE2_dt, dV_dt];
        }

        // RK4 ODE solver
        function solveRK4(f, t0, y0, tf, dt, params) {
            const t = [t0];
            const y = [y0.slice()];

            for (let current_t = t0; current_t < tf; current_t += dt) {
                const k1 = f(current_t, y[y.length - 1], params).map(dy => dy * dt);
                const k2 = f(current_t + dt/2, y[y.length - 1].map((yi, i) => yi + k1[i]/2), params).map(dy => dy * dt);
                const k3 = f(current_t + dt/2, y[y.length - 1].map((yi, i) => yi + k2[i]/2), params).map(dy => dy * dt);
                const k4 = f(current_t + dt, y[y.length - 1].map((yi, i) => yi + k3[i]), params).map(dy => dy * dt);

                const y_next = y[y.length - 1].map((yi, i) => 
                    yi + (k1[i] + 2*k2[i] + 2*k3[i] + k4[i]) / 6
                );

                t.push(current_t + dt);
                y.push(y_next);
            }

            return { t, y };
        }

        // Run simulation
        function runSimulation() {
            const params = {
                alphaMax: parseFloat(sliders.alphaMax.value),
                KI: parseFloat(sliders.enzymeControl.value),
                feedRate: parseFloat(sliders.feedRate.value),
                substrateFeedConc: parseFloat(sliders.substrateFeedConc.value),
                tFeed: parseFloat(sliders.feedTime.value),
                tMax: parseFloat(sliders.tMax.value)
            };

            const y0 = [
                initialConditions.X,
                initialConditions.Glu,
                initialConditions.Eta,
                initialConditions.E0,
                initialConditions.E1,
                initialConditions.E2,
                initialConditions.V
            ];

            const dt = 0.1; // Time step [h]
            const solution = solveRK4(odeSystem, 0, y0, params.tMax, dt, params);

            currentTimeData = solution.t;
            currentData = solution.y;

            // Calculate alpha at each time point
            currentAlphaData = currentData.map((state, i) => 
                calculateAlpha(state[1], params.alphaMax, params.KI)
            );
        }

        // Draw plots
        function drawPlots() {
            const plotType = plotSelect.value;

            if (!currentData) return;

            // Extract time series
            const t = currentTimeData;
            const X = currentData.map(state => state[0]);
            const Glu = currentData.map(state => state[1]);
            const Eta = currentData.map(state => state[2]);
            const E0 = currentData.map(state => state[3]);
            const E1 = currentData.map(state => state[4]);
            const E2 = currentData.map(state => state[5]);

            if (plotType === 'fed batch') {
                // Top plot: diauxic behavior
                const topTrace1 = { x: t, y: X, name: 'X(t)', line: {color: 'blue'} };
                const topTrace2 = { x: t, y: Glu, name: 'Glu(t)', line: {color: 'red'} };
                const topTrace3 = { x: t, y: Eta, name: 'Eta(t)', line: {color: 'green'} };

                Plotly.react('plot-top', [topTrace1, topTrace2, topTrace3], {
                    title: 'diauxic behavior',
                    xaxis: { title: 'time (h)' },
                    yaxis: { title: 'concentration [mol/m³]' },
                    margin: { t: 50, r: 20, b: 50, l: 60 }
                });

                // Bottom plot: enzyme concentration
                const bottomTrace1 = { x: t, y: E2, name: 'E₂(t)', line: {color: 'black'} };
                const bottomTrace2 = { x: t, y: E1, name: 'E₁(t)', line: {color: 'magenta'} };
                const bottomTrace3 = { x: t, y: E0, name: 'E₀(t)', line: {color: 'orange'} };

                Plotly.react('plot-bottom', [bottomTrace1, bottomTrace2, bottomTrace3], {
                    title: 'enzyme concentration',
                    xaxis: { title: 'time (h)' },
                    yaxis: { title: 'enzyme concentration [mol/m³]' },
                    margin: { t: 50, r: 20, b: 50, l: 60 }
                });
            } else if (plotType === 'α') {
                // Top plot: glucose concentration
                const topTrace1 = { x: t, y: Glu, name: 'Glu(t)', line: {color: 'red'} };

                Plotly.react('plot-top', [topTrace1], {
                    title: 'glucose concentration',
                    xaxis: { title: 'time (h)' },
                    yaxis: { title: 'glucose concentration [mol/m³]' },
                    margin: { t: 50, r: 20, b: 50, l: 60 }
                });

                // Bottom plot: fraction of respiring biomass
                const bottomTrace1 = { x: t, y: currentAlphaData, name: 'α', line: {color: 'blue'} };

                Plotly.react('plot-bottom', [bottomTrace1], {
                    title: 'fraction of respiring biomass',
                    xaxis: { title: 'time (h)' },
                    yaxis: { title: 'α', range: [0, 1] },
                    margin: { t: 50, r: 20, b: 50, l: 60 }
                });
            }
        }

        // Handle slider changes
        function handleSliderChange() {
            // Update feed time slider max to match t-max
            const tMax = parseFloat(sliders.tMax.value);
            sliders.feedTime.max = tMax;

            // Cap feed time if it exceeds new max
            if (parseFloat(sliders.feedTime.value) > tMax) {
                sliders.feedTime.value = tMax;
            }

            updateValueDisplays();
            runSimulation();
            drawPlots();
        }

        // Handle plot selector change
        function handlePlotChange() {
            drawPlots();
        }

        // Initialize
        function init() {
            updateValueDisplays();

            // Add event listeners
            Object.values(sliders).forEach(slider => {
                slider.addEventListener('input', handleSliderChange);
            });

            plotSelect.addEventListener('change', handlePlotChange);

            // Run initial simulation
            runSimulation();
            drawPlots();
        }

        // Start the application
        init();
    </script>
</body>
</html>
