    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let currentView = 'ground';
        let needsRender = true;

        // Three.js objects
        let sun, sunLight;
        let ground, celestialDome;
        let earth, earthAxis, terminator, latitudeParallel, dayNightParallel;
        let sunPathLine;
        let constellationsGroup;

        // Control elements
        const dateSlider = document.getElementById('slider-date');
        const latitudeSlider = document.getElementById('slider-latitude');
        const timeSlider = document.getElementById('slider-time');
        const viewSelect = document.getElementById('select-view');
        const dateLabel = document.getElementById('label-date');
        const latitudeLabel = document.getElementById('label-latitude');
        const timeLabel = document.getElementById('label-time');

        // Options
        const optionsGround = document.getElementById('options-ground');
        const optionsSpace = document.getElementById('options-space');
        const checkSunPath = document.getElementById('check-sun-path');
        const checkConstellationsDay = document.getElementById('check-constellations-day');
        const checkStationaryEarth = document.getElementById('check-stationary-earth');
        const checkConstellations = document.getElementById('check-constellations');
        const checkDayNightDivision = document.getElementById('check-day-night-division');
        const checkDayNightParallel = document.getElementById('check-day-night-parallel');

        // Initialize Three.js
        function init() {
            const container = document.getElementById('visualization-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x87CEEB);

            // Camera
            camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);

            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas'), antialias: true });
            renderer.setSize(width, height);
            renderer.shadowMap.enabled = true;
            renderer.shadowMap.type = THREE.PCFSoftShadowMap;

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040, 0.5);
            scene.add(ambientLight);

            // Sun
            sun = new THREE.Mesh(
                new THREE.SphereGeometry(1, 32, 32),
                new THREE.MeshBasicMaterial({ color: 0xFFFF00 })
            );
            sunLight = new THREE.DirectionalLight(0xFFFFFF, 1);
            sun.add(sunLight);
            sunLight.castShadow = true;
            sunLight.shadow.camera.near = 0.1;
            sunLight.shadow.camera.far = 100;
            sunLight.shadow.camera.left = -20;
            sunLight.shadow.camera.right = 20;
            sunLight.shadow.camera.top = 20;
            sunLight.shadow.camera.bottom = -20;
            scene.add(sun);

            // Create ground view objects
            createGroundView();

            // Create space view objects
            createSpaceView();

            // Create constellations
            createConstellations();

            // Set initial view
            setView('ground');

            // Update all calculations
            updateAll();
        }

        function createGroundView() {
            // Ground
            ground = new THREE.Mesh(
                new THREE.CylinderGeometry(10, 10, 1, 32),
                new THREE.MeshPhongMaterial({ color: 0x228B22 })
            );
            ground.position.y = -0.5;
            ground.receiveShadow = true;
            ground.userData.view = 'ground';

            // Celestial dome
            celestialDome = new THREE.Mesh(
                new THREE.SphereGeometry(15, 32, 32),
                new THREE.MeshPhongMaterial({ 
                    color: 0x000080, 
                    transparent: true, 
                    opacity: 0.3,
                    side: THREE.BackSide 
                })
            );
            celestialDome.userData.view = 'ground';
        }

        function createSpaceView() {
            // Earth
            earth = new THREE.Mesh(
                new THREE.SphereGeometry(5, 32, 32),
                new THREE.MeshPhongMaterial({ color: 0x4169E1 })
            );
            earth.castShadow = true;
            earth.receiveShadow = true;
            earth.userData.view = 'space';

            // Earth's axis
            earthAxis = new THREE.Line(
                new THREE.BufferGeometry().setFromPoints([
                    new THREE.Vector3(0, -7, 0),
                    new THREE.Vector3(0, 7, 0)
                ]),
                new THREE.LineBasicMaterial({ color: 0xFFFFFF })
            );
            earthAxis.userData.view = 'space';

            // Earth group (for tilt)
            const earthGroup = new THREE.Group();
            earthGroup.add(earth);
            earthGroup.add(earthAxis);
            earthGroup.rotation.z = -23.45 * Math.PI / 180;
            earthGroup.userData.view = 'space';

            // Terminator (day/night division)
            terminator = new THREE.Mesh(
                new THREE.PlaneGeometry(20, 20),
                new THREE.MeshBasicMaterial({ 
                    color: 0x000000, 
                    transparent: true, 
                    opacity: 0.3,
                    side: THREE.DoubleSide 
                })
            );
            terminator.userData.view = 'space';

            // Latitude parallel
            const latGeometry = new THREE.TorusGeometry(5, 0.05, 8, 100);
            latGeometry.rotateX(Math.PI / 2);
            latitudeParallel = new THREE.Mesh(
                latGeometry,
                new THREE.MeshBasicMaterial({ color: 0xFF0000 })
            );
            latitudeParallel.userData.view = 'space';

            // Day/night on parallel
            dayNightParallel = new THREE.Line(
                new THREE.BufferGeometry(),
                new THREE.LineBasicMaterial({ color: 0xFFFF00, linewidth: 3 })
            );
            dayNightParallel.userData.view = 'space';

            // Add to scene
            scene.add(earthGroup);
            scene.add(terminator);
            scene.add(latitudeParallel);
            scene.add(dayNightParallel);
        }

        function createConstellations() {
            constellationsGroup = new THREE.Group();

            // Simple constellation data (approximate)
            const constellations = [
                // Ursa Major (Big Dipper)
                [
                    new THREE.Vector3(10, 5, 10),
                    new THREE.Vector3(8, 6, 12),
                    new THREE.Vector3(6, 5, 14),
                    new THREE.Vector3(4, 6, 12),
                    new THREE.Vector3(2, 5, 10),
                    new THREE.Vector3(3, 3, 8),
                    new THREE.Vector3(5, 2, 6)
                ],
                // Orion
                [
                    new THREE.Vector3(-5, 8, 5),
                    new THREE.Vector3(-3, 7, 7),
                    new THREE.Vector3(-3, 5, 7),
                    new THREE.Vector3(-5, 4, 5),
                    new THREE.Vector3(-7, 5, 3),
                    new THREE.Vector3(-7, 7, 3),
                    new THREE.Vector3(-5, 6, 5)
                ]
            ];

            constellations.forEach(constellation => {
                const geometry = new THREE.BufferGeometry().setFromPoints(constellation);
                const line = new THREE.Line(geometry, new THREE.LineBasicMaterial({ color: 0xFFFFFF }));
                constellationsGroup.add(line);
            });

            scene.add(constellationsGroup);
        }

        function setView(view) {
            currentView = view;

            // Hide all view-specific objects
            scene.traverse(child => {
                if (child.userData.view) {
                    child.visible = child.userData.view === view;
                }
            });

            // Update camera position
            if (view === 'ground') {
                camera.position.set(0, 2, 10);
                camera.lookAt(0, 0, 0);
                controls.target.set(0, 0, 0);
                optionsGround.style.display = 'block';
                optionsSpace.style.display = 'none';

                // Add ground view objects
                if (!scene.getObjectById(ground.id)) scene.add(ground);
                if (!scene.getObjectById(celestialDome.id)) scene.add(celestialDome);
            } else {
                camera.position.set(0, 0, 30);
                camera.lookAt(0, 0, 0);
                controls.target.set(0, 0, 0);
                optionsGround.style.display = 'none';
                optionsSpace.style.display = 'block';

                // Remove ground view objects
                scene.remove(ground);
                scene.remove(celestialDome);
            }

            // Update constellation visibility
            updateConstellationVisibility();

            needsRender = true;
        }

        function updateConstellationVisibility() {
            if (currentView === 'ground') {
                constellationsGroup.visible = checkConstellationsDay.checked;
            } else {
                constellationsGroup.visible = checkConstellations.checked;
            }
        }

        // Astronomical calculations
        function dayOfYearToDate(day) {
            const months = [
                'January', 'February', 'March', 'April', 'May', 'June',
                'July', 'August', 'September', 'October', 'November', 'December'
            ];
            const daysInMonth = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

            let month = 0;
            let remaining = day;

            while (remaining > daysInMonth[month]) {
                remaining -= daysInMonth[month];
                month++;
            }

            return `${months[month]} ${remaining}`;
        }

        function calculateSolarDeclination(dayOfYear) {
            // Approximate calculation of solar declination
            return 23.45 * Math.sin((360 * (284 + dayOfYear) / 365) * Math.PI / 180);
        }

        function calculateDayLength(latitude, declination) {
            const latRad = latitude * Math.PI / 180;
            const decRad = declination * Math.PI / 180;

            const hourAngle = Math.acos(-Math.tan(latRad) * Math.tan(decRad));
            const dayLength = 2 * hourAngle * 12 / Math.PI;

            return Math.max(0, Math.min(24, dayLength));
        }

        function calculateSunriseSunset(latitude, declination, dayLength) {
            const sunrise = 12 - dayLength / 2;
            const sunset = 12 + dayLength / 2;

            return { sunrise, sunset };
        }

        function formatTime(hours) {
            const h = Math.floor(hours);
            const m = Math.floor((hours - h) * 60);
            return `${h.toString().padStart(2, '0')}:${m.toString().padStart(2, '0')}`;
        }

        function formatDuration(hours) {
            const h = Math.floor(hours);
            const m = Math.floor((hours - h) * 60);
            return `${h}h ${m.toString().padStart(2, '0')}m`;
        }

        function calculateSunPosition(time, latitude, declination) {
            const latRad = latitude * Math.PI / 180;
            const decRad = declination * Math.PI / 180;
            const hourAngle = (time - 12) * 15 * Math.PI / 180;

            const altitude = Math.asin(
                Math.sin(latRad) * Math.sin(decRad) + 
                Math.cos(latRad) * Math.cos(decRad) * Math.cos(hourAngle)
            );

            const azimuth = Math.atan2(
                Math.sin(hourAngle),
                Math.cos(hourAngle) * Math.sin(latRad) - Math.tan(decRad) * Math.cos(latRad)
            );

            return { altitude, azimuth };
        }

        function updateSunPath() {
            const latitude = parseFloat(latitudeSlider.value);
            const declination = calculateSolarDeclination(parseFloat(dateSlider.value));

            const points = [];
            for (let time = 0; time <= 24; time += 0.5) {
                const pos = calculateSunPosition(time, latitude, declination);
                if (pos.altitude > 0) {
                    const x = 10 * Math.cos(pos.altitude) * Math.sin(pos.azimuth);
                    const y = 10 * Math.sin(pos.altitude);
                    const z = 10 * Math.cos(pos.altitude) * Math.cos(pos.azimuth);
                    points.push(new THREE.Vector3(x, y, z));
                }
            }

            if (sunPathLine) {
                scene.remove(sunPathLine);
            }

            if (points.length > 1) {
                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                sunPathLine = new THREE.Line(geometry, new THREE.LineBasicMaterial({ color: 0xFFFF00 }));
                sunPathLine.userData.view = 'ground';
                scene.add(sunPathLine);
            }
        }

        function updateSunPosition() {
            const time = parseFloat(timeSlider.value);
            const latitude = parseFloat(latitudeSlider.value);
            const declination = calculateSolarDeclination(parseFloat(dateSlider.value));

            const pos = calculateSunPosition(time, latitude, declination);

            if (currentView === 'ground') {
                if (pos.altitude > 0) {
                    const x = 10 * Math.cos(pos.altitude) * Math.sin(pos.azimuth);
                    const y = 10 * Math.sin(pos.altitude);
                    const z = 10 * Math.cos(pos.altitude) * Math.cos(pos.azimuth);
                    sun.position.set(x, y, z);
                    sun.visible = true;
                } else {
                    sun.visible = false;
                }
            } else {
                // Space view - sun is fixed, earth rotates
                sun.position.set(20, 0, 0);
                sun.visible = true;

                // Rotate earth unless stationary
                if (!checkStationaryEarth.checked) {
                    const earthGroup = scene.children.find(child => child.userData.view === 'space' && child.type === 'Group');
                    if (earthGroup) {
                        earthGroup.rotation.y = (time - 12) * Math.PI / 12;
                    }
                }
            }

            // Update terminator position
            if (terminator) {
                terminator.position.copy(sun.position).normalize().multiplyScalar(10);
                terminator.lookAt(sun.position);
            }
        }

        function updateLatitudeParallel() {
            const latitude = parseFloat(latitudeSlider.value);
            const latRad = latitude * Math.PI / 180;

            if (latitudeParallel) {
                latitudeParallel.position.y = 5 * Math.sin(latRad);
                latitudeParallel.scale.y = Math.cos(latRad);
            }
        }

        function updateDayNightParallel() {
            const latitude = parseFloat(latitudeSlider.value);
            const declination = calculateSolarDeclination(parseFloat(dateSlider.value));
            const dayLength = calculateDayLength(latitude, declination);

            if (dayNightParallel) {
                const points = [];
                const latRad = latitude * Math.PI / 180;
                const radius = 5 * Math.cos(latRad);
                const y = 5 * Math.sin(latRad);

                const startAngle = -(dayLength / 24) * Math.PI;
                const endAngle = (dayLength / 24) * Math.PI;

                for (let angle = startAngle; angle <= endAngle; angle += 0.1) {
                    points.push(new THREE.Vector3(
                        radius * Math.sin(angle),
                        y,
                        radius * Math.cos(angle)
                    ));
                }

                dayNightParallel.geometry.setFromPoints(points);
            }
        }

        function updateReadouts() {
            const latitude = parseFloat(latitudeSlider.value);
            const declination = calculateSolarDeclination(parseFloat(dateSlider.value));
            const dayLength = calculateDayLength(latitude, declination);
            const nightLength = 24 - dayLength;
            const { sunrise, sunset } = calculateSunriseSunset(latitude, declination, dayLength);

            document.getElementById('readout-day-length').textContent = formatDuration(dayLength);
            document.getElementById('readout-night-length').textContent = formatDuration(nightLength);
            document.getElementById('readout-sunrise-time').textContent = formatTime(sunrise);
            document.getElementById('readout-sunset-time').textContent = formatTime(sunset);
            document.getElementById('readout-sunrise-direction').textContent = 'E';
            document.getElementById('readout-sunset-direction').textContent = 'W';
        }

        function updateAll() {
            // Update labels
            dateLabel.textContent = dayOfYearToDate(parseInt(dateSlider.value));
            latitudeLabel.textContent = `${latitudeSlider.value}°`;
            timeLabel.textContent = formatTime(parseFloat(timeSlider.value));

            // Update calculations
            updateReadouts();

            // Update visualization
            updateSunPath();
            updateSunPosition();
            updateLatitudeParallel();
            updateDayNightParallel();

            needsRender = true;
        }

        // Event listeners
        dateSlider.addEventListener('input', updateAll);
        latitudeSlider.addEventListener('input', updateAll);
        timeSlider.addEventListener('input', () => {
            timeLabel.textContent = formatTime(parseFloat(timeSlider.value));
            updateSunPosition();
            needsRender = true;
        });

        viewSelect.addEventListener('change', (e) => {
            setView(e.target.value);
            updateAll();
        });

        checkSunPath.addEventListener('change', (e) => {
            if (sunPathLine) sunPathLine.visible = e.target.checked;
            needsRender = true;
        });

        checkConstellationsDay.addEventListener('change', updateConstellationVisibility);
        checkConstellations.addEventListener('change', updateConstellationVisibility);

        checkStationaryEarth.addEventListener('change', () => {
            updateSunPosition();
            needsRender = true;
        });

        checkDayNightDivision.addEventListener('change', (e) => {
            if (terminator) terminator.visible = e.target.checked;
            needsRender = true;
        });

        checkDayNightParallel.addEventListener('change', (e) => {
            if (dayNightParallel) dayNightParallel.visible = e.target.checked;
            needsRender = true;
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);

            controls.update();

            if (needsRender) {
                renderer.render(scene, camera);
                needsRender = false;
            }
        }

        // Handle window resize
        window.addEventListener('resize', () => {
            const container = document.getElementById('visualization-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
            needsRender = true;
        });

        // Initialize
        init();
        animate();
    </script>
</body>
</html>
