<body>
    <div id="main-container">
        <h1>Unsteady-State Diffusion in a Slab</h1>
        <p>This demo visualizes the diffusion of a solute in a slab of thickness L = 0.004 m. The initial concentration profile is linear from c=0.001 kg/m³ at x=0 to c=0.002 kg/m³ at x=L. At t=0+, the surface concentrations are suddenly changed to c<sub>s</sub> = 0.004 kg/m³ and held constant. The diffusion process is governed by Fick's second law: ∂c/∂t = D(∂²c/∂x²).</p>

        <div id="control-panel">
            <div class="control-row">
                <label for="time-slider">time</label>
                <input type="range" id="time-slider" min="0" max="2500" step="1" value="100">
                <span id="time-value">100.0</span>
            </div>
            <div class="control-row">
                <label for="diffusivity-slider">diffusivity in m<sup>2</sup>/s</label>
                <input type="range" id="diffusivity-slider" min="1e-10" max="1e-8" step="1e-10" value="4e-9">
                <span id="diffusivity-value">4.0 x 10⁻⁹</span>
            </div>
        </div>

        <div id="plot-div"></div>
    </div>

    <script>
        // Constants
        const L = 0.004; // Slab thickness in meters
        const c_s = 4.0e-3; // Surface concentration in kg/m³
        const N = 100; // Number of terms in the series
        const num_points = 200; // Number of points for x array

        // Data revision counter for efficient Plotly updates
        let dataRevision = 0;

        // Pre-calculate k_n and B_n coefficients
        function calculateCoefficients() {
            const k_n = [];
            const B_n = [];

            for (let n = 0; n < N; n++) {
                k_n[n] = ((n + 0.5) * Math.PI) / L;
                const sign = Math.pow(-1, n);
                B_n[n] = (2 / L) * 1e-3 * ((250 * sign) / (k_n[n] * k_n[n]) - 3 / k_n[n]);
            }

            return { k_n, B_n };
        }

        const { k_n, B_n } = calculateCoefficients();

        // Function to format number in scientific notation
        function formatScientific(value) {
            const exp = Math.floor(Math.log10(Math.abs(value)));
            const mantissa = (value / Math.pow(10, exp)).toFixed(1);
            const expSign = exp < 0 ? '⁻' : '';
            const expDigits = Math.abs(exp).toString().split('').map(d => {
                const superscripts = { '0': '⁰', '1': '¹', '2': '²', '3': '³', '4': '⁴', '5': '⁵', '6': '⁶', '7': '⁷', '8': '⁸', '9': '⁹' };
                return superscripts[d] || d;
            }).join('');
            return `${mantissa} x 10${expSign}${expDigits}`;
        }

        // Function to calculate concentration profile
        function calculateConcentration(t, D) {
            const x = [];
            const c = [];

            // Generate x array
            for (let i = 0; i <= num_points; i++) {
                x[i] = (i / num_points) * L;
            }

            // Calculate concentration profile
            if (t === 0) {
                // Initial condition
                for (let i = 0; i <= num_points; i++) {
                    c[i] = (1.0 + 250 * x[i]) * 1e-3;
                }
            } else {
                // Series solution
                for (let i = 0; i <= num_points; i++) {
                    let u = 0;
                    for (let n = 0; n < N; n++) {
                        const term = B_n[n] * Math.sin(k_n[n] * x[i]) * Math.exp(-k_n[n] * k_n[n] * D * t);
                        u += term;
                    }
                    c[i] = u + c_s;
                }
            }

            return { x, c };
        }

        // Function to update the plot
        function updatePlot() {
            const t = parseFloat(document.getElementById('time-slider').value);
            const D = parseFloat(document.getElementById('diffusivity-slider').value);

            const { x, c } = calculateConcentration(t, D);

            const data = [{
                x: x,
                y: c,
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: '#007bff',
                    width: 2
                },
                name: 'concentration'
            }];

            const layout = {
                datarevision: dataRevision++,
                xaxis: {
                    title: 'position',
                    range: [0, 0.004],
                    tickformat: '.3f',
                    dtick: 0.001,
                    showgrid: true,
                    minor: { showgrid: true }
                },
                yaxis: {
                    title: 'concentration',
                    range: [0.001, 0.0042],
                    tickformat: '.4f',
                    dtick: 0.0005,
                    showgrid: true,
                    minor: { showgrid: true }
                },
                margin: {
                    l: 80,
                    r: 20,
                    t: 20,
                    b: 50
                },
                showlegend: false
            };

            Plotly.react('plot-div', data, layout);
        }

        // Initialize the interface
        function initialize() {
            const timeSlider = document.getElementById('time-slider');
            const diffusivitySlider = document.getElementById('diffusivity-slider');
            const timeValue = document.getElementById('time-value');
            const diffusivityValue = document.getElementById('diffusivity-value');

            // Set initial values
            timeValue.textContent = '100.0';
            diffusivityValue.textContent = formatScientific(4e-9);

            // Add event listeners
            timeSlider.addEventListener('input', function() {
                timeValue.textContent = parseFloat(this.value).toFixed(1);
                updatePlot();
            });

            diffusivitySlider.addEventListener('input', function() {
                diffusivityValue.textContent = formatScientific(parseFloat(this.value));
                updatePlot();
            });

            // Create initial plot
            updatePlot();
        }

        // Run initialization when page loads
        document.addEventListener('DOMContentLoaded', initialize);
    </script>
</body>
</html>
