            </div>

            <div id="type-controls" class="control-section">
                <span class="control-label">typespan>
                <div class="button-group">
                    <button class="type-btn" data-type="left">leftbutton>
                    <button class="type-btn active" data-type="right">rightbutton>
                    <button class="type-btn" data-type="midpoint">midpointbutton>
                    <button class="type-btn" data-type="overestimate">overestimatebutton>
                    <button class="type-btn" data-type="underestimate">underestimatebutton>
                    <button class="type-btn" data-type="trapezoidal">trapezoidalbutton>
                </div>
            </div>

            <div id="slider-a-controls" class="control-section">
                <div class="slider-container">
                    <label for="slider-a" class="slider-label">upper limit alabel>
                    <input type="range" id="slider-a" min="0.1" max="8" step="0.01" value="5">
                    <span id="value-a" class="value-display">5.00span>
                </div>
            </div>

            <div id="slider-n-controls" class="control-section">
                <div class="slider-container">
                    <label for="slider-n" class="slider-label">number of quadrilateralslabel>
                    <input type="range" id="slider-n" min="1" max="50" step="1" value="3">
                    <span id="value-n" class="value-display">3span>
                </div>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        // State management
        let state = {
            funcKey: 'x',
            type: 'right',
            a: 5,
            n: 3
        };

        // Function definitions
        const functions = {
            'x': {
                f: (x) => x,
                integral: (x) => x * x / 2,
                label: 'f(x) = x'
            },
            '(x-3)²': {
                f: (x) => Math.pow(x - 3, 2),
                integral: (x) => Math.pow(x - 3, 3) / 3,
                label: 'f(x) = (x-3)²'
            },
            '(x-3)³+27': {
                f: (x) => Math.pow(x - 3, 3) + 27,
                integral: (x) => Math.pow(x - 3, 4) / 4 + 27 * x,
                label: 'f(x) = (x-3)³+27'
            },
            '√x': {
                f: (x) => Math.sqrt(x),
                integral: (x) => 2 / 3 * Math.pow(x, 3 / 2),
                label: 'f(x) = √x'
            },
            'sin(x)+1': {
                f: (x) => Math.sin(x) + 1,
                integral: (x) => -Math.cos(x) + x,
                label: 'f(x) = sin(x)+1'
            },
            'eˣ/³': {
                f: (x) => Math.exp(x / 3),
                integral: (x) => 3 * Math.exp(x / 3),
                label: 'f(x) = eˣ/³'
            }
        };

        // p5.js setup
        let sketch = function (p) {
            let canvas;
            let xMin = -1, xMax = 8.5;
            let yMin, yMax;
            let padding = 50;

            p.setup = function () {
                let container = document.getElementById('canvas-container');
                let width = container.clientWidth;
                let height = 600;
                canvas = p.createCanvas(width, height);
                canvas.parent('canvas-container');
            };

            p.draw = function () {
                p.background(248, 248, 248);

                // Get current function
                const func = functions[state.funcKey];

                // Calculate y range
                calculateYRange(func);

                // Draw axes
                drawAxes();

                // Draw function curve
                drawFunctionCurve(func);

                // Draw estimation shapes
                drawEstimationShapes(func);

                // Draw upper limit marker
                drawMarker();

                // Display calculations
                displayCalculations(func);
            };

            function calculateYRange(func) {
                let samples = 100;
                let yValues = [];

                for (let i = 0; i <= samples; i++) {
                    let x = p.map(i, 0, samples, xMin, xMax);
                    if (x >= 0) {
                        yValues.push(func.f(x));
                    }
                }

                yMin = Math.min(...yValues);
                yMax = Math.max(...yValues);

                let yRange = yMax - yMin;
                yMin -= yRange * 0.1;
                yMax += yRange * 0.1;

                if (Math.abs(yMin - yMax) < 0.001) {
                    yMin -= 1;
                    yMax += 1;
                }
            }

            function toCanvasX(x) {
                return p.map(x, xMin, xMax, padding, p.width - padding);
            }

            function toCanvasY(y) {
                return p.map(y, yMin, yMax, p.height - padding, padding);
            }

            function drawAxes() {
                p.stroke('#cccccc');
                p.strokeWeight(1);

                // X-axis
                let xAxisY = toCanvasY(0);
                p.line(padding, xAxisY, p.width - padding, xAxisY);

                // Y-axis
                let yAxisX = toCanvasX(0);
                p.line(yAxisX, padding, yAxisX, p.height - padding);

                // X-axis ticks
                for (let x = 0; x <= 8; x++) {
                    let tickX = toCanvasX(x);
                    p.line(tickX, xAxisY - 5, tickX, xAxisY + 5);
                    p.noStroke();
                    p.fill('#666');
                    p.textAlign(p.CENTER);
                    p.textSize(12);
                    p.text(x.toString(), tickX, xAxisY + 20);
                    p.stroke('#cccccc');
                }

                // Y-axis ticks
                let numTicks = 6;
                for (let i = 0; i <= numTicks; i++) {
                    let y = p.map(i, 0, numTicks, yMin, yMax);
                    let tickY = toCanvasY(y);
                    p.line(yAxisX - 5, tickY, yAxisX + 5, tickY);
                    p.noStroke();
                    p.fill('#666');
                    p.textAlign(p.RIGHT);
                    p.textSize(12);
                    p.text(y.toFixed(1), yAxisX - 10, tickY + 4);
                    p.stroke('#cccccc');
                }
            }

            function drawFunctionCurve(func) {
                p.stroke(0);
                p.strokeWeight(3);
                p.noFill();

                p.beginShape();
                for (let x = xMin; x <= xMax; x += 0.01) {
                    if (x >= 0) {
                        let y = func.f(x);
                        p.vertex(toCanvasX(x), toCanvasY(y));
                    }
                }
                p.endShape();
            }

            function drawEstimationShapes(func) {
                let estimatedArea = 0;
                let deltaX = state.a / state.n;

                p.strokeWeight(1);
                p.stroke('#666');

                for (let i = 0; i < state.n; i++) {
                    let x1 = i * deltaX;
                    let x2 = (i + 1) * deltaX;
                    let shapePoints = [];
                    let area;

                    if (state.type === 'left') {
                        let y = func.f(x1);
                        shapePoints = [
                            toCanvasX(x1), toCanvasY(0),
                            toCanvasX(x1), toCanvasY(y),
                            toCanvasX(x2), toCanvasY(y),
                            toCanvasX(x2), toCanvasY(0)
                        ];
                        area = y * deltaX;
                    } else if (state.type === 'right') {
                        let y = func.f(x2);
                        shapePoints = [
                            toCanvasX(x1), toCanvasY(0),
                            toCanvasX(x1), toCanvasY(y),
                            toCanvasX(x2), toCanvasY(y),
                            toCanvasX(x2), toCanvasY(0)
                        ];
                        area = y * deltaX;
                    } else if (state.type === 'midpoint') {
                        let xMid = (x1 + x2) / 2;
                        let y = func.f(xMid);
                        shapePoints = [
                            toCanvasX(x1), toCanvasY(0),
                            toCanvasX(x1), toCanvasY(y),
                            toCanvasX(x2), toCanvasY(y),
                            toCanvasX(x2), toCanvasY(0)
                        ];
                        area = y * deltaX;
                    } else if (state.type === 'overestimate') {
                        let samples = 20;
                        let maxY = -Infinity;
                        for (let j = 0; j <= samples; j++) {
                            let x = p.map(j, 0, samples, x1, x2);
                            maxY = Math.max(maxY, func.f(x));
                        }
                        shapePoints = [
                            toCanvasX(x1), toCanvasY(0),
                            toCanvasX(x1), toCanvasY(maxY),
                            toCanvasX(x2), toCanvasY(maxY),
                            toCanvasX(x2), toCanvasY(0)
                        ];
                        area = maxY * deltaX;
                    } else if (state.type === 'underestimate') {
                        let samples = 20;
                        let minY = Infinity;
                        for (let j = 0; j <= samples; j++) {
                            let x = p.map(j, 0, samples, x1, x2);
                            minY = Math.min(minY, func.f(x));
                        }
                        shapePoints = [
                            toCanvasX(x1), toCanvasY(0),
                            toCanvasX(x1), toCanvasY(minY),
                            toCanvasX(x2), toCanvasY(minY),
                            toCanvasX(x2), toCanvasY(0)
                        ];
                        area = minY * deltaX;
                    } else if (state.type === 'trapezoidal') {
                        let y1 = func.f(x1);
                        let y2 = func.f(x2);
                        shapePoints = [
                            toCanvasX(x1), toCanvasY(0),
                            toCanvasX(x1), toCanvasY(y1),
                            toCanvasX(x2), toCanvasY(y2),
                            toCanvasX(x2), toCanvasY(0)
                        ];
                        area = (y1 + y2) * deltaX / 2;
                    }

                    // Calculate true integral for this interval
                    let trueIntervalArea = func.integral(x2) - func.integral(x1);

                    // Set fill color based on comparison
                    if (area > trueIntervalArea + 0.0001) {
                        p.fill(173, 216, 230, 0.7); // Light blue
                    } else {
                        p.fill(144, 238, 144, 0.7); // Light green
                    }

                    // Draw shape
                    if (state.type === 'trapezoidal') {
                        p.quad(...shapePoints);
                    } else {
                        p.rect(toCanvasX(x1), toCanvasY(0), toCanvasX(x2) - toCanvasX(x1), toCanvasY(0) - toCanvasY(func.f(x1)));
                    }

                    estimatedArea += area;
                }
            }

            function drawMarker() {
                p.stroke(255, 0, 0);
                p.strokeWeight(2);
                let markerX = toCanvasX(state.a);
                p.line(markerX, padding, markerX, p.height - padding);
            }

            function displayCalculations(func) {
                let estimatedArea = calculateEstimatedArea(func);
                let trueIntegral = func.integral(state.a) - func.integral(0);

                p.noStroke();
                p.fill(0);
                p.textSize(16);
                p.textAlign(p.CENTER);

                let textY = 30;
                p.text(`estimated area: ${estimatedArea.toFixed(5)}`, p.width / 2, textY);
                p.text(`integral: ${trueIntegral.toFixed(5)}`, p.width / 2, textY + 25);
            }

            function calculateEstimatedArea(func) {
                let estimatedArea = 0;
                let deltaX = state.a / state.n;

                for (let i = 0; i < state.n; i++) {
                    let x1 = i * deltaX;
                    let x2 = (i + 1) * deltaX;

                    if (state.type === 'left') {
                        estimatedArea += func.f(x1) * deltaX;
                    } else if (state.type === 'right') {
                        estimatedArea += func.f(x2) * deltaX;
                    } else if (state.type === 'midpoint') {
                        let xMid = (x1 + x2) / 2;
                        estimatedArea += func.f(xMid) * deltaX;
                    } else if (state.type === 'overestimate') {
                        let samples = 20;
                        let maxY = -Infinity;
                        for (let j = 0; j <= samples; j++) {
                            let x = p.map(j, 0, samples, x1, x2);
                            maxY = Math.max(maxY, func.f(x));
                        }
                        estimatedArea += maxY * deltaX;
                    } else if (state.type === 'underestimate') {
                        let samples = 20;
                        let minY = Infinity;
                        for (let j = 0; j <= samples; j++) {
                            let x = p.map(j, 0, samples, x1, x2);
                            minY = Math.min(minY, func.f(x));
                        }
                        estimatedArea += minY * deltaX;
                    } else if (state.type === 'trapezoidal') {
                        let y1 = func.f(x1);
                        let y2 = func.f(x2);
                        estimatedArea += (y1 + y2) * deltaX / 2;
                    }
                }

                return estimatedArea;
            }

            p.windowResized = function () {
                let container = document.getElementById('canvas-container');
                let width = container.clientWidth;
                canvas = p.resizeCanvas(width, 600);
            };
        };

        // Initialize p5.js
        new p5(sketch);

        // Event handlers
        document.addEventListener('DOMContentLoaded', function () {
            // Function button clicks
