<body>
    <div id="main-container">
        <div id="control-panel">
            <button id="btn-reset">×</button>
            <div class="slider-group">
                <label for="slider-current">current (A)</label>
                <input type="range" id="slider-current" min="0" max="1" step="0.001" value="0.431">
                <span id="value-current">0.431</span>
            </div>
            <div class="slider-group">
                <label for="slider-earth-field">horizontal Earth field (T)</label>
                <input type="range" id="slider-earth-field" min="0" max="0.0001" step="0.0000001" value="0.0000194">
                <span id="value-earth-field">1.94e-5</span>
            </div>
            <div class="slider-group">
                <label for="slider-radius">radius (m)</label>
                <input type="range" id="slider-radius" min="0.1" max="1.0" step="0.01" value="0.5">
                <span id="value-radius">0.50</span>
            </div>
            <div class="slider-group">
                <label for="slider-turns">number of turns</label>
                <input type="range" id="slider-turns" min="1" max="100" step="1" value="25">
                <span id="value-turns">25</span>
            </div>
        </div>
        <div id="viz-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        // Global variables for parameters
        let current = 0.431;
        let earthField = 0.0000194;
        let radius = 0.5;
        let turns = 25;

        // Physical constants
        const mu0 = 4 * Math.PI * 1e-7; // T*m/A

        // Elliptic integrals using AGM method
        function ellipticK(m) {
            if (m >= 1) return Infinity;
            if (m <= 0) return Math.PI / 2;

            let a = 1;
            let b = Math.sqrt(1 - m);
            let s = a;

            for (let i = 0; i < 10; i++) {
                let aNext = (a + b) / 2;
                let bNext = Math.sqrt(a * b);
                s -= a * (a - bNext) * (a - bNext);
                a = aNext;
                b = bNext;
            }

            return Math.PI / (2 * a);
        }

        function ellipticE(m) {
            if (m >= 1) return 1;
            if (m <= 0) return Math.PI / 2;

            let a = 1;
            let b = Math.sqrt(1 - m);
            let s = a * a;

            for (let i = 0; i < 10; i++) {
                let aNext = (a + b) / 2;
                let bNext = Math.sqrt(a * b);
                s -= 2 * i * (a * a - bNext * bNext) * Math.pow(2, -2 * i - 1);
                a = aNext;
                b = bNext;
            }

            return (Math.PI / (4 * a)) * s;
        }

        // Calculate magnetic field from a single loop
        function loopField(y, z, zOffset) {
            const R = radius;
            const I = current;
            const N = turns;
            const zp = z - zOffset;

            // Handle special case when y = 0
            if (Math.abs(y) < 1e-10) {
                const commonFactor = (mu0 * I * N) / (2 * Math.PI);
                const denom = Math.sqrt((R + 0) * (R + 0) + zp * zp + 1e-10);
                const m = (4 * R * 0) / ((R + 0) * (R + 0) + zp * zp);

                const K = ellipticK(m);
                const E = ellipticE(m);

                const By = 0;
                const Bz = commonFactor * (1 / denom) * (K - ((R * R - 0 - zp * zp) / ((R - 0) * (R - 0) + zp * zp + 1e-10)) * E);

                return { By, Bz };
            }

            const m = (4 * R * y) / ((R + y) * (R + y) + zp * zp);

            const commonFactor1 = (mu0 * I * N) / (2 * Math.PI);
            const commonFactor2 = 1 / Math.sqrt((R + y) * (R + y) + zp * zp);

            const K = ellipticK(m);
            const E = ellipticE(m);

            const By = commonFactor1 * (zp / y) * commonFactor2 * 
                      (((R * R + y * y + zp * zp) / ((R - y) * (R - y) + zp * zp)) * E - K);

            const Bz = commonFactor1 * commonFactor2 * 
                      (K - ((R * R - y * y - zp * zp) / ((R - y) * (R - y) + zp * zp)) * E);

            return { By, Bz };
        }

        // Calculate total magnetic field at a point
        function totalField(y, z) {
            // Field from coil at z = -R/2
            const field1 = loopField(y, z, -radius / 2);

            // Field from coil at z = +R/2
            const field2 = loopField(y, z, radius / 2);

            // Earth's field (pointing in Z direction)
            const B_earth_y = 0;
            const B_earth_z = earthField;

            // Total field
            const By = field1.By + field2.By + B_earth_y;
            const Bz = field1.Bz + field2.Bz + B_earth_z;

            return { By, Bz };
        }

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('p5-canvas');
        }

        function draw() {
            background(255);

            const bounds = 0.5;
            const stepSize = 0.01;
            const maxSteps = 100;

            // Calculate magnitude range
            let minMag = Infinity;
            let maxMag = 0;

            const gridSize = 20;
            const yStep = (2 * bounds) / gridSize;
            const zStep = (2 * bounds) / gridSize;

            for (let i = 0; i <= gridSize; i++) {
                for (let j = 0; j <= gridSize; j++) {
                    const y = -bounds + i * yStep;
                    const z = -bounds + j * zStep;
                    const field = totalField(y, z);
                    const mag = Math.sqrt(field.By * field.By + field.Bz * field.Bz);
                    minMag = Math.min(minMag, mag);
                    maxMag = Math.max(maxMag, mag);
                }
            }

            // Add small buffer to avoid division by zero in color mapping
            if (maxMag - minMag < 1e-10) {
                maxMag = minMag + 1e-10;
            }

            // Draw streamlines
            colorMode(HSB, 360, 100, 100);

            for (let i = 0; i <= gridSize; i++) {
                for (let j = 0; j <= gridSize; j++) {
                    const startY = -bounds + i * yStep;
                    const startZ = -bounds + j * zStep;

                    drawStreamline(startY, startZ, stepSize, maxSteps, minMag, maxMag, bounds);
                }
            }

            colorMode(RGB, 255);

            // Draw axes in red
            strokeWeight(2);
            stroke(255, 0, 0);

            // Y axis
            line(0, height/2, width, height/2);

            // Z axis
            line(width/2, 0, width/2, height);

            // Draw tick marks and labels
            textAlign(CENTER, CENTER);
            fill(255, 0, 0);
            noStroke();

            // Y axis ticks
            for (let i = -2; i <= 2; i++) {
                if (i !== 0) {
                    const x = map(i * 0.2, -bounds, bounds, 0, width);
                    stroke(255, 0, 0);
                    line(x, height/2 - 5, x, height/2 + 5);
                    noStroke();
                    text(i * 0.2, x, height/2 + 20);
                }
            }

            // Z axis ticks
            for (let i = -2; i <= 2; i++) {
                if (i !== 0) {
                    const y = map(i * 0.2, -bounds, bounds, height, 0);
                    stroke(255, 0, 0);
                    line(width/2 - 5, y, width/2 + 5, y);
                    noStroke();
                    text(i * 0.2, width/2 - 20, y);
                }
            }

            // Axis labels
            textSize(16);
            text('Y', width - 20, height/2);
            text('Z', width/2, 20);
        }

        function drawStreamline(startY, startZ, stepSize, maxSteps, minMag, maxMag, bounds) {
            strokeWeight(1);
            noFill();

            // Draw forward direction
            let points1 = [];
            let y = startY;
            let z = startZ;

            for (let step = 0; step < maxSteps; step++) {
                const field = totalField(y, z);
                const mag = Math.sqrt(field.By * field.By + field.Bz * field.Bz);

                if (mag < 1e-10) break;

                const normBy = field.By / mag;
                const normBz = field.Bz / mag;

                y += stepSize * normBy;
                z += stepSize * stepSize * normBz;

                if (Math.abs(y) > bounds || Math.abs(z) > bounds) break;

                points1.push({ y, z, mag });
            }

            // Draw backward direction
            let points2 = [];
            y = startY;
            z = startZ;

            for (let step = 0; step < maxSteps; step++) {
                const field = totalField(y, z);
                const mag = Math.sqrt(field.By * field.By + field.Bz * field.Bz);

                if (mag < 1e-10) break;

                const normBy = field.By / mag;
                const normBz = field.Bz / mag;

                y -= stepSize * normBy;
                z -= stepSize * normBz;

                if (Math.abs(y) > bounds || Math.abs(z) > bounds) break;

                points2.unshift({ y, z, mag });
            }

            // Combine all points
            const allPoints = [...points2, { y: startY, z: startZ, mag: 0 }, ...points1];

            // Draw the streamline
            for (let i = 1; i < allPoints.length; i++) {
                const p1 = allPoints[i - 1];
                const p2 = allPoints[i];

                // Map coordinates to canvas
                const x1 = map(p1.y, -bounds, bounds, 0, width);
                const y1 = map(p1.z, -bounds, bounds, height, 0);
                const x2 = map(p2.y, -bounds, bounds, 0, width);
                const y2 = map(p2.z, -bounds, bounds, height, 0);

                // Color based on magnitude
                const hue = map(p2.mag, minMag, maxMag, 60, 270);
                stroke(hue, 100, 100);

                line(x1, y1, x2, y2);

                // Draw arrow every 10 steps
                if (i % 10 === 0 && i < allPoints.length - 1) {
                    const angle = Math.atan2(y2 - y1, x2 - x1);
                    const arrowLength = 10;

                    push();
                    translate(x2, y2);
                    rotate(angle);

                    stroke(hue, 100, 100);
                    line(0, 0, -arrowLength/2, -arrowLength/3);
                    line(0, 0, -arrowLength/2, arrowLength/3);
                    pop();
                }
            }
        }

        function map(value, inMin, inMax, outMin, outMax) {
            return (value - inMin) * (outMax - outMin) / (inMax - inMin) + outMin;
        }

        // Event listeners
        document.getElementById('slider-current').addEventListener('input', function(e) {
            current = parseFloat(e.target.value);
            document.getElementById('value-current').textContent = current.toFixed(3);
        });

        document.getElementById('slider-earth-field').addEventListener('input', function(e) {
            earthField = parseFloat(e.target.value);
            const formatted = earthField >= 1e-4 ? earthField.toFixed(6) : earthField.toExponential(2);
            document.getElementById('value-earth-field').textContent = formatted;
        });

        document.getElementById('slider-radius').addEventListener('input', function(e) {
            radius = parseFloat(e.target.value);
            document.getElementById('value-radius').textContent = radius.toFixed(2);
        });

        document.getElementById('slider-turns').addEventListener('input', function(e) {
            turns = parseInt(e.target.value);
            document.getElementById('value-turns').textContent = turns;
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            // Reset sliders
            document.getElementById('slider-current').value = 0.431;
            document.getElementById('slider-earth-field').value = 0.0000194;
            document.getElementById('slider-radius').value = 0.5;
            document.getElementById('slider-turns').value = 25;

            // Reset global variables
            current = 0.431;
            earthField = 0.0000194;
            radius = 0.5;
            turns = 25;

            // Update displays
            document.getElementById('value-current').textContent = '0.431';
            document.getElementById('value-earth-field').textContent = '1.94e-5';
            document.getElementById('value-radius').textContent = '0.50';
            document.getElementById('value-turns').textContent = '25';
        });
    </script>
</body>
