<body>
    <header>
        <h1>Applying Graphics Filters Using Convolution</h1>
        <p>This demo demonstrates how convolution filters can be applied to pixelated images. Adjust the controls to see how different kernel sizes and filter types affect the output.</p>
    </header>

    <div id="control-panel">
        <div class="control-group">
            <label>convolver</label>
            <button onclick="adjustSlider('slider-convolver', -1)">−</button>
            <input type="range" id="slider-convolver" min="1" max="5" value="2" step="1">
            <button id="convolver-plus-btn" onclick="adjustSlider('slider-convolver', 1)">+</button>
        </div>

        <div class="control-group">
            <label>image size</label>
            <button onclick="adjustSlider('slider-image-size', -1)">−</button>
            <input type="range" id="slider-image-size" min="10" max="40" value="24" step="1">
            <button id="image-size-plus-btn" onclick="adjustSlider('slider-image-size', 1)">+</button>
        </div>

        <div class="control-group">
            <label>pick a letter:</label>
            <input type="text" id="input-letter" value="a" maxlength="1">
        </div>

        <div class="control-group">
            <label>filter</label>
            <div class="radio-group">
                <input type="radio" name="filter" id="radio-shadow" value="shadow" checked>
                <label for="radio-shadow" class="radio-label">shadow</label>
                <input type="radio" name="filter" id="radio-blur" value="blur">
                <label for="radio-blur" class="radio-label">blur</label>
            </div>
        </div>
    </div>

    <div id="visualization-area">
        <div class="image-container">
            <div class="image-box">
                <p>before</p>
                <div id="p5-container"></div>
            </div>
        </div>
    </div>

    <script>
        let beforeImage;
        let afterImage;
        let sketch;

        function setup() {
            sketch = new p5(p5Sketch, document.getElementById('p5-container'));

            // Add event listeners
            document.getElementById('slider-convolver').addEventListener('input', updateVisualization);
            document.getElementById('slider-image-size').addEventListener('input', updateVisualization);
            document.getElementById('input-letter').addEventListener('input', updateVisualization);
            document.getElementById('radio-shadow').addEventListener('change', updateVisualization);
            document.getElementById('radio-blur').addEventListener('change', updateVisualization);

            // Add arrow key support for letter input
            document.getElementById('input-letter').addEventListener('keydown', function(e) {
                if (e.key === 'ArrowUp' || e.key === 'ArrowDown') {
                    e.preventDefault();
                    const letter = this.value.toLowerCase();
                    let newLetter = String.fromCharCode(letter.charCodeAt(0) + (e.key === 'ArrowUp' ? 1 : -1));

                    // Wrap around alphabet
                    if (newLetter > 'z') newLetter = 'a';
                    if (newLetter < 'a') newLetter = 'z';

                    this.value = newLetter;
                    updateVisualization();
                }
            });

            // Initial render
            updateVisualization();
        }

        function p5Sketch(p) {
            p.setup = function() {
                p.createCanvas(450, 250);
                p.textAlign(p.CENTER, p.CENTER);
            };

            p.draw = function() {
                p.background(255);

                if (beforeImage && afterImage) {
                    drawImage(p, beforeImage, 25, 25, 200, binaryColor);
                    drawImage(p, afterImage, 225, 25, 200, grayscaleColor);
                }
            };
        }

        function binaryColor(value) {
            return value > 0.5 ? 0 : 255;
        }

        function grayscaleColor(value) {
            return 255 * (1 - value);
        }

        function drawImage(p, imageGrid, x, y, size, colorFunction) {
            const N = imageGrid.length;
            const cellSize = size / N;

            p.noStroke();
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    const value = imageGrid[i][j];
                    p.fill(colorFunction(value));
                    p.rect(x + j * cellSize, y + i * cellSize, cellSize, cellSize);
                }
            }
        }

        function updateVisualization() {
            const k = parseInt(document.getElementById('slider-convolver').value);
            const N = parseInt(document.getElementById('slider-image-size').value);
            const letter = document.getElementById('input-letter').value.toLowerCase() || 'a';
            const filter = document.querySelector('input[name="filter"]:checked').value;

            // Generate before image
            beforeImage = generateBeforeImage(N, letter);

            // Generate kernel
            const kernel = generateKernel(k, filter);

            // Apply convolution
            afterImage = applyConvolution(beforeImage, kernel);

            // Redraw
            sketch.redraw();
        }

        function generateBeforeImage(N, letter) {
            const image = Array(N).fill().map(() => Array(N).fill(0));

            // Create a temporary p5 graphics buffer
            const pg = sketch.createGraphics(N, N);
            pg.background(255);
            pg.fill(0);
            pg.noStroke();
            pg.textAlign(pg.CENTER, pg.CENTER);
            pg.textSize(N * 0.8);
            pg.text(letter, N/2, N/2);

            // Read pixel data
            pg.loadPixels();
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    const idx = (i * N + j) * 4;
                    const brightness = (pg.pixels[idx] + pg.pixels[idx + 1] + pg.pixels[idx + 2]) / 3;
                    image[i][j] = brightness < 128 ? 1 : 0;
                }
            }

            return image;
        }

        function generateKernel(k, filter) {
            const D = 2 * k + 1;
            const kernel = Array(D).fill().map(() => Array(D).fill(0));

            if (filter === 'blur') {
                const value = 1.0 / (D * D);
                for (let i = 0; i < D; i++) {
                    for (let j = 0; j < D; j++) {
                        kernel[i][j] = value;
                    }
                }
            } else if (filter === 'shadow') {
                const value = 1.0 / (k * k);
                for (let i = k; i < D; i++) {
                    for (let j = k; j < D; j++) {
                        kernel[i][j] = value;
                    }
                }
            }

            return kernel;
        }

        function applyConvolution(image, kernel) {
            const N = image.length;
            const D = kernel.length;
            const output = Array(N).fill().map(() => Array(N).fill(0));

            const k = Math.floor(D / 2);

            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    let sum = 0;

                    for (let ki = 0; ki < D; ki++) {
                        for (let kj = 0; kj < D; kj++) {
                            const ni = i + ki - k;
                            const nj = j + kj - k;

                            if (ni >= 0 && ni < N && nj >= 0 && nj < N) {
                                sum += image[ni][nj] * kernel[ki][kj];
                            }
                            // out-of-bounds pixels are treated as 0 (white)
                        }
                    }

                    output[i][j] = Math.min(1, Math.max(0, sum));
                }
            }

            return output;
        }

        function adjustSlider(sliderId, delta) {
            const slider = document.getElementById(sliderId);
            const newValue = parseInt(slider.value) + delta;
            const min = parseInt(slider.min);
            const max = parseInt(slider.max);

            if (newValue >= min && newValue <= max) {
                slider.value = newValue;
                slider.dispatchEvent(new Event('input'));
            }
        }
    </script>
</body>
</html>
