    <script>
        // Global variables
        let proteinData = [];
        let currentView = 'crystallography';
        let scene, camera, renderer, controls;
        let molecule = [];
        let boundingBox;
        let p5Instance;
        let distanceMatrix;

        // Amino acid codes and names mapping
        const aminoAcids = {
            'A': 'ALA', 'C': 'CYS', 'D': 'ASP', 'E': 'GLU', 'F': 'PHE',
            'G': 'GLY', 'H': 'HIS', 'I': 'ILE', 'K': 'LYS', 'L': 'LEU',
            'M': 'MET', 'N': 'ASN', 'P': 'PRO', 'Q': 'GLN', 'R': 'ARG',
            'S': 'SER', 'T': 'THR', 'V': 'VAL', 'W': 'TRP', 'Y': 'TYR'
        };

        // Initialize the application
        function init() {
            generateProteinData(400);
            setupControls();
            initThreeJS();
            updateInfoDisplay();
            animate();
        }

        // Generate synthetic protein data
        function generateProteinData(numResidues) {
            proteinData = [];
            let currentPos = new THREE.Vector3(0, 0, 0);
            let randomDirection = new THREE.Vector3(
                Math.random() - 0.5,
                Math.random() - 0.5,
                Math.random() - 0.5
            ).normalize();

            const aaKeys = Object.keys(aminoAcids);

            for (let i = 0; i < numResidues; i++) {
                const residue = {
                    resName: aminoAcids[aaKeys[Math.floor(Math.random() * aaKeys.length)]],
                    resSeq: i + 1,
                    atoms: []
                };

                randomDirection.add(new THREE.Vector3(
                    (Math.random() - 0.5) * 0.3,
                    (Math.random() - 0.5) * 0.3,
                    (Math.random() - 0.5) * 0.3
                )).normalize();

                currentPos.add(randomDirection.clone().multiplyScalar(3.8));

                // Add CA atom
                residue.atoms.push({
                    atomName: 'CA',
                    x: currentPos.x,
                    y: currentPos.y,
                    z: currentPos.z
                });

                // Add other atoms at fixed offsets
                const offsets = {
                    'N': new THREE.Vector3(-1.5, -1.0, 0),
                    'C': new THREE.Vector3(1.5, 1.0, 0),
                    'O': new THREE.Vector3(2.5, 1.0, -1.0),
                    'CB': new THREE.Vector3(-0.5, -1.5, 1.0)
                };

                for (const [name, offset] of Object.entries(offsets)) {
                    const atomPos = currentPos.clone().add(offset);
                    residue.atoms.push({
                        atomName: name,
                        x: atomPos.x,
                        y: atomPos.y,
                        z: atomPos.z
                    });
                }

                proteinData.push(residue);
            }

            // Pre-calculate distance matrix for contact map
            calculateDistanceMatrix();
        }

        // Calculate distance matrix for contact map
        function calculateDistanceMatrix() {
            distanceMatrix = [];
            for (let i = 0; i < proteinData.length; i++) {
                distanceMatrix[i] = [];
                for (let j = 0; j < proteinData.length; j++) {
                    const ca1 = proteinData[i].atoms.find(a => a.atomName === 'CA');
                    const ca2 = proteinData[j].atoms.find(a => a.atomName === 'CA');
                    const d = Math.sqrt(
                        Math.pow(ca1.x - ca2.x, 2) +
                        Math.pow(ca1.y - ca2.y, 2) +
                        Math.pow(ca1.z - ca2.z, 2)
                    );
                    distanceMatrix[i][j] = d;
                }
            }
        }

        // Setup control panel
        function setupControls() {
            // Populate residue selector
            const selector = document.getElementById('select-residue');
            const sortedAminoAcids = Object.keys(aminoAcids).sort();
            sortedAminoAcids.forEach(aa => {
                const option = document.createElement('option');
                option.value = aa;
                option.textContent = `${aa} - ${aminoAcids[aa]}`;
                selector.appendChild(option);
            });

            // View switcher
            document.getElementById('btn-crystallography').addEventListener('click', () => {
                switchView('crystallography');
            });

            document.getElementById('btn-contact-map').addEventListener('click', () => {
                switchView('contact-map');
            });

            // Slider event listeners
            setupSlider('gray-level', (value) => {
                updateThreeJSColors();
            });

            setupSlider('browse-sequence', (value) => {
                updateThreeJSHighlights();
                updateInfoDisplay();
            });

            setupSlider('residue-x', (value) => {
                updateThreeJSHighlights();
                if (currentView === 'contact-map') {
                    drawContactMap();
                }
                updateInfoDisplay();
            });

            setupSlider('residue-y', (value) => {
                updateThreeJSHighlights();
                if (currentView === 'contact-map') {
