                }
            });

            // Residue selector with arrow navigation
            let currentAAIndex = 0;
            selector.addEventListener('change', (e) => {
                const selectedAA = e.target.value;
                const currentPos = parseInt(document.getElementById('slider-browse-sequence').value) - 1;

                // Find next occurrence of selected amino acid
                for (let i = currentPos + 1; i < proteinData.length; i++) {
                    if (proteinData[i].resName.startsWith(selectedAA)) {
                        document.getElementById('slider-browse-sequence').value = i + 1;
                        document.getElementById('browse-sequence-value').textContent = i + 1;
                        updateThreeJSHighlights();
                        updateInfoDisplay();
                        return;
                    }
                }

                // Wrap around and find from beginning
                for (let i = 0; i < currentPos; i++) {
