        }

        // Setup slider with value display
        function setupSlider(sliderName, callback) {
            const slider = document.getElementById(`slider-${sliderName}`);
            const valueDisplay = document.getElementById(`${sliderName}-value`);

            slider.addEventListener('input', (e) => {
                const value = parseFloat(e.target.value);
                valueDisplay.textContent = value;
                callback(value);
            });
        }

        // Initialize Three.js
        function initThreeJS() {
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Camera
            camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
            camera.position.set(0, 0, 100);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(width, height);
            container.appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            // Render molecule
            renderMolecule();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        // Render molecule in Three.js
        function renderMolecule() {
            // Clear existing molecule
            molecule.forEach(atom => scene.remove(atom));
            molecule = [];

            const showLateralChains = document.getElementById('checkbox-lateral-chains').checked;
            const grayLevel = parseFloat(document.getElementById('slider-gray-level').value);

            proteinData.forEach((residue, resIndex) => {
