            {name: 'Mackenzie', lon: -135.0, lat: 67.5, found: false},
            {name: 'Amazon', lon: -50.0, lat: -2.0, found: false},
            {name: 'Congo', lon: 12.5, lat: -6.0, found: false},
            {name: 'Mississippi', lon: -89.0, lat: 29.5, found: false},
            {name: 'Parana', lon: -60.0, lat: -34.0, found: false},
            {name: 'Volga', lon: 47.5, lat: 46.0, found: false},
            {name: 'Danube', lon: 29.0, lat: 44.5, found: false},
            {name: 'Ganges', lon: 90.0, lat: 22.0, found: false},
            {name: 'Indus', lon: 67.5, lat: 24.0, found: false},
            {name: 'Orinoco', lon: -60.0, lat: 8.0, found: false},
            {name: 'Lena', lon: 127.0, lat: 72.5, found: false},
            {name: 'Amur', lon: 140.0, lat: 47.0, found: false},
            {name: 'Ob', lon: 73.0, lat: 67.0, found: false},
            {name: 'Pearl', lon: 113.5, lat: 23.0, found: false},
            {name: 'Salween', lon: 97.5, lat: 17.0, found: false},
            {name: 'Irrawaddy', lon: 95.0, lat: 16.5, found: false},
            {name: 'Dnieper', lon: 32.0, lat: 46.0, found: false},
            {name: 'Don', lon: 39.0, lat: 47.0, found: false},
            {name: 'Euphrates', lon: 48.0, lat: 30.0, found: false},
            {name: 'Tigris', lon: 49.0, lat: 31.0, found: false},
            {name: ' tagus', lon: -9.0, lat: 39.0, found: false},
            {name: 'Po', lon: 12.5, lat: 45.0, found: false},
            {name: 'Rhine', lon: 4.0, lat: 52.0, found: false},
            {name: 'Seine', lon: 0.0, lat: 49.0, found: false},
            {name: 'Thames', lon: 0.5, lat: 51.5, found: false},
            {name: 'Elbe', lon: 9.0, lat: 54.0, found: false},
            {name: 'Vistula', lon: 19.0, lat: 54.5, found: false},
            {name: 'Oder', lon: 14.5, lat: 53.5, found: false},
            {name: 'Rhône', lon: 4.5, lat: 43.5, found: false},
            {name: 'Dniester', lon: 30.5, lat: 46.0, found: false},
            {name: 'Bug', lon: 24.0, lat: 51.0, found: false}
        ];

        const lakesData = [
            {name: 'Onega', lon: 35.5, lat: 61.7, found: false},
            {name: 'Erie', lon: -81.2, lat: 42.2, found: false},
            {name: 'Huron', lon: -82.5, lat: 44.8, found: false},
            {name: 'Issyk-Kul', lon: 76.8, lat: 42.5, found: false},
            {name: 'Michigan', lon: -86.5, lat: 44.0, found: false},
            {name: 'Ladoga', lon: 31.0, lat: 60.8, found: false},
            {name: 'Winnipeg', lon: -97.0, lat: 52.0, found: false},
            {name: 'Baikal', lon: 104.0, lat: 53.0, found: false},
            {name: 'Tanganyika', lon: 30.0, lat: -6.0, found: false},
            {name: 'Superior', lon: -87.0, lat: 47.0, found: false},
            {name: 'Victoria', lon: 32.5, lat: -1.0, found: false},
            {name: 'Aral', lon: 60.0, lat: 45.0, found: false},
            {name: 'Titicaca', lon: -69.0, lat: -16.0, found: false},
            {name: 'Nicaragua', lon: -85.0, lat: 11.5, found: false},
            {name: 'Athabasca', lon: -110.0, lat: 59.0, found: false},
            {name: 'Reindeer', lon: -105.0, lat: 57.5, found: false},
            {name: 'Nettilling', lon: -68.5, lat: 66.5, found: false},
            {name: 'Winnipegosis', lon: -100.0, lat: 52.0, found: false},
            {name: 'Albert', lon: 31.0, lat: 1.5, found: false},
            {name: 'Kivu', lon: 29.5, lat: -2.0, found: false},
            {name: 'Edward', lon: 30.0, lat: -0.5, found: false},
            {name: 'Maracaibo', lon: -71.5, lat: 9.5, found: false},
            {name: 'Chad', lon: 14.5, lat: 13.5, found: false},
            {name: 'Eyre', lon: 138.0, lat: -28.5, found: false},
            {name: 'Torrens', lon: 138.5, lat: -30.5, found: false},
            {name: 'Gairdner', lon: 135.5, lat: -32.0, found: false},
            {name: 'Van', lon: 43.0, lat: 38.5, found: false}
        ];

        // Global variables
        let currentQuizType = 'rivers';
        let currentData = [...riversData];
        let worldMap;
        let showLocations = true;
        let showSome = false;
        let hintIndices = [];

        // p5.js sketch in instance mode
        const sketch = (p) => {
            p.preload = () => {
                worldMap = p.loadImage('https://upload.wikimedia.org/wikipedia/commons/c/c5/Blue_Marble_Next_Generation_%2B_topography_%2B_bathymetry.jpg');
            };

            p.setup = () => {
                const container = document.getElementById('canvas-container');
                const canvas = p.createCanvas(container.clientWidth, 500);
                canvas.parent('canvas-container');
                p.noLoop();
                populateTable();
                p.draw();
            };

            p.draw = () => {
                p.clear();
                p.image(worldMap, 0, 0, p.width, p.height);

                // Draw location markers if enabled
                if (showLocations) {
                    currentData.forEach(item => {
                        if (!item.found) {
                            const pos = mapToPixels(p, item.lon, item.lat);
                            p.fill('red');
                            p.noStroke();
                            p.circle(pos.x, pos.y, 10);
                        }
                    });
                }

                // Draw hint labels if enabled
                if (showSome) {
                    updateHintIndices();
                    hintIndices.forEach(index => {
                        const item = currentData[index];
                        if (!item.found) {
                            const pos = mapToPixels(p, item.lon, item.lat);
                            p.fill('white');
                            p.stroke('black');
                            p.strokeWeight(2);
                            p.textSize(12);
                            p.text(item.name, pos.x + 15, pos.y - 15);
                        }
                    });
                }

                // Draw cursor marker
                p.fill(173, 216, 230, 150);
                p.noStroke();
                p.circle(p.mouseX, p.mouseY, 30);

                // Update position display
                const geo = mapToGeo(p, p.mouseX, p.mouseY);
                document.getElementById('p-position').textContent = 
                    `Current position {${Math.round(geo.lon)}, ${Math.round(geo.lat)}}`;
            };

            p.mouseMoved = () => {
                p.draw();
            };

            p.mouseClicked = () => {
                // Check if click is on any unfound location
                for (let i = 0; i < currentData.length; i++) {
                    if (!currentData[i].found) {
                        const pos = mapToPixels(p, currentData[i].lon, currentData[i].lat);
                        const distance = p.dist(p.mouseX, p.mouseY, pos.x, pos.y);

                        if (distance < 15) {
                            currentData[i].found = true;
                            updateTableStatus(i, true);
                            p.draw();
                            break;
                        }
                    }
                }
            };
        };

        // Helper functions
        function mapToPixels(p, lon, lat) {
            const x = p.map(lon, -180, 180, 0, p.width);
            const y = p.map(lat, 90, -90, 0, p.height);
            return {x, y};
        }

        function mapToGeo(p, x, y) {
            const lon = p.map(x, 0, p.width, -180, 180);
            const lat = p.map(y, 0, p.height, 90, -90);
            return {lon, lat};
        }

        function populateTable() {
            const table = document.getElementById('table-quiz');
            table.innerHTML = '';

            // Header row
            const headerRow = table.insertRow();
            currentData.forEach(item => {
                const th = document.createElement('th');
                th.textContent = item.name;
                headerRow.appendChild(th);
            });

            // Status row
            const statusRow = table.insertRow();
            currentData.forEach((item, index) => {
                const td = document.createElement('td');
                td.textContent = 'False';
                td.id = `status-${index}`;
                statusRow.appendChild(td);
            });
        }

        function updateTableStatus(index, found) {
            const cell = document.getElementById(`status-${index}`);
            if (found) {
                cell.textContent = 'True';
                cell.classList.add('found');
            } else {
                cell.textContent = 'False';
                cell.classList.remove('found');
            }
        }

        function updateHintIndices() {
            const unfoundIndices = currentData.map((item, index) => item.found ? -1 : index)
                                            .filter(index => index !== -1);

            // Get random 3 indices
            hintIndices = [];
            const shuffled = [...unfoundIndices].sort(() => 0.5 - Math.random());
            hintIndices = shuffled.slice(0, 3);
        }

        function resetQuiz() {
            currentData.forEach(item => item.found = false);
            const cells = document.querySelectorAll('#table-quiz td');
            cells.forEach((cell, index) => {
                updateTableStatus(index, false);
            });
        }

        function switchQuizType(type) {
            currentQuizType = type;
            if (type === 'rivers') {
                currentData = [...riversData];
            } else {
                currentData = [...lakesData];
            }
            populateTable();
        }

        // Event listeners
        document.getElementById('radio-rivers').addEventListener('change', (e) => {
            if (e.target.checked) {
                switchQuizType('rivers');
                window._p5Instance.draw();
            }
        });

        document.getElementById('radio-lakes').addEventListener('change', (e) => {
            if (e.target.checked) {
                switchQuizType('lakes');
                window._p5Instance.draw();
            }
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            resetQuiz();
            window._p5Instance.draw();
        });

        document.getElementById('check-show-locations').addEventListener('change', (e) => {
            showLocations = e.target.checked;
            window._p5Instance.draw();
        });

        document.getElementById('check-show-some').addEventListener('change', (e) => {
            showSome = e.target.checked;
            window._p5Instance.draw();
        });

        // Initialize p5.js
        window._p5Instance = new p5(sketch);
    </script>
</body>
</html>
