                    <div class="radio-option">
                        <input type="radio" id="radio-chars-yes" name="chars-toggle" value="yes" checked>
                        <label for="radio-chars-yes">yes</label>
                    </div>
                </div>
            </div>
        </div>

        <div id="viz-container">
            <div id="header-section">
                <p id="original-text">MISSISSIPPI_RIVER</p>
                <p id="encoded-text">1100000101000101001001000011111010011011110101</p>
            </div>

            <div id="main-content">
                <div id="info-panel">
                    <p id="explanation-text">Once at the top, the total frequency becomes 17, which is the total number of characters.</p>
                    <div id="encoding-table">
                        <p>I = 00</p>
                        <p>S = 01</p>
                        <p>P = 100</p>
                        <p>R = 101</p>
                        <p>M = 1100</p>
                        <p>V = 1101</p>
                        <p>E = 1110</p>
                        <p>_ = 1111</p>
                    </div>
                </div>

                <div id="canvas-area"></div>
            </div>
        </div>
    </div>

    <script>
        // State variables
        let currentTier = 5;
        let showBinary = false;
        let showNodeChars = true;

        // Tree data structure
        const nodes = {
            'I': { id: 'I',  label: 'I', freq: 5, tier: 1, pos: {x: 100, y: 450}, children: [] },
            'S': { id: 'S',  label: 'S', freq: 4, tier: 1, pos: {x: 180, y: 450}, children: [] },
            'P': { id: 'P',  label: 'P', freq: 2, tier: 1, pos: {x: 260, y: 450}, children: [] },
            'R': { id: 'R',  label: 'R', freq: 2, tier: 1, pos: {x: 340, y: 450}, children: [] },
            'M': { id: 'M',  label: 'M', freq: 1, tier: 1, pos: {x: 420, y: 450}, children: [] },
            'V': { id: 'V',  label: 'V', freq: 1, tier: 1, pos: {x: 500, y: 450}, children: [] },
            'E': { id: 'E',  label: 'E', freq: 1, tier: 1, pos: {x: 580, y: 450}, children: [] },
            '_': { id: '_',  label: '_', freq: 1, tier: 1, pos: {x: 660, y: 450}, children: [] },
            'IS': { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: {x: 140, y: 350}, children: ['I', 'S'] },
            'PR': { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: {x: 300, y: 350}, children: ['P', 'R'] },
            'MV': { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: {x: 460, y: 350}, children: ['M', 'V'] },
            'E_': { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: {x: 620, y: 350}, children: ['E', '_'] },
            'MVE_': { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: {x: 540, y: 250}, children: ['MV', 'E_'] },
            'PRMVE_': { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: {x: 420, y: 150}, children: ['PR', 'MVE_'] },
            'ROOT': { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] }
        };

        // Find parent of a node
        function findParent(childId) {
            for (let nodeId in nodes) {
                if (nodes[nodeId].children.includes(childId)) {
                    return nodes[nodeId];
                }
            }
            return null;
        }

        // p5.js setup
        let sketch = function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(800, 500);
                canvas.parent('canvas-area');
                drawTree();
            };

            p.draw = function() {
                p.clear();
                drawTree();
            };

            function drawTree() {
                // Draw edges first
                for (let nodeId in nodes) {
                    let node = nodes[nodeId];
                    let parent = findParent(nodeId);

                    if (parent && parent.tier <= currentTier && node.tier <= currentTier) {
                        p.stroke('#C70000');
                        p.strokeWeight(2);
                        p.line(parent.pos.x, parent.pos.y, node.pos.x, node.pos.y);

                        // Draw binary label if enabled
                        if (showBinary) {
                            let midX = (parent.pos.x + node.pos.x) / 2;
                            let midY = (parent.pos.y + node.pos.y) / 2;

                            p.noStroke();
                            p.fill('#333');
                            p.textAlign(p.CENTER, p.CENTER);
                            p.textSize(16);
                            p.textStyle(p.BOLD);

                            let binaryLabel = parent.children[0] === nodeId ? '0' : '1';
                            p.text(binaryLabel, midX, midY);
                        }
                    }
                }

                // Draw nodes
                for (let nodeId in nodes) {
                    let node = nodes[nodeId];

                    if (node.tier <= currentTier) {
                        // Draw node rectangle
                        p.fill('#FEFDD5');
                        p.stroke('#C70000');
                        p.strokeWeight(2);
                        p.rectMode(p.CENTER);
                        p.rect(node.pos.x, node.pos.y, 80, 40, 8);

                        // Draw node label
                        p.noStroke();
                        p.fill('#333');
                        p.textAlign(p.CENTER, p.CENTER);
                        p.textSize(14);

                        let labelText = '';
                        if (node.children.length === 0 || showNodeChars) {
                            labelText = node.label + '(' + node.freq + ')';
                        } else {
                            labelText = '(' + node.freq + ')';
                        }

                        p.text(labelText, node.pos.x, node.pos.y);
                    }
                }
            }
        };

        let p5Instance = new p5(sketch);

        // Event listeners
        // Tier buttons
        document.querySelectorAll('.tier-button').forEach(button => {
            button.addEventListener('click', function() {
                // Update active state
                document.querySelectorAll('.tier-button').forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');

                // Update tier state
                currentTier = parseInt(this.textContent);

                // Update explanation text
                const explanationText = document.getElementById('explanation-text');
                if (currentTier === 1) {
                    explanationText.textContent = "After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.";
                } else if (currentTier === 2 || currentTier === 3 || currentTier === 4) {
                    explanationText.textContent = "We continue grouping until we get to the highest tier.";
                } else if (currentTier === 5) {
                    explanationText.textContent = "Once at the top, the total frequency becomes 17, which is the total number of characters.";
                }

                // Update encoding table visibility
                const encodingTable = document.getElementById('encoding-table');
                if (currentTier >= 2) {
                    encodingTable.classList.remove('hidden');
                } else {
                    encodingTable.classList.add('hidden');
                }

                // Update encoded text visibility
                const encodedText = document.getElementById('encoded-text');
                if (currentTier === 5) {
                    encodedText.classList.remove('hidden');
                } else {
                    encodedText.classList.add('hidden');
                }
            });
        });

        // Show binary checkbox
        document.getElementById('checkbox-binary').addEventListener('change', function() {
            showBinary = this.checked;
            p5Instance.redraw();
        });

        // Character radio buttons
        document.querySelectorAll('input[name="chars-toggle"]').forEach(radio => {
            radio.addEventListener('change', function() {
                showNodeChars = this.value === 'yes';
                p5Instance.redraw();
            });
        });
    </script>
</body>
</html>
