    <script>
        // Color array for rectangles
        const colors = [
            '#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa', 
            '#6495ed', '#9370db', '#8a2be2', '#ff69b4', '#ff6347',
            '#ffa500', '#ffd700', '#90ee90', '#add8e6', '#dda0dd',
            '#f0e68c', '#98d8c8', '#87ceeb', '#da70d6', '#ffb6c1'
        ];

        // Global variable for n
        let nValue = 1;

        // Sync slider and number input
        const slider = document.getElementById('slider-n');
        const input = document.getElementById('input-n');

        slider.addEventListener('input', function() {
            nValue = parseInt(this.value);
            input.value = nValue;
            window.MathJax.typesetPromise();
        });

        input.addEventListener('input', function() {
            let value = parseInt(this.value);
            if (isNaN(value)) value = 1;
            if (value < 1) value = 1;
            if (value > 20) value = 20;

            nValue = value;
            this.value = nValue;
            slider.value = nValue;
        });

        // Function plot sketch
        const functionSketch = function(p) {
            let canvas;
            const xMin = 0, xMax = 10.5;
            const yMin = 0, yMax = 2.2;

            p.setup = function() {
                canvas = p.createCanvas(p.pixelDensity() * 600, p.pixelDensity() * 400);
                canvas.parent('canvas-function');
                p.resizeCanvas(600, 400);
            };

            p.draw = function() {
                p.background(255);

                // Set up coordinate transformation
                p.push();
                p.translate(50, p.height - 50);
                p.scale((p.width - 100) / (xMax - xMin), -(p.height - 100) / (yMax - yMin));

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(0, 0, xMax, 0);  // x-axis
                p.line(0, 0, 0, yMax);   // y-axis

                // Draw tick marks and labels
                p.textFont('Arial');
                p.textSize(12);
                p.textAlign(p.CENTER, p.TOP);

                // X-axis ticks
                for (let i = 1; i <= 10; i++) {
                    p.line(i, -0.05, i, 0.05);
                    p.text(i, i, 0.1);
                }

                // Y-axis ticks
                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 1; i <= 2; i++) {
                    p.line(-0.1, i, 0.1, i);
                    p.text(i, -0.15, i);
                }

                // Draw function y = 2/x
                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (let x = 0.5; x <= 10; x += 0.01) {
                    let y = 2 / x;
                    if (y <= yMax) {
                        p.vertex(x, y);
                    }
                }
                p.endShape();

                // Draw rectangles
                for (let k = 1; k <= nValue; k++) {
                    if (k + 1 > xMax) break;

                    let x1 = k;
                    let x2 = k + 1;
                    let y1 = 2 / (k + 1);
                    let y2 = 2 / k;

                    p.fill(colors[k - 1]);
                    p.noStroke();
                    p.rect(x1, y1, x2 - x1, y2 - y1);

                    // Draw vertical dashed lines for k <= 3
                    if (k <= 3) {
                        p.stroke(150);
                        p.strokeWeight(1);
                        p.setLineDash([5, 5]);
                        p.line(k, 0, k, y2);
                        p.setLineDash([]);
                    }
                }

                p.pop();
            };

            p.setLineDash = function(list) {
                p.drawingContext.setLineDash(list);
            };
        };

        // Summation plot sketch
        const sumSketch = function(p) {
            let canvas;
            const xMin = 0, xMax = 1.5;
            const yMin = 0, yMax = 2.2;

            p.setup = function() {
                canvas = p.createCanvas(p.pixelDensity() * 300, p.pixelDensity() * 400);
                canvas.parent('canvas-sum');
                p.resizeCanvas(300, 400);
            };

            p.draw = function() {
                p.background(255);

                // Set up coordinate transformation
                p.push();
                p.translate(50, p.height - 50);
                p.scale((p.width - 100) / (xMax - xMin), -(p.height - 100) / (yMax - yMin));

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(0, 0, xMax, 0);  // x-axis
                p.line(0, 0, 0, yMax);   // y-axis

                // Draw tick marks and labels
                p.textFont('Arial');
                p.textSize(12);
                p.textAlign(p.CENTER, p.TOP);

                // X-axis ticks
                p.text(0, 0, 0.1);
                p.text(1, 1, 0.1);

                // Y-axis ticks
                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 1; i <= 2; i++) {
                    p.line(-0.1, i, 0.1, i);
                    p.text(i, -0.15, i);
                }

                // Draw stacked rectangles
                let yStack = 0;
                for (let k = nValue; k >= 1; k--) {
                    let h = 2 / k - 2 / (k + 1);

                    p.fill(colors[k - 1]);
                    p.noStroke();
                    p.rect(0, yStack, 1, h);

                    yStack += h;
                }

                p.pop();
            };
        };

        // Create p5.js instances
        new p5(functionSketch);
        new p5(sumSketch);
    </script>
