<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="button-group">
                <h3>Model</h3>
                <button id="btn-monod" class="active">Monod</button>
                <button id="btn-si">substrate inhibition</button>
            </div>

            <div class="button-group">
                <h3>View</h3>
                <button id="btn-bifurcation" class="active">bifurcation diagram</button>
                <button id="btn-nontrivial">nontrivial steady state</button>
            </div>

            <div class="slider-container">
                <label for="slider-x2f">x<sub>2f</sub></label>
                <input type="range" id="slider-x2f" min="1" max="10" step="0.1" value="4">
                <span id="value-x2f">4.0</span>
            </div>

            <div class="slider-container">
                <label for="slider-umax">μ<sub>max</sub></label>
                <input type="range" id="slider-umax" min="0.1" max="2" step="0.01" value="0.6">
                <span id="value-umax">0.60</span>
            </div>

            <div class="slider-container">
                <label for="slider-km">k<sub>m</sub></label>
                <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
                <span id="value-km">0.12</span>
            </div>

            <div class="slider-container hidden" id="slider-k1-container">
                <label for="slider-k1">k<sub>1</sub></label>
                <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
                <span id="value-k1">0.4545</span>
            </div>

            <div class="slider-container">
                <label for="slider-ds">D<sub>s</sub></label>
                <input type="range" id="slider-ds" min="0.01" max="1.0" step="0.01" value="0.6">
                <span id="value-ds">0.60</span>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Global state variables
        let currentModel = 'monod';
        let currentView = 'bifurcation';

        // Parameter values object
        const params = {
            x2f: 4.0,
            umax: 0.6,
            km: 0.12,
            k1: 0.4545,
            ds: 0.6
        };

        // Slider configurations
        const sliderConfigs = {
            'slider-x2f': { param: 'x2f', decimals: 1 },
            'slider-umax': { param: 'umax', decimals: 2 },
            'slider-km': { param: 'km', decimals: 2 },
            'slider-k1': { param: 'k1', decimals: 4 },
            'slider-ds': { param: 'ds', decimals: 2 }
        };

        // Initialize sliders and event listeners
        function initializeSliders() {
            Object.keys(sliderConfigs).forEach(sliderId => {
                const slider = document.getElementById(sliderId);
                const valueSpan = document.getElementById(`value-${sliderId.replace('slider-', '')}`);
                const config = sliderConfigs[sliderId];

                slider.addEventListener('input', (e) => {
                    const value = parseFloat(e.target.value);
                    params[config.param] = value;
                    valueSpan.textContent = value.toFixed(config.decimals);
                    updatePlot();
                });

                // Set initial value display
                valueSpan.textContent = slider.value;
            });
        }

        // Initialize button event listeners
        function initializeButtons() {
            document.getElementById('btn-monod').addEventListener('click', () => {
                currentModel = 'monod';
                updateActiveButton('btn-monod', 'btn-si');
                document.getElementById('slider-k1-container').classList.add('hidden');
                updatePlot();
            });

            document.getElementById('btn-si').addEventListener('click', () => {
                currentModel = 'si';
                updateActiveButton('btn-si', 'btn-monod');
                document.getElementById('slider-k1-container').classList.remove('hidden');
                updatePlot();
            });

            document.getElementById('btn-bifurcation').addEventListener('click', () => {
                currentView = 'bifurcation';
                updateActiveButton('btn-bifurcation', 'btn-nontrivial');
                updatePlot();
            });

            document.getElementById('btn-nontrivial').addEventListener('click', () => {
                currentView = 'nontrivial';
                updateActiveButton('btn-nontrivial', 'btn-bifurcation');
                updatePlot();
            });
        }

        // Update active button styling
        function updateActiveButton(activeId, inactiveId) {
            document.getElementById(activeId).classList.add('active');
            document.getElementById(inactiveId).classList.remove('active');
        }

        // Calculate D for Monod model
        function monodD(x2) {
            return params.umax * x2 / (params.km + x2);
        }

        // Calculate D for Substrate Inhibition model
        function siD(x2) {
            return params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
        }

        // Calculate μ for Substrate Inhibition model
        function siMu(x2) {
            return params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
        }

        // Numerical solver for D(x2) = D_s
        function solveDx2(f, D_s, x_min, x_max, tol = 1e-6) {
            const steps = 1000;
            const dx = (x_max - x_min) / steps;
            const solutions = [];

            for (let i = 0; i < steps - 1; i++) {
                const x1 = x_min + i * dx;
                const x2 = x_min + (i + 1) * dx;
                const y1 = f(x1) - D_s;
                const y2 = f(x2) - D_s;

                if (y1 * y2 < 0) {
                    // Binary search for better accuracy
                    let a = x1;
                    let b = x2;
                    while (b - a > tol) {
                        const mid = (a + b) / 2;
                        if ((f(mid) - D_s) * (f(a) - D_s) > 0) {
                            a = mid;
                        } else {
                            b = mid;
                        }
                    }
                    solutions.push((a + b) / 2);
                }
            }

            return solutions;
        }

        // Draw bifurcation diagram
        function drawBifurcationDiagram() {
            const traces = [];
            const x2Values = [];
            const dValues = [];

            // Generate nontrivial steady state curve
            for (let x2 = 0; x2 <= params.x2f; x2 += params.x2f / 200) {
                x2Values.push(x2);
                const d = currentModel === 'monod' ? monodD(x2) : siD(x2);
                dValues.push(d);
            }

            if (currentModel === 'si') {
                // For SI model, split at critical point
                const x2Crit = Math.sqrt(params.km / params.k1);
                const critIndex = x2Values.findIndex(x2 => x2 >= x2Crit);

                if (critIndex > 0) {
                    // Stable part (solid blue)
                    traces.push({
                        x: dValues.slice(0, critIndex),
                        y: x2Values.slice(0, critIndex),
                        type: 'scatter',
                        mode: 'lines',
                        line: { color: 'blue', width: 2 },
                        name: 'Stable nontrivial',
                        showlegend: false
                    });

                    // Unstable part (dashed red)
                    traces.push({
                        x: dValues.slice(critIndex),
                        y: x2Values.slice(critIndex),
                        type: 'scatter',
                        mode: 'lines',
                        line: { color: 'red', width: 2, dash: 'dash' },
                        name: 'Unstable nontrivial',
                        showlegend: false
                    });
                }
            } else {
                // Monod model - always stable
                traces.push({
                    x: dValues,
                    y: x2Values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2 },
                    name: 'Nontrivial steady state',
                    showlegend: false
                });
            }

            // Trivial steady state line
            const d_bif = currentModel === 'monod' ? monodD(params.x2f) : siD(params.x2f);
            const dLine = [];

            for (let d = 0; d <= 1; d += 0.01) {
                dLine.push(d);
            }

            // Unstable part of trivial line
            const unstableD = dLine.filter(d => d < d_bif);
            const unstableY = unstableD.map(() => params.x2f);
            traces.push({
                x: unstableD,
                y: unstableY,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'red', width: 2, dash: 'dash' },
                name: 'Unstable trivial',
                showlegend: false
            });

            // Stable part of trivial line
            const stableD = dLine.filter(d => d >= d_bif);
            const stableY = stableD.map(() => params.x2f);
            traces.push({
                x: stableD,
                y: stableY,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 2 },
                name: 'Stable trivial',
                showlegend: false
            });

            // Find and plot intersection points
            const f = currentModel === 'monod' ? monodD : siD;
            const solutions = solveDx2(f, params.ds, 0, params.x2f);

            solutions.forEach(x2 => {
                const dDdx2 = (f(x2 + 0.001) - f(x2 - 0.001)) / 0.002;
                const color = dDdx2 > 0 ? 'magenta' : 'cyan';
                traces.push({
                    x: [params.ds],
                    y: [x2],
                    type: 'scatter',
                    mode: 'markers',
                    marker: { color: color, size: 10 },
                    name: 'Intersection',
                    showlegend: false
                });
            });

            // Plot trivial solution point
            traces.push({
                x: [params.ds],
                y: [params.x2f],
                type: 'scatter',
                mode: 'markers',
                marker: { color: 'green', size: 10 },
                name: 'Trivial solution',
                showlegend: false
            });

            const layout = {
                xaxis: { title: 'dilution rate', range: [0, 1.0] },
                yaxis: { title: 'X₂', range: [0, 6.0] },
                shapes: [{
                    type: 'line',
                    x0: params.ds,
                    x1: params.ds,
                    y0: 0,
                    y1: 6.0,
                    line: { color: 'gray', width: 1, dash: 'dot' }
                }],
                margin: { t: 20, r: 20, b: 40, l: 60 }
            };

            Plotly.newPlot('plot-container', traces, layout, { responsive: true });
        }

        // Draw nontrivial steady state plot
        function drawNontrivialStatePlot() {
            const traces = [];
            const x2Values = [];
            const muValues = [];

            // Generate μ(x2) curve
            for (let x2 = 0; x2 <= 7; x2 += 7 / 200) {
                x2Values.push(x2);
                muValues.push(siMu(x2));
            }

            traces.push({
                x: x2Values,
                y: muValues,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 2 },
                name: 'μ(x₂)',
                showlegend: false
            });

            // Horizontal line at D_s
            traces.push({
                x: [0, 7],
                y: [params.ds, params.ds],
                type: 'scatter',
                mode: 'lines',
                line: { color: 'gray', width: 2 },
                name: 'D_s',
                showlegend: false
            });

            // Calculate and draw bistability region
            const dUpper = params.umax / (1 + 2 * Math.sqrt(params.k1 * params.km));
            const dLower = siMu(params.x2f);

            // Upper bound
            traces.push({
                x: [0, 7],
                y: [dUpper, dUpper],
                type: 'scatter',
                mode: 'lines',
                line: { color: 'red', width: 2, dash: 'dash' },
                name: 'D_upper',
                showlegend: false
            });

            // Lower bound
            traces.push({
                x: [0, 7],
                y: [dLower, dLower],
                type: 'scatter',
                mode: 'lines',
                line: { color: 'red', width: 2, dash: 'dash' },
                name: 'D_lower',
                showlegend: false
            });

            // Shaded region for bistability
            traces.push({
                x: [0, 7, 7, 0],
                y: [dLower, dLower, dUpper, dUpper],
                type: 'scatter',
                mode: 'none',
                fill: 'toself',
                fillcolor: 'rgba(255, 165, 0, 0.2)',
                name: 'Bistability region',
                showlegend: false
            });

            // Find intersection points
            const solutions = solveDx2(siMu, params.ds, 0, 7);

            solutions.forEach(x2 => {
                // Check if on rising or falling part by checking derivative
                const dMuDx2 = (siMu(x2 + 0.001) - siMu(x2 - 0.001)) / 0.002;
                const color = dMuDx2 > 0 ? 'magenta' : 'cyan';
                traces.push({
                    x: [x2],
                    y: [params.ds],
                    type: 'scatter',
                    mode: 'markers',
                    marker: { color: color, size: 10 },
                    name: 'Intersection',
                    showlegend: false
                });
            });

            const layout = {
                xaxis: { title: 'substrate concentration', range: [0, 7] },
                yaxis: { title: 'specific growth rate coefficient', range: [0, 1] },
                margin: { t: 20, r: 20, b: 40, l: 60 }
            };

            Plotly.newPlot('plot-container', traces, layout, { responsive: true });
        }

        // Main plot update function
        function updatePlot() {
            if (currentView === 'bifurcation') {
                drawBifurcationDiagram();
            } else {
                drawNontrivialStatePlot();
            }
        }

        // Initialize everything on page load
        window.addEventListener('load', () => {
            initializeSliders();
            initializeButtons();

            // Set initial state
            document.getElementById('btn-monod').classList.add('active');
            document.getElementById('btn-bifurcation').classList.add('active');
            document.getElementById('slider-k1-container').classList.add('hidden');

            // Initialize plot
            updatePlot();
        });
    </script>
</body>
</html>
