            </div>

            <div class="slider-group">
                <label for="slider-time">time of day</label>
                <input type="range" id="slider-time" min="0" max="24" step="0.01" value="12">
                <span id="value-time">12.00</span>
            </div>
        </div>

        <div id="main-content">
            <div id="globe-container"></div>

            <div id="plots-container">
                <div id="plot-instant"></div>
                <div id="plot-legend">
                    <div class="legend-item">
                        <div class="legend-line red"></div>
                        <span>Equator</span>
                    </div>
                    <div class="legend-item">
                        <div class="legend-line black"></div>
                        <span>Selected Latitude</span>
                    </div>
                    <div class="legend-item">
                        <div class="legend-line blue"></div>
                        <span>North Pole</span>
                    </div>
                </div>
                <div id="plot-cumulative"></div>
            </div>
        </div>
    </div>

    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let earthMesh, sunLight, ambientLight;
        let selectedLatitudeLine, surfaceNormalVector, sunVector;
        let polarAxisVector;

        // Initialize Three.js scene
        function initThreeJS() {
            const container = document.getElementById('globe-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000011);

            // Camera setup
            camera = new THREE.PerspectiveCamera(45, width / height, 0.1, 1000);
            camera.position.set(0, 0, 3);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(width, height);
            renderer.shadowMap.enabled = true;
            container.appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.enablePan = false;
            controls.minDistance = 2;
            controls.maxDistance = 5;

            // Create Earth sphere with procedural texture
            const createEarthTexture = () => {
                const canvas = document.createElement('canvas');
                canvas.width = 512;
                canvas.height = 256;
                const ctx = canvas.getContext('2d');

                // Fill with blue for ocean
                ctx.fillStyle = '#006994';
                ctx.fillRect(0, 0, canvas.width, canvas.height);

                // Add simplified continents
                ctx.fillStyle = '#228B22';
                // Africa
                ctx.beginPath();
                ctx.ellipse(256, 128, 40, 60, 0, 0, 2 * Math.PI);
                ctx.fill();

                // Eurasia
                ctx.beginPath();
                ctx.ellipse(350, 100, 60, 50, 0, 0, 2 * Math.PI);
                ctx.fill();

                // Americas
                ctx.beginPath();
                ctx.ellipse(120, 120, 40, 70, 0, 0, 2 * Math.PI);
                ctx.fill();

                return new THREE.CanvasTexture(canvas);
            };

            const earthGeometry = new THREE.SphereGeometry(1, 32, 32);
            const earthMaterial = new THREE.MeshPhongMaterial({
                map: createEarthTexture(),
                shininess: 100
            });
            earthMesh = new THREE.Mesh(earthGeometry, earthMaterial);
            earthMesh.castShadow = true;
            earthMesh.receiveShadow = true;
            scene.add(earthMesh);

            // Add wireframe for graticule
            const wireframeGeometry = new THREE.WireframeGeometry(earthGeometry);
            const wireframeMaterial = new THREE.LineBasicMaterial({ 
                color: 0xffffff, 
                opacity: 0.2, 
                transparent: true 
            });
            const wireframe = new THREE.LineSegments(wireframeGeometry, wireframeMaterial);
            earthMesh.add(wireframe);

            // Lighting
            ambientLight = new THREE.AmbientLight(0x404040, 0.3);
            scene.add(ambientLight);

            sunLight = new THREE.DirectionalLight(0xffffff, 1);
            sunLight.castShadow = true;
            scene.add(sunLight);

            // Initialize visual aids
            initVisualAids();

            // Handle window resize
            window.addEventListener('resize', () => {
                const newWidth = container.clientWidth;
                const newHeight = container.clientHeight;
                camera.aspect = newWidth / newHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(newWidth, newHeight);
            });

            animate();
        }

        // Initialize visual aids (latitude lines, vectors)
        function initVisualAids() {
            // Selected latitude line
            const latitudeGeometry = new THREE.CircleGeometry(1, 64);
            latitudeGeometry.rotateX(Math.PI / 2);
            const latitudeMaterial = new THREE.LineDashedMaterial({
                color: 0x0000ff,
                linewidth: 2,
                dashSize: 0.05,
                gapSize: 0.05
            });
            const latitudePoints = latitudeGeometry.getPoints(100);
            selectedLatitudeLine = new THREE.Line(
                new THREE.BufferGeometry().setFromPoints(latitudePoints),
                latitudeMaterial
            );
            selectedLatitudeLine.computeLineDistances();
            scene.add(selectedLatitudeLine);

            // Surface normal vector
            surfaceNormalVector = new THREE.ArrowHelper(
                new THREE.Vector3(1, 0, 0),
                new THREE.Vector3(0, 0, 0),
                0.5,
                0x000000
            );
            scene.add(surfaceNormalVector);

            // Sun direction vector
            sunVector = new THREE.ArrowHelper(
                new THREE.Vector3(1, 0, 0),
                new THREE.Vector3(0, 0, 0),
                0.5,
                0xff0000
            );
            scene.add(sunVector);

            // Polar axis vector
            polarAxisVector = new THREE.ArrowHelper(
                new THREE.Vector3(0, 1, 0),
                new THREE.Vector3(0, 0, 0),
                1.5,
                0x000000
            );
            scene.add(polarAxisVector);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize Plotly charts
        function initPlots() {
            // Instantaneous energy plot
            const instantLayout = {
                title: 'energy',
                yaxis: { title: 'watts/m^2', range: [0, 1400] },
                xaxis: { title: 'time (hr)', range: [0, 24] },
                margin: { t: 50, r: 30, b: 50, l: 50 },
                showlegend: false
            };

            Plotly.newPlot('plot-instant', [], instantLayout);

            // Cumulative energy plot
            const cumulativeLayout = {
                title: 'energy',
                yaxis: { title: 'watts/m^2', range: [0, 14000] },
                xaxis: { title: 'time (hr)', range: [0, 24] },
                margin: { t: 50, r: 30, b: 50, l: 50 },
                showlegend: false
            };

            Plotly.newPlot('plot-cumulative', [], cumulativeLayout);
        }

        // Calculate solar declination
        function calculateSolarDeclination(season) {
            return 23.5 * Math.cos(2 * Math.PI * season);
        }

        // Calculate solar energy
        function calculateSolarEnergy(latitude, declination, hourAngle) {
            const latRad = latitude * Math.PI / 180;
            const decRad = declination * Math.PI / 180;
            const harRad = hourAngle * Math.PI / 180;

            const sinLat = Math.sin(latRad);
            const sinDec = Math.sin(decRad);
            const cosLat = Math.cos(latRad);
            const cosDec = Math.cos(decRad);
            const cosHar = Math.cos(harRad);

            const elevationAngle = sinLat * sinDec + cosLat * cosDec * cosHar;

            return 1361 * Math.max(0, elevationAngle);
        }

        // Update visualization
        function update() {
            // Get slider values
            const season = parseFloat(document.getElementById('slider-season').value);
            const latitude = parseFloat(document.getElementById('slider-latitude').value);
            const time = parseFloat(document.getElementById('slider-time').value);

            // Update value displays
            document.getElementById('value-season').textContent = season.toFixed(2);
            document.getElementById('value-latitude').textContent = latitude.toFixed(2);
            document.getElementById('value-time').textContent = time.toFixed(2);

            // Calculate solar declination
            const declination = calculateSolarDeclination(season);
            const declinationRad = declination * Math.PI / 180;

            // Update Earth rotation
            earthMesh.rotation.y = (time / 24) * 2 * Math.PI;

            // Update Sun position
            const sunDirection = new THREE.Vector3(
                0,
                Math.sin(declinationRad),
                -Math.cos(declinationRad)
            ).normalize();
            sunLight.position.copy(sunDirection).multiplyScalar(5);

            // Update latitude line
            const latRad = latitude * Math.PI / 180;
            const latRadius = Math.cos(latRad);
            const latY = Math.sin(latRad);

            const latitudePoints = [];
            for (let i = 0; i <= 64; i++) {
                const angle = (i / 64) * 2 * Math.PI;
                latitudePoints.push(new THREE.Vector3(
                    latRadius * Math.cos(angle),
                    latY,
                    latRadius * Math.sin(angle)
                ));
            }
            selectedLatitudeLine.geometry.setFromPoints(latitudePoints);

            // Update vectors
            const latPos = new THREE.Vector3(latRadius, latY, 0);
            const surfaceNormal = latPos.clone().normalize();
            surfaceNormalVector.position.copy(latPos);
            surfaceNormalVector.setDirection(surfaceNormal);

            sunVector.position.copy(latPos);
            sunVector.setDirection(sunDirection);

            // Update plots
            updatePlots(latitude, declination, time);
        }

        // Update Plotly charts
        function updatePlots(latitude, declination, currentTime) {
            const hours = [];
            const equatorEnergy = [];
            const selectedLatEnergy = [];
            const northPoleEnergy = [];

            // Calculate energy values for each hour
            for (let t = 0; t <= 24; t += 0.1) {
                hours.push(t);
                const hourAngle = 15 * (t - 12);

                equatorEnergy.push(calculateSolarEnergy(0, declination, hourAngle));
                selectedLatEnergy.push(calculateSolarEnergy(latitude, declination, hourAngle));
                northPoleEnergy.push(calculateSolarEnergy(90, declination, hourAngle));
            }

            // Current time index
            const currentTimeIndex = Math.round(currentTime / 0.1);
            const currentEnergy = selectedLatEnergy[currentTimeIndex] || 0;

            // Instantaneous plot data
            const instantTraces = [
                { x: hours, y: equatorEnergy, type: 'scatter', mode: 'lines', line: { color: 'red' } },
                { x: hours, y: selectedLatEnergy, type: 'scatter', mode: 'lines', line: { color: 'black' } },
                { x: hours, y: northPoleEnergy, type: 'scatter', mode: 'lines', line: { color: 'blue' } },
                { x: [currentTime], y: [currentEnergy], type: 'scatter', mode: 'markers', 
                  marker: { color: 'black', size: 10 } }
            ];

            Plotly.react('plot-instant', instantTraces);

            // Calculate cumulative energy
            const equatorCumulative = [];
            const selectedLatCumulative = [];
            const northPoleCumulative = [];

            let eqSum = 0, selSum = 0, npSum = 0;
            for (let i = 0; i < equatorEnergy.length; i++) {
                const dt = 0.1;
                eqSum += equatorEnergy[i] * dt;
                selSum += selectedLatEnergy[i] * dt;
                npSum += northPoleEnergy[i] * dt;

                equatorCumulative.push(eqSum);
                selectedLatCumulative.push(selSum);
                northPoleCumulative.push(npSum);
            }

            // Cumulative plot data
            const cumulativeTraces = [
                { x: hours, y: equatorCumulative, type: 'scatter', mode: 'lines', line: { color: 'red' } },
                { x: hours, y: selectedLatCumulative, type: 'scatter', mode: 'lines', line: { color: 'black' } },
                { x: hours, y: northPoleCumulative, type: 'scatter', mode: 'lines', line: { color: 'blue' } }
            ];

            Plotly.react('plot-cumulative', cumulativeTraces);
        }

        // Initialize everything
        window.addEventListener('load', () => {
            initThreeJS();
            initPlots();

            // Add event listeners
            document.getElementById('slider-season').addEventListener('input', update);
            document.getElementById('slider-latitude').addEventListener('input', update);
            document.getElementById('slider-time').addEventListener('input', update);

            // Initial update
            update();
        });
    </script>
</body>
</html>
