    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Global variables
        let scenes = {}, cameras = {}, renderers = {}, controls = {};
        let primalIsoquantCurtain, primalIsocostPlane, primalSolutionLine;
        let dualProductionSurface, dualBudgetCurtain, dualIntersectionCurve, dualOptimalPoint;

        // Initialize the application
        function init() {
            setupEventListeners();
            initThreeJS();
            update();
        }

        // Setup all event listeners
        function setupEventListeners() {
            // Slider change events
            ['alpha', 'beta', 'w', 'r', 'Q', 'C'].forEach(id => {
                const slider = document.getElementById(`slider-${id}`);
                const label = document.getElementById(`label-${id}`);

                slider.addEventListener('input', (e) => {
                    label.textContent = parseFloat(e.target.value).toFixed(2);
                    update();
                });
            });

            // Button click events
            document.getElementById('btn-reset').addEventListener('click', resetToDefaults);
            document.getElementById('btn-C-from-primal').addEventListener('click', setCFromPrimal);
            document.getElementById('btn-Q-from-dual').addEventListener('click', setQFromDual);
        }

        // Reset all sliders to default values
        function resetToDefaults() {
            const defaults = {
                'alpha': 0.5, 'beta': 0.5, 'w': 1.0, 'r': 1.0,
                'Q': 3.0, 'C': 4.0
            };

            Object.entries(defaults).forEach(([id, value]) => {
                document.getElementById(`slider-${id}`).value = value;
                document.getElementById(`label-${id}`).textContent = value.toFixed(2);
            });

            update();
        }

        // Set C from primal solution
        function setCFromPrimal() {
            const C_out = primalSolution.C;
            const slider = document.getElementById('slider-C');
            const label = document.getElementById('label-C');

            slider.value = Math.min(10, Math.max(1, C_out));
            label.textContent = parseFloat(slider.value).toFixed(2);
            update();
        }

        // Set Q from dual solution
        function setQFromDual() {
            const Q_out = dualSolution.Q;
            const slider = document.getElementById('slider-Q');
            const label = document.getElementById('label-Q');

            slider.value = Math.min(10, Math.max(1, Q_out));
            label.textContent = parseFloat(slider.value).toFixed(2);
            update();
        }

        // Get current parameter values
        function getParameters() {
            return {
                alpha: parseFloat(document.getElementById('slider-alpha').value),
                beta: parseFloat(document.getElementById('slider-beta').value),
                w: parseFloat(document.getElementById('slider-w').value),
                r: parseFloat(document.getElementById('slider-r').value),
                Q: parseFloat(document.getElementById('slider-Q').value),
                C: parseFloat(document.getElementById('slider-C').value)
            };
        }

        // Calculate primal solution
        function solvePrimal(params) {
            const { alpha, beta, w, r, Q } = params;

            const k = (w * beta) / (r * alpha);
            const x_p = Math.pow(Q / Math.pow(k, beta), 1 / (alpha + beta));
            const y_p = x_p * k;
            const C_out = w * x_p + r * y_p;

            return { x: x_p, y: y_p, C: C_out };
        }

        // Calculate dual solution
        function solveDual(params) {
            const { alpha, beta, w, r, C } = params;

            const x_d = (C * alpha) / (w * (alpha + beta));
            const y_d = (C * beta) / (r * (alpha + beta));
            const Q_out = Math.pow(x_d, alpha) * Math.pow(y_d, beta);

            return { x: x_d, y: y_d, Q: Q_out };
        }

        // Solutions storage
        let primalSolution = { x: 3, y: 3, C: 6 };
        let dualSolution = { x: 2, y: 2, Q: 2 };

        // Main update function
        function update() {
            const params = getParameters();

            // Calculate solutions
            primalSolution = solvePrimal(params);
            dualSolution = solveDual(params);

            // Update output displays
            document.getElementById('output-primal').textContent = 
                `x = ${primalSolution.x.toFixed(2)}, y = ${primalSolution.y.toFixed(2)}, C = ${primalSolution.C.toFixed(2)}`;
            document.getElementById('output-dual').textContent = 
                `x = ${dualSolution.x.toFixed(2)}, y = ${dualSolution.y.toFixed(2)}, Q = ${dualSolution.Q.toFixed(2)}`;

            // Update 3D visualizations
            updatePrimalVisualization(params);
            updateDualVisualization(params);
        }

        // Initialize Three.js scenes
        function initThreeJS() {
            const plotContainers = ['plot-primal', 'plot-dual'];

            plotContainers.forEach(containerId => {
                const container = document.getElementById(containerId);

                // Scene
                const scene = new THREE.Scene();
                scene.background = new THREE.Color(0xf8f9fa);
                scenes[containerId] = scene;

                // Camera
                const camera = new THREE.PerspectiveCamera(
                    45, 
                    container.clientWidth / container.clientHeight, 
                    0.1, 
                    1000
                );
                camera.position.set(15, 15, 15);
                cameras[containerId] = camera;

                // Renderer
                const renderer = new THREE.WebGLRenderer({ antialias: true });
                renderer.setSize(container.clientWidth, container.clientHeight);
                renderer.shadowMap.enabled = true;
                container.appendChild(renderer.domElement);
                renderers[containerId] = renderer;

                // OrbitControls
                const orbitControls = new THREE.OrbitControls(camera, renderer.domElement);
                orbitControls.enableDamping = true;
                orbitControls.dampingFactor = 0.05;
                orbitControls.minDistance = 5;
                orbitControls.maxDistance = 50;
                controls[containerId] = orbitControls;

                // Lighting
                const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
                scene.add(ambientLight);

                const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
                directionalLight.position.set(10, 10, 5);
                directionalLight.castShadow = true;
                scene.add(directionalLight);

                // Axes helpers
                const axesHelper = new THREE.AxesHelper(10);
                scene.add(axesHelper);

                // Grid
                const gridHelper = new THREE.GridHelper(10, 10, 0x888888, 0xcccccc);
                scene.add(gridHelper);

                // Box frame
                const boxHelper = new THREE.BoxHelper(new THREE.Box3(
                    new THREE.Vector3(0, 0, 0),
                    new THREE.Vector3(10, 10, 10)
                ));
                scene.add(boxHelper);
            });

            // Start animation loop
            animate();
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);

            Object.keys(controls).forEach(key => {
                controls[key].update();
                renderers[key].render(scenes[key], cameras[key]);
            });
        }

        // Update primal visualization
        function updatePrimalVisualization(params) {
            const scene = scenes['plot-primal'];

            // Clear previous objects
            if (primalIsoquantCurtain) scene.remove(primalIsoquantCurtain);
            if (primalIsocostPlane) scene.remove(primalIsocostPlane);
            if (primalSolutionLine) scene.remove(primalSolutionLine);

            const { x_p, y_p, C_p } = primalSolution;
            const { alpha, beta, w, r, Q } = params;

            // Create isoquant curtain (vertical wall)
            const isoquantPoints = [];
            for (let x = 0.1; x <= 8; x += 0.1) {
                const y = Math.pow(Q / Math.pow(x, alpha), 1 / beta);
                if (y <= 8 && isFinite(y)) {
                    isoquantPoints.push(new THREE.Vector3(x, y, 0));
                }
            }

            if (isoquantPoints.length > 1) {
                const curtainGeometry = new THREE.Geometry();
                curtainGeometry.vertices = [];
                for (let point of isoquantPoints) {
                    curtainGeometry.vertices.push(point.clone());
                    curtainGeometry.vertices.push(new THREE.Vector3(point.x, point.y, C_p * 1.5));
                }

                curtainGeometry.faces = [];
                for (let i = 0; i < isoquantPoints.length - 1; i++) {
                    curtainGeometry.faces.push(
                        new THREE.Face3(i * 2, i * 2 + 1, (i + 1) * 2),
                        new THREE.Face3((i + 1) * 2, i * 2 + 1, (i + 1) * 2 + 1)
                    );
                }

                curtainGeometry.computeFaceNormals();
                const curtainMaterial = new THREE.MeshBasicMaterial({
                    color: 0xff8800,
                    opacity: 0.7,
                    transparent: true,
                    side: THREE.DoubleSide
                });
                primalIsoquantCurtain = new THREE.Mesh(curtainGeometry, curtainMaterial);
                scene.add(primalIsoquantCurtain);
            }

            // Create isocost plane
            const planeGeometry = new THREE.PlaneGeometry(20, 20);
            const planeMaterial = new THREE.MeshBasicMaterial({
                color: 0x666666,
                opacity: 0.5,
                transparent: true,
                side: THREE.DoubleSide
            });

            primalIsocostPlane = new THREE.Mesh(planeGeometry, planeMaterial);

            // Position and rotate the plane to match C = w*x + r*y
            const normal = new THREE.Vector3(-w, -r, 1).normalize();
            primalIsocostPlane.lookAt(
                primalIsocostPlane.position.clone().add(normal)
            );

            // Translate plane to correct height
            const d = C_p / Math.sqrt(w * w + r * r + 1);
            primalIsocostPlane.translateOnAxis(normal, d);

            scene.add(primalIsocostPlane);

            // Create solution line
            const lineGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(x_p, y_p, C_p)
            ]);
            const lineMaterial = new THREE.LineBasicMaterial({
                color: 0x0088ff,
                linewidth: 3
            });
            primalSolutionLine = new THREE.Line(lineGeometry, lineMaterial);
            scene.add(primalSolutionLine);
        }

        // Update dual visualization
        function updateDualVisualization(params) {
            const scene = scenes['plot-dual'];

            // Clear previous objects
            if (dualProductionSurface) scene.remove(dualProductionSurface);
            if (dualBudgetCurtain) scene.remove(dualBudgetCurtain);
            if (dualIntersectionCurve) scene.remove(dualIntersectionCurve);
            if (dualOptimalPoint) scene.remove(dualOptimalPoint);

            const { x_d, y_d, Q_d } = dualSolution;
            const { alpha, beta, w, r, C } = params;

            // Create production surface using parametric geometry
            const surfaceGeometry = new THREE.ParametricGeometry((u, v, target) => {
                u = u * 8;
                v = v * 8;
                const Q = Math.pow(u, alpha) * Math.pow(v, beta);
                target.set(u, v, Math.min(Q, 10));
            }, 32, 32);

            const surfaceMaterial = new THREE.MeshLambertMaterial({
                color: 0xff8800,
                wireframe: false
            });
            dualProductionSurface = new THREE.Mesh(surfaceGeometry, surfaceMaterial);
            scene.add(dualProductionSurface);

            // Create budget curtain
            const budgetPoints = [];
            for (let x = 0.1; x <= 8; x += 0.1) {
                const y = (C - w * x) / r;
                if (y >= 0 && y <= 8) {
                    budgetPoints.push(new THREE.Vector3(x, y, 0));
                    budgetPoints.push(new THREE.Vector3(x, y, 10));
                }
            }

            if (budgetPoints.length > 1) {
                const curtainGeometry = new THREE.BufferGeometry();
                const vertices = new Float32Array(budgetPoints.length * 3);
                budgetPoints.forEach((point, i) => {
                    vertices[i * 3] = point.x;
                    vertices[i * 3 + 1] = point.y;
                    vertices[i * 3 + 2] = point.z;
                });
                curtainGeometry.setAttribute('position', new THREE.BufferAttribute(vertices, 3));

                const indices = [];
                for (let i = 0; i < budgetPoints.length / 2 - 1; i++) {
                    indices.push(
                        i * 2, i * 2 + 1, (i + 1) * 2,
                        (i + 1) * 2, i * 2 + 1, (i + 1) * 2 + 1
                    );
                }
                curtainGeometry.setIndex(indices);

                curtainGeometry.computeVertexNormals();
                const curtainMaterial = new THREE.MeshBasicMaterial({
                    color: 0x666666,
                    opacity: 0.4,
                    transparent: true,
                    side: THREE.DoubleSide
                });
                dualBudgetCurtain = new THREE.Mesh(curtainGeometry, curtainMaterial);
                scene.add(dualBudgetCurtain);
            }

            // Create intersection curve
            const curvePoints = [];
            for (let x = 0.1; x <= C / w; x += 0.1) {
                const y = (C - w * x) / r;
                if (y > 0) {
                    const Q = Math.pow(x, alpha) * Math.pow(y, beta);
                    curvePoints.push(new THREE.Vector3(x, y, Q));
                }
            }

            if (curvePoints.length > 1) {
                const curveGeometry = new THREE.BufferGeometry().setFromPoints(curvePoints);
                const curveMaterial = new THREE.LineBasicMaterial({
                    color: 0xff0000,
                    linewidth: 2
                });
                dualIntersectionCurve = new THREE.Line(curveGeometry, curveMaterial);
                scene.add(dualIntersectionCurve);
            }

            // Create optimal point marker
            const sphereGeometry = new THREE.SphereGeometry(0.2, 16, 16);
            const sphereMaterial = new THREE.MeshBasicMaterial({ color: 0x333333 });
            dualOptimalPoint = new THREE.Mesh(sphereGeometry, sphereMaterial);
            dualOptimalPoint.position.set(x_d, y_d, Q_d);
            scene.add(dualOptimalPoint);
        }

        // Initialize when page loads
        window.addEventListener('load', init);
    </script>
</body>
</html>
