        <div id="visualization-area">
            <!-- p5.js canvas will be inserted here by the script -->
        </div>
    </div>

    <!-- CDN for p5.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        // Global variables for storing calculation results
        let flashResults = {
            T_flash: 0,
            V_frac: 0,
            x: [0, 0, 0, 0],
            y: [0, 0, 0, 0]
        };

        // Component data
        const components = [
            { name: 'n-butane', A: 6.80165, B: 1005.157, C: -27.159, Cp_L: 132, ΔH_vap: 22400 },
            { name: 'n-pentane', A: 6.86177, B: 1184.249, C: -35.223, Cp_L: 161, ΔH_vap: 25800 },
            { name: 'n-heptane', A: 6.90237, B: 1358.59, C: -51.303, Cp_L: 225, ΔH_vap: 31800 },
            { name: 'n-octane', A: 6.92383, B: 1431.149, C: -59.501, Cp_L: 255, ΔH_vap: 34400 }
        ];

        const F = 10; // kmol/hr
        const T_F = 323.2; // K
        const P_F = 1000; // kPa
        const z_i = [0.25, 0.25, 0.25, 0.25]; // Feed mole fractions

        // Function to calculate saturation pressure using Antoine's equation
        function calcP_sat(T, A, B, C) {
            return Math.pow(10, A - B / (T + C));
        }

        // Function to calculate K-values
        function calcK_i(T, P_flash) {
            return components.map(comp => calcP_sat(T, comp.A, comp.B, comp.C) / P_flash);
        }

        // Rachford-Rice equation
        function rachfordRice(V, K_i, z_i) {
            let sum = 0;
            for (let i = 0; i < K_i.length; i++) {
                sum += z_i[i] * (K_i[i] - 1) / (1 + V * (K_i[i] - 1));
            }
            return sum;
        }

        // Bisection method to solve Rachford-Rice for V given T
        function solveRachfordRice(T, P_flash) {
            const K_i = calcK_i(T, P_flash);
            let V_min = 0.001;
            let V_max = 0.999;
            let tol = 1e-6;
            let max_iter = 100;

            for (let iter = 0; iter < max_iter; iter++) {
                let V = (V_min + V_max) / 2;
                let f = rachfordRice(V, K_i, z_i);

                if (Math.abs(f) < tol) {
                    // Calculate x_i and y_i
                    let x_i = [];
                    let y_i = [];
                    for (let i = 0; i < K_i.length; i++) {
                        x_i.push(z_i[i] / (1 + V * (K_i[i] - 1)));
                        y_i.push(K_i[i] * x_i[i]);
                    }
                    return { V, x_i, y_i };
                }

                if (f > 0) {
                    V_min = V;
                } else {
                    V_max = V;
                }
            }
            return null;
        }

        // Energy balance function
        function energyBalance(T, P_flash, Q_hr) {
            const rr_result = solveRachfordRice(T, P_flash);
            if (!rr_result) return 1e10; // Return large value if no solution

            // Calculate sensible heat term
            let Cp_avg = 0;
            for (let i = 0; i < components.length; i++) {
                Cp_avg += z_i[i] * components[i].Cp_L;
            }
            let sensible_heat = Cp_avg * (T_F - T);

            // Calculate latent heat term
            let latent_heat = 0;
            for (let i = 0; i < components.length; i++) {
                latent_heat += rr_result.y_i[i] * components[i].ΔH_vap;
            }

            // Energy balance residual
            return (Q_hr / F + sensible_heat) - rr_result.V * latent_heat;
        }

        // Main flash calculation function
        function calculateFlash(P_flash, Q_hr) {
            // Find temperature bounds
            let T_min = 250; // K
            let T_max = 500; // K
            let tol = 0.01;
            let max_iter = 100;

            for (let iter = 0; iter < max_iter; iter++) {
                let T = (T_min + T_max) / 2;
                let f = energyBalance(T, P_flash, Q_hr);

                if (Math.abs(f) < tol) {
                    const rr_result = solveRachfordRice(T, P_flash);
                    if (rr_result) {
                        return {
                            T_flash: T,
                            V_frac: rr_result.V,
                            x: rr_result.x_i,
                            y: rr_result.y_i
                        };
                    }
                    break;
                }

                if (f > 0) {
                    T_min = T;
                } else {
                    T_max = T;
                }
            }

            // Return default values if no solution found
            return {
                T_flash: T_F,
                V_frac: 0.5,
                x: [1, 1, 1, 1],
                y: [1, 1, 1, 1]
            };
        }

        // Event listeners for sliders
        document.getElementById('slider-pressure').addEventListener('input', function() {
