                // Draw vapor stream
                p.stroke(147, 112, 219);
                p.line(400, 200, 400, 100);
                p.line(400, 100, 550, 100);
                drawArrow(p, 540, 100, 550, 100);

                // Draw liquid stream
                p.stroke(255, 165, 0);
                p.line(400, 300, 400, 400);
                p.line(400, 400, 550, 400);
                drawArrow(p, 540, 400, 550, 400);

                // Draw heat input arrow (dashed)
                p.stroke(255, 0, 0);
                drawDashedLine(p, 500, 250, 420, 250);
                drawArrow(p, 420, 250, 410, 250);

                // Draw text labels
                p.noStroke();
                p.fill(0);
                p.textAlign(p.LEFT, p.TOP);
                p.textSize(14);

                // Feed info
                p.text('Feed', 150, 220);
                p.text('T = ' + T_F.toFixed(1) + ' K', 150, 240);
                p.text('P = ' + P_F + ' kPa', 150, 260);
                p.text('z = [' + z_i.join(', ') + ']', 150, 280);

                // Vapor info
                p.text('Vapor', 570, 70);
                let y_str = 'y = [' + flashResults.y.map(y => y.toFixed(3)).join(', ') + ']';
                p.text(y_str, 570, 90);

                // Liquid info
                p.text('Liquid', 570, 370);
                let x_str = 'x = [' + flashResults.x.map(x => x.toFixed(3)).join(', ') + ']';
                p.text(x_str, 570, 390);

                // Process info
                p.text('Vapor fraction = ' + flashResults.V_frac.toFixed(3), 570, 220);
                p.text('Flash temp = ' + flashResults.T_flash.toFixed(1) + ' K', 570, 240);
                p.text('Flash pressure = ' + document.getElementById('slider-pressure').value + ' kPa', 570, 260);

                // Heat info
                p.text('Q = ' + document.getElementById('slider-heat').value + ' kJ/hr', 430, 230);
            };

            function drawArrow(p, x1, y1, x2, y2) {
                let angle = p.atan2(y2 - y1, x2 - x1);
                let arrowSize = 10;

                p.line(x1, y1, x2, y2);

                p.push();
                p.translate(x2, y2);
                p.rotate(angle);
                p.line(0, 0, -arrowSize * p.cos(p.PI / 6), -arrowSize * p.sin(p.PI / 6));
                p.line(0, 0, -arrowSize * p.cos(p.PI / 6), arrowSize * p.sin(p.PI / 6));
                p.pop();
            }

            function drawDashedLine(p, x1, y1, x2, y2) {
                let segments = 20;
                let dx = (x2 - x1) / segments;
                let dy = (y2 - y1) / segments;

                for (let i = 0; i < segments; i += 2) {
                    p.line(x1 + i * dx, y1 + i * dy, x1 + (i + 1) * dx, y1 + (i + 1) * dy);
                }
            }
        };

        new p5(sketch);
    </script>
