            createMoon();

            // Create Constellations
            createConstellations();

            // Set initial view
            setView('land');

            // Start animation loop
            animate();
        }

        function createEarth() {
            // Earth geometry
            const earthGeometry = new THREE.SphereGeometry(1, 32, 32);
            const earthMaterial = new THREE.MeshPhongMaterial({ 
                color: 0x2266cc,
                emissive: 0x001122
            });
            earthMesh = new THREE.Mesh(earthGeometry, earthMaterial);
            earthMesh.castShadow = true;
            earthMesh.receiveShadow = true;

            // Earth atmosphere
            const atmoGeometry = new THREE.SphereGeometry(1.1, 32, 32);
            const atmoMaterial = new THREE.MeshBasicMaterial({
                color: 0x4488ff,
                transparent: true,
                opacity: 0.3,
                blending: THREE.AdditiveBlending
            });
            earthAtmosphere = new THREE.Mesh(atmoGeometry, atmoMaterial);

            earth = new THREE.Group();
            earth.add(earthMesh);
            earth.add(earthAtmosphere);
            earth.position.set(10, 0, 0);
            scene.add(earth);
        }

        function createMoon() {
            // Moon surface (for land view)
            const surfaceGeometry = new THREE.PlaneGeometry(50, 50, 50, 50);
            const surfaceMaterial = new THREE.MeshStandardMaterial({
                color: 0x666666,
                roughness: 0.8,
                metalness: 0.2
            });
            moonSurface = new THREE.Mesh(surfaceGeometry, surfaceMaterial);
            moonSurface.rotation.x = -Math.PI / 2;
            moonSurface.position.y = -2;
            moonSurface.visible = false;
            scene.add(moonSurface);

            // Moon sphere (for space view)
            const moonGeometry = new THREE.SphereGeometry(2, 32, 32);
            const moonMaterial = new THREE.MeshPhongMaterial({
                color: 0x888888,
                emissive: 0x222222
            });
            moonSphere = new THREE.Mesh(moonGeometry, moonMaterial);
            moonSphere.castShadow = true;
            moonSphere.receiveShadow = true;
            scene.add(moonSphere);
        }

        function createConstellations() {
            constellationGroup = new THREE.Group();

            for (const [name, constellation] of Object.entries(constellations)) {
                // Create stars
                const starGeometry = new THREE.BufferGeometry().setFromPoints(constellation.stars);
                const starMaterial = new THREE.PointsMaterial({
                    color: 0xffffff,
                    size: 0.1
                });
                const points = new THREE.Points(starGeometry, starMaterial);
                constellationGroup.add(points);

                // Create lines
                const linePoints = [];
                for (const [start, end] of constellation.lines) {
                    linePoints.push(constellation.stars[start]);
                    linePoints.push(constellation.stars[end]);
                }
                const lineGeometry = new THREE.BufferGeometry().setFromPoints(linePoints);
                const lineMaterial = new THREE.LineBasicMaterial({
                    color: 0xffff66,
                    opacity: 0.6,
                    transparent: true
                });
                const lines = new THREE.LineSegments(lineGeometry, lineMaterial);
                constellationGroup.add(lines);
            }

            scene.add(constellationGroup);
        }

        function setView(view) {
            controls.view = view;

            if (view === 'land') {
                // Position camera on lunar surface
                camera.position.set(0, 2, 5);
                camera.lookAt(10, 0, 0);
                moonSurface.visible = true;
                earth.visible = true;
                moonSphere.visible = false;
            } else {
                // Position camera in space
                camera.position.set(20, 15, 20);
                camera.lookAt(0, 0, 0);
                moonSurface.visible = false;
                earth.visible = true;
                moonSphere.visible = true;
            }
        }

        function updateScene() {
            // Update Earth position based on time
            const orbitalRadius = 10;
            const angle = controls.time * 0.1 + controls.sunEarthAngle;
            const elevation = Math.sin(controls.nodalAngle) * 2;
            earth.position.set(
                orbitalRadius * Math.cos(angle),
                elevation,
                orbitalRadius * Math.sin(angle)
            );

            // Update Earth rotation
            earth.rotation.y = controls.time * 0.5;

            // Update Sun direction
            const sunDirection = new THREE.Vector3(
                Math.cos(angle + Math.PI),
                Math.sin(elevation * 0.5),
                Math.sin(angle + Math.PI)
            ).normalize();
            sunLight.position.copy(sunDirection.multiplyScalar(30));

            // Update constellation rotation
            constellationGroup.rotation.y = controls.zodiac;

            // Update Earth size
            const scale = controls.realisticSize ? 0.5 : 1;
            earthMesh.scale.set(scale, scale, scale);
            earthAtmosphere.scale.set(scale, scale, scale);

            // Check for eclipse effect
            const earthToLight = sunDirection.clone().negate();
            const earthToMoon = earth.position.clone().negate().normalize();
            const dot = earthToLight.dot(earthToMoon);

            if (dot > 0.95) {
                // Eclipse - make Earth reddish
                const intensity = (dot - 0.95) * 20;
                earthMesh.material.emissive.setRGB(0.4 * intensity, 0.1 * intensity, 0.1 * intensity);
                earthMesh.material.color.setRGB(0.8 - intensity * 0.3, 0.4 - intensity * 0.2, 0.4 - intensity * 0.2);
            } else {
                // Normal state
                earthMesh.material.emissive.setRGB(0.01, 0.01, 0.02);
                earthMesh.material.color.setRGB(0.133, 0.4, 0.8);
            }

            // Update constellation visibility
            constellationGroup.visible = controls.showConstellations;

            // Render scene
            renderer.render(scene, camera);
        }

        function animate() {
            requestAnimationFrame(animate);

            const delta = clock.getDelta();
            const deltaTime = delta * 0.5; // Animation speed multiplier

            // Update animated sliders
            if (isAnimating.time) {
                controls.time += deltaTime;
                document.getElementById('slider-time').value = controls.time;
                document.getElementById('slider-time-value').textContent = controls.time.toFixed(2);
            }

            if (isAnimating.nodalAngle) {
                controls.nodalAngle += deltaTime * 0.5;
                if (controls.nodalAngle > 6.283) controls.nodalAngle = 0;
                document.getElementById('slider-nodal-angle').value = controls.nodalAngle;
                document.getElementById('slider-nodal-angle-value').textContent = controls.nodalAngle.toFixed(2);
            }

            if (isAnimating.sunEarthAngle) {
                controls.sunEarthAngle += deltaTime * 0.5;
                if (controls.sunEarthAngle > 6.283) controls.sunEarthAngle = 0;
                document.getElementById('slider-sun-earth-angle').value = controls.sunEarthAngle;
                document.getElementById('slider-sun-earth-angle-value').textContent = controls.sunEarthAngle.toFixed(2);
            }

            if (isAnimating.zodiac) {
                controls.zodiac += deltaTime * 0.3;
                if (controls.zodiac > 6.283) controls.zodiac = 0;
                document.getElementById('slider-zodiac').value = controls.zodiac;
                document.getElementById('slider-zodiac-value').textContent = controls.zodiac.toFixed(2);
            }

            updateScene();
        }

        // Event listeners
        document.getElementById('btn-land').addEventListener('click', function() {
            document.getElementById('btn-land').classList.add('active');
            document.getElementById('btn-space').classList.remove('active');
            setView('land');
        });

        document.getElementById('btn-space').addEventListener('click', function() {
            document.getElementById('btn-space').classList.add('active');
            document.getElementById('btn-land').classList.remove('active');
            setView('space');
        });

        // Slider event listeners
        document.getElementById('slider-time').addEventListener('input', function(e) {
            controls.time = parseFloat(e.target.value);
            document.getElementById('slider-time-value').textContent = controls.time.toFixed(2);
        });

        document.getElementById('slider-nodal-angle').addEventListener('input', function(e) {
            controls.nodalAngle = parseFloat(e.target.value);
            document.getElementById('slider-nodal-angle-value').textContent = controls.nodalAngle.toFixed(2);
        });

        document.getElementById('slider-sun-earth-angle').addEventListener('input', function(e) {
            controls.sunEarthAngle = parseFloat(e.target.value);
            document.getElementById('slider-sun-earth-angle-value').textContent = controls.sunEarthAngle.toFixed(2);
        });

        document.getElementById('slider-zodiac').addEventListener('input', function(e) {
            controls.zodiac = parseFloat(e.target.value);
            document.getElementById('slider-zodiac-value').textContent = controls.zodiac.toFixed(2);
        });

        // Play button event listeners
        document.getElementById('btn-time-play').addEventListener('click', function() {
            isAnimating.time = !isAnimating.time;
            this.classList.toggle('active');
        });

        document.getElementById('btn-nodal-angle-play').addEventListener('click', function() {
            isAnimating.nodalAngle = !isAnimating.nodalAngle;
            this.classList.toggle('active');
        });

        document.getElementById('btn-sun-earth-angle-play').addEventListener('click', function() {
            isAnimating.sunEarthAngle = !isAnimating.sunEarthAngle;
            this.classList.toggle('active');
        });

        document.getElementById('btn-zodiac-play').addEventListener('click', function() {
            isAnimating.zodiac = !isAnimating.zodiac;
            this.classList.toggle('active');
        });

        // Checkbox event listeners
        document.getElementById('checkbox-constellations').addEventListener('change', function(e) {
            controls.showConstellations = e.target.checked;
        });

        document.getElementById('checkbox-realistic-size').addEventListener('change', function(e) {
            controls.realisticSize = e.target.checked;
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            const canvas = document.getElementById('scene-canvas');
            const width = canvas.clientWidth;
            const height = canvas.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();

            renderer.setSize(width, height);
        });

        // Initialize the scene when the page loads
        init();
    </script>
</body>
</html>
