            // Create compass rose
            createCompassRose();

            // Create axis labels
            createAxisLabels();

            // Initial update
            updateVisualization();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function createCompassRose() {
            compassGroup = new THREE.Group();

            // Ellipse curve
            const curve = new THREE.EllipseCurve(
                0, 0,
                1.5, 1.5,
                0, 2 * Math.PI,
                false,
                0
            );

            const points = curve.getPoints(100);
            const ellipseGeometry = new THREE.BufferGeometry().setFromPoints(points);
            const ellipseMaterial = new THREE.LineDashedMaterial({ 
                color: 0x000000,
                dashSize: 0.1,
                gapSize: 0.05
            });

            const ellipse = new THREE.Line(ellipseGeometry, ellipseMaterial);
            ellipse.rotation.x = -Math.PI / 2;
            ellipse.computeLineDistances();
            compassGroup.add(ellipse);

            // Direction markers
            const directions = [
                { label: 'N', angle: 0, pos: [0, 0, 1.5] },
                { label: 'E', angle: Math.PI/2, pos: [1.5, 0, 0] },
                { label: 'S', angle: Math.PI, pos: [0, 0, -1.5] },
                { label: 'W', angle: -Math.PI/2, pos: [-1.5, 0, 0] }
            ];

            directions.forEach(dir => {
                const lineGeometry = new THREE.BufferGeometry().setFromPoints([
                    new THREE.Vector3(0, 0, 0),
                    new THREE.Vector3(dir.pos[0], 0, dir.pos[2])
                ]);
                const lineMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
                const line = new THREE.Line(lineGeometry, lineMaterial);
                compassGroup.add(line);

                // Add text labels (simplified with sprites)
                const canvas = document.createElement('canvas');
                const context = canvas.getContext('2d');
                canvas.width = 64;
                canvas.height = 64;
                context.fillStyle = 'black';
                context.font = '48px Arial';
                context.textAlign = 'center';
                context.textBaseline = 'middle';
                context.fillText(dir.label, 32, 32);

                const texture = new THREE.CanvasTexture(canvas);
                const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
                const sprite = new THREE.Sprite(spriteMaterial);
                sprite.position.set(dir.pos[0] * 0.8, 0.1, dir.pos[2] * 0.8);
                sprite.scale.set(0.3, 0.3, 1);
                compassGroup.add(sprite);
            });

            scene.add(compassGroup);
        }

        function createAxisLabels() {
            // Create P, T, U labels (simplified with sprites)
            const labels = [
                { id: 'P', color: '#0000ff' },
                { id: 'T', color: '#ff0000' },
                { id: 'U', color: '#00ff00' }
            ];

            labels.forEach(labelInfo => {
                const canvas = document.createElement('canvas');
                const context = canvas.getContext('2d');
                canvas.width = 64;
                canvas.height = 64;
                context.fillStyle = labelInfo.color;
                context.font = 'bold 48px Arial';
                context.textAlign = 'center';
                context.textBaseline = 'middle';
                context.fillText(labelInfo.id, 32, 32);

                const texture = new THREE.CanvasTexture(canvas);
                const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
                const sprite = new THREE.Sprite(spriteMaterial);

                if (labelInfo.id === 'P') pLabel = sprite;
                else if (labelInfo.id === 'T') tLabel = sprite;
                else if (labelInfo.id === 'U') uLabel = sprite;

                sprite.scale.set(0.3, 0.3, 1);
                scene.add(sprite);
            });
        }

        function calculateAxes(strike, dip, rake) {
            // Convert angles to radians
            const phi = strike * Math.PI / 180;
            const delta = dip * Math.PI / 180;
            const lambda = rake * Math.PI / 180;

            // Fault normal vector (n) in seismological coordinates (N,E,D)
            const n = new THREE.Vector3(
                -Math.sin(delta) * Math.sin(phi),
                Math.sin(delta) * Math.cos(phi),
                -Math.cos(delta)
            );

            // Slip vector (u) in seismological coordinates
            const u = new THREE.Vector3(
                Math.cos(lambda) * Math.cos(phi) + Math.cos(delta) * Math.sin(lambda) * Math.sin(phi),
                Math.cos(lambda) * Math.sin(phi) - Math.cos(delta) * Math.sin(lambda) * Math.cos(phi),
                -Math.sin(delta) * Math.sin(lambda)
            );

            // P and T axes
            const P = new THREE.Vector3().addVectors(n, u).normalize();
            const T = new THREE.Vector3().subVectors(u, n).normalize();

            // Map to three.js coordinates (E,U,S or x,y,z)
            return {
                P: new THREE.Vector3(P.y, -P.z, -P.x),
                T: new THREE.Vector3(T.y, -T.z, -T.x),
                U: new THREE.Vector3(u.y, -u.z, -u.x)
            };
        }

        function updateVisualization() {
            const strike = parseFloat(sliderStrike.value);
            const dip = parseFloat(sliderDip.value);
            const rake = parseFloat(sliderSlipAngle.value);
            const slipAmount = parseFloat(sliderSlipAmount.value);

            // Calculate axes
            const axes = calculateAxes(strike, dip, rake);
            pAxis = axes.P;
            tAxis = axes.T;
            uAxis = axes.U;

            // Update sphere colors
            const geometry = sphere.geometry;
            const colors = geometry.attributes.color.array;
            const positions = geometry.attributes.position.array;

            // Color each face based on dot products
            const faceCount = geometry.index.count / 3;
            for (let i = 0; i < faceCount; i++) {
                const i1 = geometry.index.array[i * 3];
                const i2 = geometry.index.array[i * 3 + 1];
                const i3 = geometry.index.array[i * 3 + 2];

                // Calculate face centroid
                const v1 = new THREE.Vector3(
                    positions[i1 * 3], positions[i1 * 3 + 1], positions[i1 * 3 + 2]
                );
                const v2 = new THREE.Vector3(
                    positions[i2 * 3], positions[i2 * 3 + 1], positions[i2 * 3 + 2]
                );
                const v3 = new THREE.Vector3(
                    positions[i3 * 3], positions[i3 * 3 + 1], positions[i3 * 3 + 2]
                );

                const centroid = new THREE.Vector3().addVectors(v1, v2).add(v3).divideScalar(3);
                centroid.normalize();

                // Determine compression/dilation
                const pDot = centroid.dot(pAxis);
                const tDot = centroid.dot(tAxis);
                const value = pDot * pDot - tDot * tDot;

                // Set color
                const color = value > 0 ? [1, 1, 1] : [1, 0.65, 0]; // white or orange
                for (let j = 0; j < 3; j++) {
                    colors[i1 * 3 + j] = colors[i2 * 3 + j] = colors[i3 * 3 + j] = color[j];
                }
            }
            geometry.attributes.color.needsUpdate = true;

            // Update vertex positions based on slip amount
            const deformationScale = 0.3;
            for (let i = 0; i < positions.length / 3; i++) {
                const vOrig = new THREE.Vector3(
                    originalVertices[i * 3],
                    originalVertices[i * 3 + 1],
                    originalVertices[i * 3 + 2]
                );
                vOrig.normalize();

                const pDot = vOrig.dot(pAxis);
                const tDot = vOrig.dot(tAxis);
                const deformation = pDot * pDot - tDot * tDot;

                const scale = 1 + deformationScale * slipAmount * deformation;

                positions[i * 3] = vOrig.x * scale;
                positions[i * 3 + 1] = vOrig.y * scale;
                positions[i * 3 + 2] = vOrig.z * scale;
            }
            geometry.attributes.position.needsUpdate = true;
            geometry.computeVertexNormals();

            // Update axis label positions
            if (pLabel) {
                pLabel.position.copy(pAxis).multiplyScalar(1.1);
            }
            if (tLabel) {
                tLabel.position.copy(tAxis).multiplyScalar(1.1);
            }
            if (uLabel) {
                uLabel.position.copy(uAxis).multiplyScalar(1.1);
            }

            // Update box helper
            boxHelper.update();
        }

        function onWindowResize() {
            const container = document.getElementById('canvas-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Event listeners
        sliderStrike.addEventListener('input', function() {
            valueStrike.value = this.value;
            updateVisualization();
        });

        sliderDip.addEventListener('input', function() {
            valueDip.value = this.value;
            updateVisualization();
        });

        sliderSlipAngle.addEventListener('input', function() {
            valueSlipAngle.value = this.value;
            updateVisualization();
        });

        sliderSlipAmount.addEventListener('input', function() {
            valueSlipAmount.value = parseFloat(this.value).toFixed(2);
            updateVisualization();
        });

        checkboxBox.addEventListener('change', function() {
            boxHelper.visible = this.checked;
        });

        btnReset.addEventListener('click', function() {
            sliderStrike.value = 180;
            sliderDip.value = 60;
            sliderSlipAngle.value = 90;
            sliderSlipAmount.value = 0.5;
            checkboxBox.checked = false;

            valueStrike.value = '180';
            valueDip.value = '60';
            valueSlipAngle.value = '90';
            valueSlipAmount.value = '0.50';

            boxHelper.visible = false;
            updateVisualization();
        });

        // Initialize and start
        init();
        animate();
    </script>
</body>
</html>
