<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-db-size">database sizelabel>
                <input type="range" id="slider-db-size" min="8" max="100" step="1" value="10">
                <div class="value-display">+span id="display-db-size">10span></div>
            </div>
            <div class="control-group">
                <label for="slider-iterations">number of iterationslabel>
                <input type="range" id="slider-iterations" min="0" max="20" step="1" value="2">
                <div class="value-display">+span id="display-iterations">2span></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="probability-display">99.9% success probabilitydiv>
    </div>

    <script>
        // Get DOM elements
        const sliderDbSize = document.getElementById('slider-db-size');
        const displayDbSize = document.getElementById('display-db-size');
        const sliderIterations = document.getElementById('slider-iterations');
        const displayIterations = document.getElementById('display-iterations');
        const probabilityDisplay = document.getElementById('probability-display');

        // Add event listeners
        sliderDbSize.addEventListener('input', updateDisplay);
        sliderIterations.addEventListener('input', updateDisplay);

        function updateDisplay() {
            displayDbSize.textContent = sliderDbSize.value;
            displayIterations.textContent = sliderIterations.value;

            // Calculate and update probability
            const N = parseInt(sliderDbSize.value);
            const k = parseInt(sliderIterations.value);
            const probability = calculateSuccessProbability(N, k);
            probabilityDisplay.textContent = `${probability}% success probability`;

            // Redraw canvas will happen automatically in p5 draw loop
        }

        function calculateSuccessProbability(N, k) {
            const theta = Math.asin(1 / Math.sqrt(N));
            const probability = Math.pow(Math.sin((2 * k + 1) * theta), 2);
            return (probability * 100).toFixed(1);
        }

        // p5.js sketch
        let sketch = function(p) {
            let targetIndex = 6;

            p.setup = function() {
                let canvas = p.createCanvas(840, 480);
                canvas.parent('canvas-container');
                p.noStroke();
            };

            p.draw = function() {
                p.background(255);

                const N = parseInt(sliderDbSize.value);
                const k = parseInt(sliderIterations.value);

                // Draw four stages
                drawStage(p, 0, "Original Amplitudes", initializeAmplitudes(N), N);
                drawStage(p, 1, "After First Inversion", applyInversion(initializeAmplitudes(N)), N);
                drawStage(p, 2, "After First Diffusion", applyDiffusion(applyInversion(initializeAmplitudes(N))), N);
                drawStage(p, 3, `After ${k} Iterations`, applyKIterations(initializeAmplitudes(N), k), N);
            };

            function initializeAmplitudes(N) {
                const amp = new Array(N);
                const value = 1 / Math.sqrt(N);
                for (let i = 0; i < N; i++) {
                    amp[i] = value;
                }
                return amp;
            }

            function applyInversion(amplitudes) {
                const result = [...amplitudes];
                result[targetIndex] *= -1;
                return result;
            }

            function applyDiffusion(amplitudes) {
                const mean = amplitudes.reduce((a, b) => a + b, 0) / amplitudes.length;
                return amplitudes.map(a => 2 * mean - a);
            }

            function applyKIterations(initialAmplitudes, k) {
                let currentState = [...initialAmplitudes];
                for (let i = 0; i < k; i++) {
                    currentState = applyInversion(currentState);
                    currentState = applyDiffusion(currentState);
                }
                return currentState;
            }

            function drawStage(p, stageIndex, title, amplitudes, N) {
                const sectionHeight = p.height / 4;
                const sectionY = stageIndex * sectionHeight;
                const margin = 40;
                const barWidth = Math.min(8, (p.width - 2 * margin) / N);
                const spacing = (p.width - 2 * margin - barWidth * N) / (N - 1);
                const scale = 40;

                // Draw title
                p.push();
                p.textAlign(p.CENTER);
                p.fill(50);
                p.textSize(14);
                p.text(title, p.width / 2, sectionY + 20);
                p.pop();

                // Draw baseline
                p.push();
                p.stroke(200);
                p.strokeWeight(1);
                p.line(margin, sectionY + sectionHeight / 2, p.width - margin, sectionY + sectionHeight / 2);
                p.pop();

                // Draw bars
                for (let i = 0; i < N; i++) {
                    const x = margin + i * (barWidth + spacing);
                    const height = amplitudes[i] * scale;
                    const y = sectionY + sectionHeight / 2 - height;

                    // Color target state differently
                    if (i === targetIndex) {
                        p.fill(255, 165, 0);
                    } else {
                        p.fill(128, 128, 128);
                    }

                    p.rect(x, y, barWidth, Math.abs(height));
                }

                // Draw parentheses for first stage
                if (stageIndex === 0) {
                    p.push();
                    p.stroke(50);
                    p.strokeWeight(2);
                    p.noFill();
                    // Left parenthesis
                    p.arc(margin - 15, sectionY + sectionHeight / 2, 30, sectionHeight - 40, -p.HALF_PI, p.HALF_PI);
                    // Right parenthesis
                    p.arc(p.width - margin + 15, sectionY + sectionHeight / 2, 30, sectionHeight - 40, p.HALF_PI, p.HALF_PI * 3);
                    p.pop();
                }
            }
        };

        // Create p5 instance
        new p5(sketch);
    </script>
