        const valueFeedQuality = document.getElementById('value-feed-quality');

        // p5.js sketch
        const p5Sketch = (p) => {
            p.setup = function() {
                let canvas = p.createCanvas(600, 600);
                canvas.parent('canvas-container');
                p.noLoop();
            };

            p.draw = function() {
                drawDiagram();
            };
        };

        // Initialize p5.js
        sketch = new p5(p5Sketch);

        // Drawing function
        function drawDiagram() {
            const canvas = sketch.canvas;
            const p = sketch.p5;

            // Clear canvas
            p.background(255);

            // Get current values
            const alpha = parseFloat(sliderVolatility.value);
            const xD = parseFloat(sliderDistillate.value);
            const xB = parseFloat(sliderBottom.value);
            const xF = parseFloat(sliderFeedComp.value);
            const q = parseFloat(sliderFeedQuality.value);

            // Define margins and plot size
            const margin = 60;
            const plotSize = Math.min(canvas.width - 2 * margin, canvas.height - 2 * margin);
            const originX = margin;
            const originY = canvas.height - margin;

            // Draw axes
            p.stroke(128);
            p.strokeWeight(2);

            // X-axis
            p.line(originX, originY, originX + plotSize, originY);

            // Y-axis
            p.line(originX, originY, originX, originY - plotSize);

            // Draw tick marks and labels
            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(12);
            p.fill(128);

            for (let i = 0; i <= 10; i++) {
                const value = i / 10;
                const x = originX + value * plotSize;
                const y = originY - value * plotSize;

                // X-axis ticks
                p.line(x, originY, x, originY + 5);
                p.text(value.toFixed(1), x, originY + 20);

                // Y-axis ticks
                p.line(originX - 5, y, originX, y);
                p.text(value.toFixed(1), originX - 20, y);
            }

            // Axis labels
            p.textAlign(p.CENTER, p.TOP);
            p.text("x (mole fraction of MVC)", originX + plotSize / 2, originY + 35);

            p.push();
            p.translate(originX - 45, originY - plotSize / 2);
            p.rotate(-p.HALF_PI);
            p.text("y (mole fraction of MVC)", 0, 0);
            p.pop();

            // Map function for coordinates
            function mapX(x) {
                return originX + x * plotSize;
            }

            function mapY(y) {
                return originY - y * plotSize;
            }

            // Draw equilibrium curve (red)
            p.stroke(255, 0, 0);
            p.strokeWeight(2);
            p.noFill();
            p.beginShape();
            for (let x = 0; x <= 1; x += 0.001) {
                const y = (alpha * x) / (1 + (alpha - 1) * x);
                p.vertex(mapX(x), mapY(y));
            }
            p.endShape();

            // Draw diagonal line (green)
            p.stroke(34, 139, 34);
            p.strokeWeight(2);
            p.line(mapX(0), mapY(0), mapX(1), mapY(1));

            // Calculate intersection point of q-line and equilibrium curve
            let xInt, yInt;

            if (q === 1) {
                // Vertical feed line
                xInt = xF;
                yInt = (alpha * xF) / (1 + (alpha - 1) * xF);
            } else if (q === 0) {
                // Horizontal feed line
                yInt = xF;
                xInt = yInt / (alpha - (alpha - 1) * yInt);
            } else {
                // Slanted feed line
                const m = q / (q - 1);
                const b = xF - m * xF;

                // Solve y = (α * x) / (1 + (α - 1) * x) and y = mx + b
                // Using numerical approximation
                for (let x = 0; x <= 1; x += 0.0001) {
                    const yEq = (alpha * x) / (1 + (alpha - 1) * x);
                    const yFeed = m * x + b;

                    if (Math.abs(yEq - yFeed) < 0.0001) {
                        xInt = x;
                        yInt = yEq;
                        break;
                    }
                }
            }

            // Draw operating lines and feed line
            // Feed line (dark blue)
            p.stroke(0, 0, 205);
            p.strokeWeight(2);
            if (q === 1) {
                // Vertical line
                p.line(mapX(xF), mapY(xF), mapX(xF), mapY(yInt));
            } else if (q === 0) {
                // Horizontal line
                p.line(mapX(xF), mapY(xF), mapX(xInt), mapY(xF));
            } else {
                // Slanted line
                p.line(mapX(xF), mapY(xF), mapX(xInt), mapY(yInt));
            }

            // Rectifying line (cyan)
            p.stroke(0, 255, 255);
            p.line(mapX(xD), mapY(xD), mapX(xInt), mapY(yInt));

            // Stripping line (magenta)
            p.stroke(255, 0, 255);
            p.line(mapX(xB), mapY(xB), mapX(xInt), mapY(yInt));

            // Calculate and draw stages
            p.stroke(0);
            p.strokeWeight(1);

            let stageCount = 0;
            let x = xD;
            let y = xD;

            while (x > xB) {
                // Horizontal line to equilibrium curve
                const yEq = (alpha * x) / (1 + (alpha - 1) * x);
                p.line(mapX(x), mapY(y), mapX(x), mapY(yEq));

                y = yEq;

                if (x <= xB) break;

                stageCount++;

                // Vertical line to operating line
                let yOp;
                if (x > xInt) {
                    // Rectifying line
                    if (xD === xInt) {
                        yOp = yInt;
                    } else {
                        const slope = (yInt - xD) / (xInt - xD);
                        yOp = xD + slope * (x - xD);
                    }
                } else {
                    // Stripping line
                    if (xB === xInt) {
                        yOp = yInt;
                    } else {
                        const slope = (yInt - xB) / (xInt - xB);
                        yOp = xB + slope * (x - xB);
                    }
                }

                p.line(mapX(x), mapY(y), mapX(xOp), mapY(yOp));

                // Update for next iteration
                x = Math.min(1, Math.max(0, xOp));
                y = yOp;
            }

            // Update stage count display
            stagesDisplay.textContent = `The number of stages is ${stageCount}.`;

            // Update value displays
            valueVolatility.textContent = alpha.toFixed(1);
            valueDistillate.textContent = xD.toFixed(2);
            valueBottom.textContent = xB.toFixed(2);
            valueFeedComp.textContent = xF.toFixed(2);
            valueFeedQuality.textContent = q.toFixed(2);
        }

        // Parameter constraint logic
        function enforceConstraints(changedSlider) {
            const step = 0.01;
            const bottom = parseFloat(sliderBottom.value);
            const distillate = parseFloat(sliderDistillate.value);
            let feedComp = parseFloat(sliderFeedComp.value);

            if (changedSlider === 'bottom' && bottom >= feedComp) {
                feedComp = parseFloat((bottom + step).toFixed(2));
                sliderFeedComp.value = feedComp;
                sliderFeedComp.min = parseFloat((bottom + step).toFixed(2));
            } else if (changedSlider === 'distillate' && distillate <= feedComp) {
                feedComp = parseFloat((distillate - step).toFixed(2));
                sliderFeedComp.value = feedComp;
                sliderFeedComp.max = parseFloat((distillate - step).toFixed(2));
            } else if (changedSlider === 'feed') {
                if (feedComp <= bottom) {
                    sliderFeedComp.value = parseFloat((bottom + step).toFixed(2));
                } else if (feedComp >= distillate) {
                    sliderFeedComp.value = parseFloat((distillate - step).toFixed(2));
                }
            }

            // Update feed composition limits
            const newFeedComp = parseFloat(sliderFeedComp.value);
            sliderFeedComp.min = parseFloat((bottom + step).toFixed(2));
            sliderFeedComp.max = parseFloat((distillate - step).toFixed(2));
        }

        // Event listeners for sliders
        sliderVolatility.addEventListener('input', () => {
            drawDiagram();
        });

        sliderDistillate.addEventListener('input', () => {
            enforceConstraints('distillate');
            drawDiagram();
        });

        sliderBottom.addEventListener('input', () => {
            enforceConstraints('bottom');
            drawDiagram();
        });

        sliderFeedComp.addEventListener('input', () => {
            enforceConstraints('feed');
            drawDiagram();
        });

        sliderFeedQuality.addEventListener('input', () => {
            drawDiagram();
        });

        // Event listeners for buttons
        document.getElementById('btn-volatility-plus').addEventListener('click', () => {
            const currentValue = parseFloat(sliderVolatility.value);
            const newValue = Math.min(10, currentValue + 0.1);
            sliderVolatility.value = newValue.toFixed(1);
            drawDiagram();
        });

        document.getElementById('btn-distillate-plus').addEventListener('click', () => {
            const currentValue = parseFloat(sliderDistillate.value);
            const newValue = Math.min(0.99, currentValue + 0.01);
            sliderDistillate.value = newValue.toFixed(2);
            enforceConstraints('distillate');
            drawDiagram();
        });

        document.getElementById('btn-bottom-plus').addEventListener('click', () => {
            const currentValue = parseFloat(sliderBottom.value);
            const newValue = Math.min(0.49, currentValue + 0.01);
            sliderBottom.value = newValue.toFixed(2);
            enforceConstraints('bottom');
            drawDiagram();
        });

        document.getElementById('btn-feed-comp-plus').addEventListener('click', () => {
            const currentValue = parseFloat(sliderFeedComp.value);
            const newValue = Math.min(parseFloat(sliderFeedComp.max), currentValue + 0.01);
            sliderFeedComp.value = newValue.toFixed(2);
            enforceConstraints('feed');
            drawDiagram();
        });

        document.getElementById('btn-feed-quality-plus').addEventListener('click', () => {
            const currentValue = parseFloat(sliderFeedQuality.value);
            const newValue = Math.min(2.0, currentValue + 0.05);
            sliderFeedQuality.value = newValue.toFixed(2);
            drawDiagram();
        });

        // Initial draw
        setTimeout(() => drawDiagram(), 100);
    </script>
