        }

        #visualization-area {
            flex: 1;
            background-color: #000000;
            position: relative;
            overflow: hidden;
        }

        #main-canvas {
            width: 100%;
            height: 100%;
            display: block;
        }
    </style>
</head>
<body>
    <!-- Main container for the demo -->
    <div id="demo-container">

        <!-- Section for user controls -->
        <div id="control-panel">
            <label for="slider-month">month</label>
            <input type="range" id="slider-month" min="0" max="11" step="1">

            <label for="perspective-buttons">perspective</label>
            <div id="perspective-buttons">
                <button id="btn-2d" class="active">2D</button>
                <button id="btn-3d">3D</button>
            </div>
        </div>

        <!-- Section for the visualization canvas -->
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>

    </div>

    <!-- CDN Includes -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Global variables
        let currentMonth = (new Date()).getMonth();
        let activeView = '2D';
        let p5Instance = null;
        let threeScene = null;
        let threeRenderer = null;
        let threeCamera = null;
        let threeControls = null;
        let earth = null;
        let earthAxis = null;
        let magnetosphere = null;
        let sunLight = null;
        let earthOrbit = null;

        // Initialize components
        document.addEventListener('DOMContentLoaded', function() {
            const sliderMonth = document.getElementById('slider-month');
            const btn2d = document.getElementById('btn-2d');
            const btn3d = document.getElementById('btn-3d');

            // Set initial slider value
            sliderMonth.value = currentMonth;

            // Event listeners
            sliderMonth.addEventListener('input', function() {
                currentMonth = parseInt(this.value);
                updateVisualization();
            });

            btn2d.addEventListener('click', function() {
                switchTo2D();
            });

            btn3d.addEventListener('click', function() {
                switchTo3D();
            });

            // Initialize with 2D view
            initialize2D();
        });

        function switchTo2D() {
            if (activeView === '2D') return;

            activeView = '2D';
            document.getElementById('btn-2d').classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');

            // Clean up three.js
            if (threeRenderer) {
                threeRenderer.dispose();
                threeRenderer = null;
            }

            // Clear canvas container
            const canvasContainer = document.getElementById('main-canvas');
            canvasContainer.innerHTML = '';

            // Initialize 2D
            initialize2D();
        }

        function switchTo3D() {
            if (activeView === '3D') return;

            activeView = '3D';
            document.getElementById('btn-3d').classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');

            // Clean up p5.js
            if (p5Instance) {
                p5Instance.remove();
                p5Instance = null;
            }

            // Initialize 3D
            initialize3D();
        }

        function updateVisualization() {
            if (activeView === '2D' && p5Instance) {
                // p5.js will automatically redraw
            } else if (activeView === '3D' && threeScene) {
                update3DScene();
            }
        }

        // 2D Visualization with p5.js
        function initialize2D() {
            const p5Sketch = function(p) {
                let canvas;

                p.setup = function() {
                    const container = document.getElementById('main-canvas');
                    canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
                    canvas.parent('main-canvas');
                };

                p.draw = function() {
                    p.background(0);
                    p.translate(p.width/2, p.height/2);

                    // Calculate tilt angle
                    const tilt = 23.5 * p.cos(2 * p.PI * currentMonth / 12);

                    p.push();
                    p.rotate(-p.radians(tilt));

                    // Draw solar wind (static, no rotation)
                    p.pop();
                    drawSolarWind(p);

                    p.push();
                    p.rotate(-p.radians(tilt));

                    // Draw bow shock
                    p.stroke(255, 165, 0);
                    p.noFill();
                    p.strokeWeight(2);
                    p.beginShape();
                    for (let x = -p.width/2; x < 0; x += 10) {
                        let y = 100 * p.exp(x / 200);
                        p.curveVertex(x, y);
                        p.curveVertex(x, -y);
                    }
                    p.endShape();

                    // Draw magnetopause
                    p.stroke(0, 255, 255);
                    p.beginShape();
                    for (let x = -p.width/2; x < p.width/2; x += 10) {
                        let y = 80 * p.exp(x / 300) + 20;
                        if (x > 0) y = 50 + 100 * p.sin(x / 200);
                        p.vertex(x, y);
                        p.vertex(x, -y);
                    }
                    p.endShape(p.CLOSE);

                    // Draw magnetic field lines
                    drawMagneticFieldLines(p);

                    // Draw Earth
                    p.noStroke();
                    p.fill(65, 105, 225);
                    p.ellipse(0, 0, 80, 80);

                    // Draw landmasses
                    p.fill(34, 139, 34);
                    p.ellipse(-10, -10, 30, 25);
                    p.ellipse(15, 5, 25, 20);
                    p.ellipse(-5, 20, 35, 15);

                    // Draw Van Allen belts
                    drawVanAllenBelts(p);

                    // Draw polar cusps
                    p.fill(240, 230, 140);
                    p.beginShape();
                    p.vertex(-20, -40);
                    p.vertex(0, -45);
                    p.vertex(20, -40);
                    p.vertex(15, -25);
                    p.vertex(-15, -25);
                    p.endShape(p.CLOSE);

                    p.beginShape();
                    p.vertex(-20, 40);
                    p.vertex(0, 45);
                    p.vertex(20, 40);
                    p.vertex(15, 25);
                    p.vertex(-15, 25);
                    p.endShape(p.CLOSE);

                    // Draw magnetotail
                    p.fill(0, 0, 139, 100);
                    p.beginShape();
                    p.vertex(40, 80);
                    p.vertex(p.width/2, 150);
                    p.vertex(p.width/2, -150);
                    p.vertex(40, -80);
                    p.endShape(p.CLOSE);

                    p.pop();
                };

                p.windowResized = function() {
                    const container = document.getElementById('main-canvas');
                    p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                };
            };

            p5Instance = new p5(p5Sketch);
        }

        function drawSolarWind(p) {
            p.push();
            p.stroke(153, 0, 0);
            p.strokeWeight(2);
            for (let y = -p.height/2 + 20; y < p.height/2; y += 40) {
                p.line(-p.width/2, y, -50, y);
                // Arrow head
                p.push();
                p.translate(-50, y);
                p.rotate(0);
                p.line(0, 0, -10, -5);
                p.line(0, 0, -10, 5);
                p.pop();
            }
            p.pop();
        }

        function drawMagneticFieldLines(p) {
            p.push();
            p.stroke(128, 128, 128);
            p.strokeWeight(1);
            p.noFill();

            // Closed field lines
            for (let i = 0; i < 4; i++) {
                p.beginShape();
                for (let t = 0; t <= p.PI; t += 0.1) {
                    let r = 50 + i * 20 + 30 * p.sin(t);
                    let x = r * p.cos(t);
                    let y = r * p.sin(t);
                    p.curveVertex(x, y);
                }
                p.endShape();

                p.beginShape();
                for (let t = 0; t <= p.PI; t += 0.1) {
                    let r = 50 + i * 20 + 30 * p.sin(t);
                    let x = r * p.cos(t);
                    let y = -r * p.sin(t);
                    p.curveVertex(x, y);
                }
                p.endShape();
            }

            // Open field lines (magnetotail)
            for (let i = 0; i < 3; i++) {
                p.beginShape();
                p.curveVertex(-30, -30 - i * 15);
                p.curveVertex(0, -40 - i * 10);
                p.curveVertex(100, -60 - i * 10);
                p.curveVertex(200, -80 - i * 15);
                p.endShape();

                p.beginShape();
                p.curveVertex(-30, 30 + i * 15);
                p.curveVertex(0, 40 + i * 10);
                p.curveVertex(100, 60 + i * 10);
                p.curveVertex(200, 80 + i * 15);
                p.endShape();
            }

            p.pop();
        }

        function drawVanAllenBelts(p) {
            p.push();
            p.noStroke();

            // Inner belt
            p.fill(0, 100, 0, 150);
            p.arc(30, 0, 120, 100, p.PI/2, 3*p.PI/2);

            // Outer belt
            p.fill(50, 205, 50, 150);
            p.arc(40, 0, 180, 140, p.PI/2, 3*p.PI/2);

            p.pop();
        }

        // 3D Visualization with three.js
        function initialize3D() {
            const canvasContainer = document.getElementById('main-canvas');

            // Scene setup
            threeScene = new THREE.Scene();
            threeScene.background = new THREE.Color(0x000000);

            // Camera
            threeCamera = new THREE.PerspectiveCamera(
                75, 
                canvasContainer.offsetWidth / canvasContainer.offsetHeight, 
                0.1, 
                1000
            );
            threeCamera.position.set(0, 50, 150);

            // Renderer
            threeRenderer = new THREE.WebGLRenderer({ 
                canvas: canvasContainer,
                antialias: true 
            });
            threeRenderer.setSize(canvasContainer.offsetWidth, canvasContainer.offsetHeight);

            // Controls
            threeControls = new THREE.OrbitControls(threeCamera, threeRenderer.domElement);
            threeControls.enableDamping = true;

            // Lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            threeScene.add(ambientLight);

            sunLight = new THREE.PointLight(0xffffff, 1);
            sunLight.position.set(0, 0, 0);
            threeScene.add(sunLight);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(10, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            const sun = new THREE.Mesh(sunGeometry, sunMaterial);
            threeScene.add(sun);

            // Earth's orbit
            const orbitCurve = new THREE.EllipseCurve(
                0, 0,  // center
                100, 90,  // xRadius, yRadius
                0, 2 * Math.PI,  // startAngle, endAngle
                false,  // clockwise
                0  // rotation
            );
            const orbitPoints = orbitCurve.getPoints(100);
            const orbitGeometry = new THREE.BufferGeometry().setFromPoints(orbitPoints);
            const orbitMaterial = new THREE.LineBasicMaterial({ color: 0xffffff });
            earthOrbit = new THREE.Line(orbitGeometry, orbitMaterial);
            earthOrbit.rotation.x = Math.PI / 2;
            threeScene.add(earthOrbit);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(8, 32, 32);
            const earthMaterial = new THREE.MeshStandardMaterial({ 
                color: 0x4169e1,
                metalness: 0.3,
                roughness: 0.7
            });
            earth = new THREE.Mesh(earthGeometry, earthMaterial);
            threeScene.add(earth);

            // Earth's axis
            const axisOrigin = new THREE.Vector3(0, 0, 0);
            const axisDirection = new THREE.Vector3(0, 1, 0);
            const axisLength = 20;
            earthAxis = new THREE.ArrowHelper(axisDirection, axisOrigin, axisLength, 0x00ff00);
            earth.add(earthAxis);

            // Solar wind arrows
            createSolarWindArrows();

            // Magnetosphere
            createMagnetosphere();

            // Initial update
            update3DScene();

            // Animation loop
            animate3D();

            // Handle resize
            window.addEventListener('resize', onWindowResize);
        }

        function createSolarWindArrows() {
            const arrowCount = 15;
            for (let i = 0; i < arrowCount; i++) {
                const phi = Math.random() * Math.PI * 2;
                const theta = Math.random() * Math.PI;
                const radius = 20 + Math.random() * 10;

                const origin = new THREE.Vector3(
                    radius * Math.sin(theta) * Math.cos(phi),
                    radius * Math.sin(theta) * Math.sin(phi),
                    radius * Math.cos(theta)
                );

                const direction = origin.clone().normalize();
                const length = 30;

                const arrowHelper = new THREE.ArrowHelper(direction, origin, length, 0xff0000);
                threeScene.add(arrowHelper);
            }
        }

        function createMagnetosphere() {
            // Create magnetosphere using lathe geometry
            const shape = new THREE.Shape();
            shape.moveTo(0, -15);
            shape.quadraticCurveTo(50, -15, 80, 0);
            shape.quadraticCurveTo(100, 10, 100, 30);
            shape.lineTo(150, 40);
            shape.lineTo(150, -40);
            shape.lineTo(100, -30);
            shape.quadraticCurveTo(100, -10, 80, 0);
            shape.quadraticCurveTo(50, 15, 0, 15);

            const geometry = new THREE.LatheGeometry(shape.getPoints(50), 32);
            const material = new THREE.MeshStandardMaterial({
                color: 0x0000ff,
                transparent: true,
                opacity: 0.4,
                side: THREE.DoubleSide
            });

            magnetosphere = new THREE.Mesh(geometry, material);
            magnetosphere.rotation.x = Math.PI / 2;

            // Create a group to hold Earth and magnetosphere
            const earthSystem = new THREE.Group();
            earthSystem.add(earth);
            earthSystem.add(magnetosphere);
            threeScene.add(earthSystem);

            // Update reference
            earth = earthSystem;
        }

        function update3DScene() {
            // Calculate Earth's position based on month
            const angle = (currentMonth / 12) * Math.PI * 2;
            const x = 100 * Math.cos(angle);
            const z = 90 * Math.sin(angle);
            earth.position.set(x, 0, z);

            // Update Earth's tilt
            const tiltAngle = (currentMonth / 12) * Math.PI * 2;
            earth.rotation.z = -23.5 * Math.PI / 180 * Math.cos(tiltAngle);

            // Orient magnetosphere tail away from Sun
            if (magnetosphere) {
                magnetosphere.rotation.y = angle + Math.PI;
            }
        }

        function animate3D() {
            requestAnimationFrame(animate3D);

            if (threeControls) {
                threeControls.update();
            }

            if (threeRenderer && threeScene && threeCamera) {
                threeRenderer.render(threeScene, threeCamera);
            }
        }

        function onWindowResize() {
            if (threeCamera && threeRenderer) {
                const canvasContainer = document.getElementById('main-canvas');
                threeCamera.aspect = canvasContainer.offsetWidth / canvasContainer.offsetHeight;
                threeCamera.updateProjectionMatrix();
                threeRenderer.setSize(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
            }
        }
    </script>
